value.content:
$dark-text-color: #999;
$bright-text-color: #ededed;
$metric-color: #674ea7;
$company-color: #d3741c;
$topic-color: #2b7ea0;

@mixin box-shadow($x: 2px, $y: 2px, $blur: 5px, $color: rgba(0,0,0,.4), $inset: "") {
  @if ($inset != "") {
    -webkit-box-shadow: $inset $x $y $blur $color;
    -moz-box-shadow:    $inset $x $y $blur $color;
    box-shadow:         $inset $x $y $blur $color;
  } @else {
    -webkit-box-shadow: $x $y $blur $color;
    -moz-box-shadow:    $x $y $blur $color;
    box-shadow:         $x $y $blur $color;
  }
}
.height-auto{
  height:auto!important;
}
.inherit-anchor{
 text-decoration: none;
 color:inherit!important;
 :hover{
    text-decoration: none;
    color:inherit;
 }
}

@mixin ellipsis() {
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}


#main > .TYPE-metric >.card-frame> .card-header  {
    color: #fff;
    background:$metric-color;
    text-align:center;
}
#main > .TYPE-company >.card-frame> .card-header  {
    color: #fff;
    background:$company-color;
    text-align:center;
}
#main > .TYPE-topic >.card-frame> .card-header  {
    color: #fff;
    background:$topic-color;
    text-align:center;
}

/*Common styles and classes*/

.no-data-on-missing-view{
  .missing-view a {
      display: none;
  }
  
   .missing-view:before {
      content: "No data";
      color: #F5BABA;
      font-size:13px;
  }
}


.nopadding {
   padding: 0 !important;
   margin: 0 !important;
}

.company-header{
  color: $company-color;
  border-top: 3px solid $company-color;
  font-size: 18px;
  border-bottom: 1px solid $bright-text-color;
  padding: 2px 10px 5px 0px;
  background: #FDFDFD;
}

.topic-header{
  color: $topic-color;
  border-top: 3px solid $topic-color;
  font-size: 14px;
  border-bottom: 1px solid $bright-text-color;
  padding: 2px 10px 5px 0px;
  background: #FDFDFD;
   @include ellipsis();
}

/*Common styles and classes ENDS*/

.SELF-homepage_company{
  .pointer-item{
    float:left;
  }
  
}

.homepage-company-item{
  max-width: 550px;
}
.browse-company-item, .browse-topic-item{
  width: 100%;
}
.homepage-company-item, .browse-company-item, .browse-topic-item {
  border: 1px solid $bright-text-color;
  margin: 12px;
  @include box-shadow(0,1px,6px,rgba(0,0,0,.13));
  .company-content{
    height: 281px;
   .left{
     
   }
   .right{
     background: #f7f7f7;
     height:100%;
       overflow: hidden;// remove after limiting 
   }
  }
  .yinyang-row .value-item .header {
    cursor: pointer;
  }
  .metric-column-title{
      width: 100%;
      float: left;
      padding-top: 5px;
      color: #B2B2B2;
  }
    .header-row {
    font-size: 10px;
    color: #bebebe;
    text-align: center;
    margin-top: 10px;
    .header-header {
      float: left;
      margin-left: 80px;
    }
    .data-header {
      width: 146px;
      float: right;
      margin-right: 15px;
    }
  }

}

.STRUCTURE-browse_company_item .company-header{
   @include ellipsis();
}

.STRUCTURE-homepage_company_item,.STRUCTURE-browse_company_item, .STRUCTURE-browse_topic_item , .company-page-logo-container  {
  .contribution-details{
    display:none;
    position: absolute;
    width: 100%;
    bottom: -107px;
    .number-small {
      border-right: 1px solid $bright-text-color;
      border-bottom: 1px solid $bright-text-color;
    }
    a{
      text-decoration:none;
      &:hover{
        text-decoration:none;
      }
    }
    .row {
      padding: 0;
      margin: 0px;
    }
    .row > div {
      padding: 0;
      &:hover{
        background:#fafafa;  
      } 
    }
  }
  :hover .contribution-details{
    display:block;
  }
  :hover .contribution-count{
    display:none;
  }
}

.TYPE-company.open-view .company-page-logo-container {
    float: left;
    width: 18%;
    margin-right:10px;
  .center-logo>div {
    height: 137px;
  }  
  .contribution-details {
    bottom: -103px;
    display:block!important;
  }
  .contribution-count{
      border-bottom: 1px solid $bright-text-color;
      display:none;
  }
}
.center-logo{
  border-right: 1px solid $bright-text-color;
  >div {
    height: 176px;
    width: 100%;
    overflow: hidden;
    padding: 20px;
    text-align: center;
    }
    img{
      position: relative;
      top: 50%;
      -webkit-transform: translateY(-50%);
      -ms-transform: translateY(-50%);
      transform: translateY(-50%);
    }
}


.metric-source .tab-content {
  background: #fffefa;
  padding: 30px;
}

.form .metric-value-year {
  width: 135px;
}
.form .metric-value-value {
  width: 200px;
}


.label-metric {
  background: $metric-color;
  color: white;
  a {
    font-weight: normal;
    color: white;
  }
}

// metric value details
.metric-year, .metric-unit {
  font-size: 8px;
  color: $dark-text-color;
}
.metric-value {
  color: #674ea7;
  font-size: 18px;
  font-weight: bold;
}
.no-metric-value {
  display: none;
}


.timeline {
  .year {
    width: 35px; 
  }
  .timeline-header {
    height: 22px;
    margin-top: 15px;
    .th {
      float: left;
      margin: 0px 10px 0px 10px;
      font-weight: bold;
    }
    .new a {
      color: $dark-text-color;
      background-color: $bright-text-color;
      border: 1px solid #cdcdcd;
      font-weight: normal;
      font-size: 0.8em;
      padding: 2px 5px 2px 5px;
    }
  }
  .timeline-body {
    .timeline-row {
      position: relative;
      clear: both;
      height: 30px;
      font-size: 12px;
      color: $dark-text-color;
      .td {
        float: left;
        margin: 0px 10px 0px 10px;
        .metric-value {
          font-size: 16px;
        }
      }
      .year, .credit {
        padding-top: 2px;
      }
      .search-result-list {
        display: inline-block;
        padding: 0px;
        .search-result-item {
          display: inline;
          padding-left: 0;
        }
      }
    }

  }
  .timeline-dot {
    position: absolute;
    top: 8px;
    left: 46px;
    height: 5px;
    width: 5px;
    background: gray;
  }
  .timeline-line {
    position: absolute;
    top: 10px;
    left: 48px;
    height: 32px;
    width: 1px;
    border-left: 1px solid gray;
  }
}

.RIGHT-company.card-content .company-item .item-link>a {
  background: #03998d;
  border-top-left-radius: 0px;
  border-bottom-left-radius: 0px;
}

.metric-info {
  border-bottom: 1px solid $bright-text-color;
  .row {
    margin-left: 5px;
    margin-right: 5px;
    .row-icon {
      text-align: center;
      font-size: 17px;
      float: left;
      width: 30px;
      color: $bright-text-color;
    }
    .glyphicon-question-mark {
      font-weight: bold;
    }
    .row-data {
      margin-left: 40px;
    }
  }
}

.TYPE-metric_value {
  .timeline, .RIGHT-discussion, .TYPE_PLUS_RIGHT-metric_value-source{
    float: left;
    width: 100%;
    .missing-view{
      display: none;
    }
  }
}


//browse metric
.browse-metric-item {
  padding: 0;
  margin-bottom: 6px;
  outline: 1px solid $bright-text-color;
  border-top: 3px solid $metric-color;
  @include box-shadow(0,1px,6px,rgba(0,0,0,.13));
  font-size:14px;
  .RIGHT-Xvote_count > div {
    line-height: 1em;
  }
  button.vote-button {
    color: $dark-text-color;
    width: 15px;
    height: 15px;
    padding: 0;
    border: none;
    box-shadow: none;
    --webkit-box-shadow: none;
    background: none;
  }
  .vote-count {
    color: $dark-text-color;
    width: 15px;
    height: 15px;
  }
  .logo {
    padding-top: 5px;
    float: left;
    img {
      max-width: 35px;
      max-height: 35px;
      margin: 0 10px;
    }
  }
  .header {
    margin: 4px 12px;
  }
  .name {
    padding: 12px 16px;
    font-size: 21px;
    float: left;
    max-width: 94%;
    text-overflow: ellipsis;
    white-space: nowrap;
    overflow: hidden;
    color: $metric-color;
    a {
      color: $metric-color; 
    }
  }
  .question {
    height:60px;
    text-overflow: ellipsis;
    white-space: nowrap;
    overflow: hidden;
    color:$dark-text-color;
 }
 .topic{
   height:38px;
 }
}

.data-count {
  border-top: 1px solid $bright-text-color;
  padding: 0;
  text-align: center;
  margin: 0px!important;
  >div {
      padding: 8px 0px;
  }
  > div:first-child {
      border-right: 1px solid $bright-text-color;
  }
  .number-label {
    font-size: 12px;
    color: $dark-text-color;
  }
  .number {
      font-size: 26px;
  }
  .number-small {
      font-size: 14px;
      padding:7px;
  }
  .number-large {
        font-size: 26px;
        height: 100px;
        padding: 12%;
  }
}

.ellipsis, .ellipsis .known-card {
  max-width: 94%;
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
}

.SELF-metric .search-result-item.item-content {
  width: 32%;
  padding: 10px;
  display: inline-block;
  min-width: 350px;
}

.modal-body .LTYPE_RTYPE-metric-company .timeline {
  padding-top: 10px;
  margin-top: 25px;
  border-top: 1px dotted $metric-color;
  width: 100%;
}

.TYPE-metric.new-view form .form-group.RIGHT-Xvote_count {
  display: none;
}
