value.content:
/*----------- General Variables ---------*/

$heading_color: #555;
$body_background: #ebebeb;
$primary_color: #555;
$anchor_color: #03998d;
$body-txt-color: #777;
$wanted-card-color: #b6060a;
$closed-content-a-color: #729;
$infographic-heading: #949494;
$div-border-color: #f1f1f1;

//button colors
$button-color: #03998d;
$button-color-hover: #009185;
$button-border: #00756c;
$cancel-button-color: #bebebe;
$cancel-button-border: #9b9b9b;
$cancel-button-hover: #b4b4b4;

//claim colors
$claim-tag-companybg: #d3741c;
$claim-tag-companybg-hover: #bb5b00;
$claim-tag-topicbg: #2b7ea0;
$claim-tag-topicbg-hover: #01648d;
$claim-tag-notexist: #f0f0f0;
$claim-tag-notexist-hover: #ececec;

$card-header-color: #dbdbdb;

$claim-color: #03998d;
$source-color: #ffc201;
$article-color: #666;
$heading-color: #999;

@mixin box-shadow($x: 2px, $y: 2px, $blur: 5px, $color: rgba(0,0,0,.4), $inset: "") {
  @if ($inset != "") {
    -webkit-box-shadow: $inset $x $y $blur $color;
    -moz-box-shadow:    $inset $x $y $blur $color;
    box-shadow:         $inset $x $y $blur $color;
  } @else {
    -webkit-box-shadow: $x $y $blur $color;
    -moz-box-shadow:    $x $y $blur $color;
    box-shadow:         $x $y $blur $color;
  }
}

/*----------- Top banner ---------*/

#top-banner-wrapper {
  .column-1,
  .column-2,
  .column-3 {
    background: #fafafa;
    height: 175px;
    float: left;
    margin-left: 0.4%;
    margin-bottom: 20px;
    border: 1px solid #eaeaea;
  }
  .column-1,
  .column-3 {
    width: 24%;
  }
  .column-1{
    iframe{
      width:100%;
      height:175px;
    }
  }
  .column-2 {
    position:relative;
    width: 50%;
    .wikirate-message {
      display: block;
      text-align: center;
      line-height: normal;
      padding: 30px;
      .homepage-heading-h1{
        font-size: 1.5em;
      }
    }
  }
  .column-3 {
    .SELF-banner_column3{
       overflow:hidden;
      }
    .banner-column-button a {
      text-align: center;
      display: block;
      font-size: 0.9em;
      margin: 30px 20px;
    }
  }
}

.SELF-wikirate_globe.card-content {
  height: 130px;
  padding: 18px 16px;
  max-width: 30%;
  float: left;
  overflow:hidden;
  img {
    max-height: 100%;
  }
}

/*----------- Content Wrapper --------------*/
#page-wrapper {
  background: #fff;
  padding: 20px 12px;

}
#main-content-wrapper {
  margin: 0 5px;

}
.browse-button {
  text-align: center;
  margin-top: 5px;
  margin-left: 16px;

}
/*-----------  Grid  ---------*/
.grid-row {
  display: inline-block;
  width: 100%;
  .grid-column {
    width: 49%;
    // background: green;
  }

}
/*----------- Companies and Topics  ---------*/
#company-n-topic .grid-column {
  position: relative;
  //for slick-js icon position

}
#company-n-topic .card-header {
  display: none;
  // workaround for carousel

}
#company-n-topic {
  .company-list,
  .topic-list {
    .search-result-list {
      // workaround for carousel
      height: 220px;
      overflow: hidden;
      margin-left: 26px;
      width: 550px;
    }
    .search-result-item {
      display: inline-block;
      min-width: 130px;
      width: 175px;
      padding: 10px 0;
      margin-left: 11px;
    }
  }
  .company-list {
    float: left;
  }
  .topic-list {
    float: right;
  }
  .browse-button {
    margin-top: 35px;
  }

}
/*----------- Articles ---------*/

#articles {
  .search-result-item {
    display: inline-block;
    margin: 10px;
  }

}
/*----------- Claims and Sources ---------*/

#claims-sources {

  .card-content.SELF-homepage_claim_list,
  .card-content.SELF-homepage_source_list {
    padding: 5px 15px;
    margin: 15px 15px;
    border: 1px solid #ddd;
    @include box-shadow(0,1px,6px,rgba(0,0,0,.13));
    height: 750px; // Temporary fix for same height , it should be dynamic at somepoint...
    overflow: hidden;
  }
  .card-content.SELF-homepage_claim_list {
    border-top:8px solid $claim-color;
  }
  .card-content.SELF-homepage_source_list {
    border-top:8px solid $source-color;
  }
  .claim-list,
  .source-list {
    width: 48%;
    .search-result-list {
      padding: 15px;
    }

  }
  .claim-list {
    float: left;
  }
  .source-list {
    float: right;
  }
  .browse-button {
    margin-top: 25px;
  }
}

/*----------- Headings ---------*/

.homepage-heading-h1  {
  text-align: center;
  font-size:1.6em;
  color: $heading-color;
  .count-color{
    color:$article-color;
    font-weight:900;
  }
  .company-color{
    color:$claim-tag-companybg;
  }
  .topic-color{
    color:$claim-tag-topicbg;
  }
  .article-color{
    color: $article-color;
  }
  .claim-color{
    color: $claim-color;
  }
  .source-color{
    color:$source-color
  }
}
.homepage-heading-h2 {
  font-size: 1.9em;
  font-weight: normal;
  padding: 5px 0 0 0;
}

@media screen and (max-width: 1150px) {
    .homepage-heading-h1  {
      font-size:1.2em;
    }
    .homepage-heading-h2 {
      font-size: 1.6em;
    }
    #top-banner-wrapper .column-2 .wikirate-message .homepage-heading-h1 {
      font-size: 1.2em;
    }
}

@media screen and (max-width: 1085px) {
    .homepage-heading-h1  {
      font-size:1.1em;
    }
    .homepage-heading-h2 {
      font-size: 1.4em;
    }
    #top-banner-wrapper .column-2 .wikirate-message .homepage-heading-h1 {
      font-size: 1.1em;
    }
}

@media screen and (max-width: 1085px) {
    .homepage-heading-h1  {
      font-size:0.9em;
    }
    .homepage-heading-h2 {
      font-size: 1em;
    }
    #top-banner-wrapper .column-2 .wikirate-message .homepage-heading-h1 {
      font-size: 0.9em;
    }
}
