value.content:
$drag-item-height: 52px;
$dark-text-color: #666;
$dark-text-color2: #bebebe;
$bright-text-color: #e5e5e5;

$metric-color: #674ea7;
$company-color: #d3741c;
$topic-color: #2b7ea0;

$company-color: #d3741c;
$topic-color: #2b7ea0;
$metric-color: #674ea7;


.TYPE-analysis > .card-frame > .card-body.panel-body >.yinyang {
    border :0
}

.border-right {
    border-right: 1px solid $bright-text-color;
}
.yinyang {
  border-top: 1px solid $bright-text-color;
  h4 {
    // text-align: center;
    // color: #999;
    margin-bottom: 10px;
  }
  
  .wiki {
    padding-left: 0;
    padding-right: 0;
    .yin {
      position: absolute;
      top: 5px;
      right: 0;
      color: #f7f7f7;
      font-size: 50px;
      line-height: 50px;
    }
  }
  
  .rate {
    padding-left: 0;
    padding-right: 0;

    .yang {
      position: absolute;
      top: 5px;
      left: 0;
      color: #fff;
      font-size: 50px;
      line-height: 50px;
    }
  }
  
  .header-row {
    font-size: 10px;
    color: $dark-text-color2;
    text-align: center;
    margin-top: 10px;
    .header-header {
      float: left;
      margin-left: 80px;
    }
    .data-header {
      width: 161px;
      float: right;
      margin-right: 15px;
    }
  }
  
  .empty-message {
    display: none;
    padding: 5px;
    border: 1px dashed #ddd;
    text-align: center;
    height: 32px;
    color: #ddd;
    background: #fff;
    font-weight: normal;
  }
  .unsaved-message {
    display: none;
    height: 25px;
  }
  .up_vote-container, .down_vote-container {
    background: #fff;
    h5 {
      text-align: center;
          color: #999;
    }
  }
}

.list-drag-and-drop, .yinyang-list {
      clear: both;
    margin: 22px 0px 10px;
    padding: 5px 0px;
    background: none;
    border: none;
    box-shadow: none;
    --webkit-box-shadow: none;
}


.TYPE-company, .TYPE-topic, .TYPE-metric, .RIGHT-overview_page, .RIGHT-reference_page,.RIGHT-note_page {
  &.card-content{
    padding: 0;
  }
}

.TYPE-company,.RIGHT-overview_page, .RIGHT-reference_page,.RIGHT-note_page{
      &.open-view .company-page-logo-container {
          float: left;
          width: 28%;
          margin-right: 1px;
          border: 7px solid #F3F3F3;
          margin-right: 10px;
          box-shadow: 0px 0px 3px 0px rgba(0, 0, 0, 0.36);
          position: relative;
          z-index: 1;
          overflow: hidden;
       
        .center-logo>div {
          height: 137px;
          padding:2px;
        }  
        .contribution-details {
          bottom: -103px;
          display:block!important;
        }
        .contribution-count{
            border-bottom: 1px solid $bright-text-color;
            display:none;
        }
      }
      
      &.open-view  .nav-tabs{
        display:inline-block;
        border:0px;
        margin-top: 1px;
        >li{
          width:100px;
        }
      }
        
}


@mixin yinyang-frame($header-color) {
  > .card-body.card-content {
    padding: 0;
  }
  > .card-header {
    background: $header-color;
    .panel-title {
      color: #fff;
       a {
        color: inherit !important;
        font-size: 16px;
      } 
    }
  }
}

@mixin yinyang-header($header-color) {
  >.card-frame> .card-header  {
      color: #fff;
      background:$header-color;
      text-align:center;
  }
  
}


#main > .TYPE-metric  {
    @include yinyang-header($metric-color);
}
#main > {
    .TYPE-company,.RIGHT-overview_page, .RIGHT-reference_page,.RIGHT-note_page   {
      @include yinyang-header($company-color);
    }
}
#main > .TYPE-topic   {
    @include yinyang-header($topic-color);
}

.company-item.value-item.active .header{
    border-color: $company-color;
}

.company-item.value-item.active {
    background-color: rgba($company-color, 0.07);
    outline: 1px solid $company-color;
}

.metric-item.value-item.active .header{
    border-color: $metric-color;
}


.metric-item.value-item.active {
    background-color: rgba($metric-color, 0.07);
    outline: 1px solid $metric-color;
}
.discussion-container{
  margin-bottom:100px;
}

.TYPE-metric{
  .question-container {
        padding: 0px 30px 30px 22px;
        margin: 0px -14px;
        background-color: #F9F9F9;
        border-top: 1px solid #ebebeb;
        border-bottom: 1px solid #ebebeb;
        .row-data {
            font-size: 18px;
            margin: 10px 25px 10px 10px;
            float: left;
          }
          .row-icon {
            color: #674EA7;
            margin-top: 16px;
          }
  }
  .rate {
    padding-top: 34px;
    .RIGHT-methodology {
      margin-top: 20px;
      padding-top: 15px;
    }
    .chosen-container {
      height: 12px;
      margin-left: -18px;
    }
  }
  .wiki {
    .header {
        h3, img {
          display: inline-block;
          float: left;
          max-height: 50px;
        }
        h3 {
          margin: 13px 10px;
        }
      }
  }
}


.drag-item, .yinyang-row{
  padding: 0;
  margin-bottom: -1px;
  border: 1px solid $bright-text-color;
  background: rgb(251, 251, 251);
  color: rgb(204, 204, 204);
  
  .metric-item{
    &:hover{
      .header{
        border-color: $metric-color;
      }
    }
  }
  .company-item{
    &:hover{
      .header{
        border-color: $company-color;
      }
    }
  }
  &:hover {
    .handle{
      color: $dark-text-color2 !important;
      background-color: #F5F5F5 !important;
      border-right: 1px solid $bright-text-color !important;
    }
  }
  
  .contribution-item {
    overflow: hidden;
    cursor: move;
    .header {
      .name {
        font-size: 18px;
      }
    }
    &.metric-item .header .name {
      color: $metric-color;
    }
    &.company-item .header .name {
      color: $company-color;
    }
    &.topic-item .header .name {
      color: $topic-color;
    }
  }
  .value-item {
    color: $dark-text-color;
    width: 100%;
    background: #fff;
    overflow: hidden;
    
    .header {
      width: 60%;
      cursor: move;
      .name {
        width: 70%;
      }
    }
    
    .data {
      width: 38%;
      .data-item {
        float: left;
        width: 90%;
      }
      &:hover{
        background-color: #fafafa;
        cursor: pointer;
      }
    }
    
   
  }
  
  .topic-item .header {
    border-left: 3px solid rgba($topic-color,0.14);
  }
  .company-item .header {
    border-left: 3px solid rgba($company-color,0.14);
  }
  .value-item {
    .header {
      border-left: 3px solid rgba($metric-color, 0.14);
      // border-left: 3px solid rgba(103, 78, 167, 0.14);
      //border-left: 3px solid $metric-color;
    }
    .metric-info {
      .header .row .row-data 
        { 
          h4 {
              float: left;
          }
        img {
              float: left;
              padding-right: 12px;
          }  
      }
    }
  }

  .header {
    height: $drag-item-height;
    float: left;
    .handle {
      float: left;
      cursor: move;
      color: #ddd;
        margin-top: 0px;
        margin-left: 0px;
        padding: 16px 2px;
        border-right: 1px solid #fff;
        margin-right: 2px;
    }
    .RIGHT-Xvote_count > div {
      line-height: 1em;
    }
    button.vote-button {
      color: $dark-text-color;
      width: 15px;
      height: 15px;
      padding: 0;
      border: none;
      box-shadow: none;
      --webkit-box-shadow: none;
      background: none;
    }
    .vote-count {
      color: $dark-text-color;
      width: 15px;
      height: 15px;
    }
    .logo {
      padding-top: 5px;
      float: left;
      img {
        max-width: 35px;
        max-height: 35px;
        margin: 0 10px;
      }

    }
    .name {
      padding-top: 11px;
      font-size: 14px;
      float: left;
      max-width: 250px;
      text-overflow: ellipsis;
      white-space: nowrap;
      overflow: hidden;
    }
  }
  
  
  .data {
    height: $drag-item-height;
    float: right;
    height: $drag-item-height;
    border-left: 1px solid $bright-text-color;
    
    .btn-highlight a,
    .btn-default a {
      text-decoration: none;
    }
    .btn-default{
      border-color: $bright-text-color;
    }
    .data-item {
      margin-top: 15px;
      padding-left: 15px;
    }
    
    .contribution {
      float: right;
      width: 58px;
      height: 54px;
      font-size: 14px;
      font-weight: bold;
      text-align: center;
      margin-top: -1px;
      margin-right: -1px;
      margin-left: -1px;
      &.company-count {
        width: 108px;
      }
      &.metric-details {
        width: 108px;
        padding-top: 15px;
      }
      &.btn-default {
        .name {
          color: #ccc;
        }
        .content {
          color: #737373;
        }
      }
      &.btn.btn-highlight {
        background-color: #f29393;
        border-color: #f06464;
        a {
          color: #fff;
        }
        &:hover {
          background-color: #f06e6e;
        }
      }
      .glyphicon {
        font-size: 12px;
      }
      &.danger {
        /*border: 1px solid #E06666;*/
        background: #ea9999;
        color: #fff;
        a {
          color: #fff;
        }
        .name {
          color: #fff;
        }
      }
      &.warning {
        a {
          text-decoration: none;
          color: #dadada;
        }
      }
      .name {
        font-size: 11px;
        text-align: center;
        margin-left: -2px;
        font-weight: normal;
        padding-top: 2px;
      }
      // border-right: 1px solid $bright-text-color;
    }
  }
  .show-with-details {
   display: none;
  }
  .details {
    clear: both;
    color: $dark-text-color;
    padding: 0px;
    font-size: 12px;
    display: none;
    border-top: 1px solid $bright-text-color;
    position: absolute;
    background-color: #fff;
    border: 1px solid #EAEAEA;
    z-index: 1;
    top: 0px;
    left: 100%;
    width: 100%;
    overflow-y: auto;
    height: 100%;
    margin-left: 1px;
    &.stick{
      position: fixed;
      z-index: 1;
      top: 65px;
      left: 50%;
      max-width: 610px;
      width:50%;
      height: 100%;
      margin-left: 0px;
      box-shadow: 0px 0px 14px 0px rgba(0, 0, 0, 0.22);
    }
    .row-data {
      margin-left: 58px!important;
    }
  }
  
  .details-toggle {
    float: right;
    font-size: 11px;
    padding: 19px 4px 0 4px;
    min-width: 12px;
    height: $drag-item-height;
    cursor:pointer;
  }
}

 .TYPE-analysis .details{
      position: fixed;
      z-index: 1;
      top: 122px;
      left: 50%;
      max-width: 610px;
      width:50%;
      height: 100%;
      margin-left: 0px;
      box-shadow: 0px 0px 14px 0px rgba(0, 0, 0, 0.22);
  }
  
  .metric-item.contribution-item .details{
    clear: both;
    color: $dark-text-color;
    padding: 10px;
    font-size: 12px;
    display: none;
    border-top: 1px solid $bright-text-color;
    position: relative;
    background-color: #fff;
    border: 1px solid $bright-text-color;
    z-index: 11;
    top: inherit;
    left: inherit;
    width: inherit;
    overflow-y: inherit;
    height: inherit;
    margin-left: 0px;
  }


// filter
.filter-header {
  cursor: pointer;
}
.votee-filter {
  background: #f2f2f2;
  color: $dark-text-color;
  padding: 5px;
  text-align: center;
  font-size: 12px;
  font-weight: bold;
}
.filter-details {
  display: none;
  margin-bottom: -4px;
  margin-left: -5px;
  margin-right: -4px;
  background: #FDFDFD;
}
.filter-toggle {
  float: right;
}

.TYPE-topic .RIGHT-company_sidebar {
  margin: 22px 10px 10px;
}
.yinyang-row.column-header {
  .header {
    cursor: pointer;
    float: left;
    color: #ccc !important;
    padding: 6px 12px;
    text-decoration: none;
  }
  .data {
    cursor: pointer;
    float: left;
    color: #ccc !important;
    padding: 6px 12px;
    text-decoration: none;
    &:hover {
      background: #fff;
    }
  }
  .company-item {
    &:hover .header {
      border-color: #EAE7F3;
    }
    &.value-item {
      height: 32px;
      width: 100%;
    }
  }
  .header {
    width: 62%;
    &:hover {
      background: #fafafa;
    }
  }
  .data:hover {
    background: #fafafa;
  }
}
