// metric value details
.timeline {
  .year {
    width: 35px; 
  }
  .timeline-header {
    height: 22px;
    margin-top: 15px;
    .th {
      float: left;
      margin: 0px 10px 0px 10px;
      font-weight: bold;
    }
    .new a {
      color: $dark-text-color;
      background-color: $bright-text-color;
      border: 1px solid #cdcdcd;
      font-weight: normal;
      font-size: 0.8em;
      padding: 2px 5px 2px 5px;
    }
  }
  .timeline-body {
    .timeline-row {
      position: relative;
      clear: both;
      height: 52px;
      font-size: 12px;
      color: $dark_text_color;
      .td {
        float: left;
        margin: 0px 10px 0px 10px;
        .metric-value {
          font-size: 16px;
        }
      }
      .year, .credit {
        padding-top: 2px;
      }
      .search-result-list {
        display: inline-block;
        padding: 0px;
        .search-result-item {
          display: inline;
          padding-left: 0;
        }
      }
    }

  }
  .timeline-dot {
    position: absolute;
    top: 8px;
    left: 46px;
    height: 5px;
    width: 5px;
    background: gray;
  }
  .timeline-line {
    position: absolute;
    top: 10px;
    left: 48px;
    height: 54px;
    width: 1px;
    border-left: 1px solid gray;
  }
}

.RIGHT-company.card-content .company-item .item-link>a {
  background: #03998d;
  border-top-left-radius: 0px;
  border-bottom-left-radius: 0px;
}

.company-item {
  color: $dark_text_color;
  width: 100%;
  background: #fff;
  overflow: hidden;

  .header {
    width: 60%;
    border-left: 3px solid $company_color;

