.STRUCTURE-claim_item {
  margin: 0 0 5px;
  border-bottom: 1px dotted #eee;
  font-size: 0.8em;
  padding: 0 3%;
  /* hack for hash jump */
  padding-top: 60px;
  margin-top: -60px;

  div.last-edit {
    margin-left: 18px;
    margin-bottom: 10px;
    color: #adadad;
    font-size: 12px;
    .item-link {
      padding-left: 0;
    }

  }
  .item-summary {
    h4 {
      font-size: 1.3em;
      line-height: 1.4em;
    }
    > div {

      .fa {
        padding-right: 5px;
      }
      #margin-left: 5%;
      padding: 2px 0;
      #text-indent: -3%;
      div {
        display: inline;
      }
      .RIGHT-source .source-title {
        font-style: italic;
      }
    }

  }
  .claim-sourcelink {
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    .card-content {
      white-space: nowrap;
    }
    .pointer-item.item-name {
      padding-left: 0;
    }

  }
  .claim-tag {
    .pointer-item,
    .search-result-item {
      text-indent: 10px;
      margin: 0;
      padding: 2px;
      :after {
        content: ' ';
      }
    }
  }

}
.claim-next-action {
  .internal-link {
    color: red;
    float: right;
    text-decoration: underline;
  }

}
.TYPE_PLUS_RIGHT-claim-related_article {
  .company {
    color: white;
  }
  .topic {
    color: white;
  }

}
#citation-popup-window {
  .TYPE_PLUS_RIGHT-claim-related_article {
    width: 100%;
    .analysis-link {
      width: 100%;
      .claim-next-action {
        float: right;
      }
    }
  }

}
.titled_with_voting-view.STRUCTURE-claim_summary {
  >i {
    float: left;
    padding-top: 2%;
    padding-right: 6px;

  }
  /*
  .cited-claim-number
  {
    padding:1px 7px;
    margin-top: 2%;
    float : left;
  }
*/

}
.filter-search-form {
  display: inline-block!important;
  margin: 30px 0 20px 16px;
  border-bottom: 1px solid #eaeaea;
  padding-bottom: 12px;
  width: 100%;
  .chosen-container {
    display: inline-block;
  }
  .editor {
    width: 100%;
    padding: 0 5%;
  }
  .form-group{
    margin: 5px 1px;
    border: 0;
    font-size: 0.8em;
    float: left !important;
    clear: none;
    
    label{
      padding: 0 5%;
      font-size: 15px;
      font-weight: inherit;
    }
    select {
      max-width: 100%;
      height: 25px;
    }
    input {
      height: 23px;
    }
    .button-area {
      margin-top: 20px;
      font-size: 1.2em;
    }
  }
  
  
  .form-group.filter-input.tag {
    width: 20% !important;
  }
  
  .form-group.filter-input {
    width: 18%;
    input {
      width: inherit;
    }
    li.search-field {
      height: 23px;
    }
  }

}
.SELF-claim.card-content > .new-claim-button,
.SELF-source.card-content > .new-source-button {
  padding: 20px 0;
  text-align: center;

}
.RIGHT-filter_search {
  clear: both;

}
.fa-quote-left,
.fa-quote-right {
  color: #bfbfbf;

}
.TYPE_PLUS_RIGHT-claim-related_article {
  #close-tip {
    display: none;
  }

}
.TYPE-pointer.edit-view > .card-body {
  overflow: visible;

}
input.navbox {
  font-family: FontAwesome;
}

.SELF-how_it_work.card-content .table-of-contents {
  float: right;
  max-width: 50%;
  padding: 2px 3%;

}
.SELF-openelectronics15-article,
.SELF-openfood14-article,
.SELF-openfashion14-article {
  .pointer-item.item-content {
    width: 17%;
    display: inline-block;
    padding: 0.5%;
    float: left;
  }

}


/*quick fix for company pagingation position - should investigate why this happend*/
.SELF-other_company .search-result-list {
  display: inline-block;
}

.TYPE-analysi .cite-button {
float: left;
margin-top: 75px;
}
