  margin: 30px 0 20px 16px;
  border-bottom: 1px solid #eaeaea;
  padding-bottom: 12px;
  width: 100%;
  .chosen-container {
    display: inline-block;
  }
  .editor {
    width: 100%;
    padding: 0 5%;
  }
  .form-group{
    margin: 5px 1px;
    border: 0;
    font-size: 0.8em;
    float: left !important;
    clear: none;
    
    label{
      padding: 0 5%;
      font-size: 15px;
      font-weight: inherit;
    }
    select {
      max-width: 100%;
      height: 25px;
    }
    input {
      height: 23px;
    }
    .button-area {
      margin-top: 20px;
      font-size: 1.2em;
    }
  }
  
  
  .form-group.filter-input.tag {
    width: 20% !important;
  }
  
  .form-group.filter-input {
    width: 18%;
    input {
      width: inherit;
    }
    li.search-field {
      height: 23px;
    }
  }

}
.SELF-claim.card-content > .new-claim-button,
.SELF-source.card-content > .new-source-button {
  padding: 20px 0;
  text-align: center;

}
.RIGHT-filter_search {
  clear: both;

}
.fa-quote-left,
.fa-quote-right {
  color: #bfbfbf;

}
.TYPE_PLUS_RIGHT-claim-related_article {
  #close-tip {
    display: none;
  }

}
.TYPE-pointer.edit-view > .card-body {
  overflow: visible;

}
input.navbox {
  font-family: FontAwesome;
}

.SELF-how_it_work.card-content .table-of-contents {
  float: right;
  max-width: 50%;
  padding: 2px 3%;

}
.SELF-openelectronics15-article,
.SELF-openfood14-article,
.SELF-openfashion14-article {
  .pointer-item.item-content {
    width: 17%;
    display: inline-block;
    padding: 0.5%;
    float: left;
  }

}

.RIGHT-summary{
  .item-summary, .RIGHT-discussion, .item-content{
    float: left;
    width: 100%;
    .missing-view{
      display: none;
    }
  }
}


/*quick fix for company pagingation position - should investigate why this happend*/
.SELF-other_company .search-result-list {
  display: inline-block;
}

.TYPE-analysi .cite-button {
float: left;
margin-top: 75px;
}

$drag_item_height: 52px;
$dark_text_color: #999;
$bright_text_color: #ededed;
$company-header-bg: #d3741c;
$metric-color: #674ea7;

.yinyang {
  border-top: 1px solid $bright_text_color;
  h4 {
    text-align: center;
    color: #999;
    margin-bottom: 10px;
  }
}

#main >.TYPE-company.card-frame > .card-body {
  padding: 0;
}
#main > .card-frame.TYPE-company > .card-header {
  background: $company-header-bg;
  color: #fff;
  a {
    color: inherit;
  }

}

.show-with-details {
  display: none;
}

.label-metric {
  background: #674ea7;
  color: white;
  a {
    font-weight: normal;
    color: white;
  }
}

.TYPE-company {
  .panel-body {
    padding: 0;
  }
  .top {
    height: 100px;
    > .TYPE_PLUS_RIGHT-company-logo {
      height: 100px;
      padding: 10px;
      border-right: 1px solid $bright_text_color;
    }
    > .TYPE_PLUS_RIGHT-company-about {
      margin-left: 110px;
      width: inherit;
      padding-top: 10px;
    }
    a.readmore-js-toggle {
      width: inherit;
      margin: 14px;
    }
  }

}
.header-row {
  font-size: 10px;
  color: $bright_text_color;
  text-align: center;
  margin-top: 10px;
  .header-header {
    float: left;
    margin-left: 80px;
  }
  .data-header {
    width: 161px;
    float: right;
    margin-right: 15px;
  }

}
.empty-message {
  padding: 5px;
border: 1px dashed #ddd;
text-align: center;
height: 32px;
color: #ddd;
background: #fff;
font-weight: normal;
}
.unsaved-message {
  display: none;
  height: 25px;

}
.list-drag-and-drop, .yinyang-list {
  clear: both;
  margin: 10px;
  padding: 5px;
  background: none;
  border: none;
  box-shadow: none;
  --webkit-box-shadow: none;
}


.drag-item, .yinyang-row {
  padding: 0;
  margin-bottom: 6px;
  border: 1px solid $bright_text_color;
  background: rgb(251, 251, 251);
  color: rgb(204, 204, 204);
  .header {
    height: $drag_item_height;
    float: left;

    .handle {
      float: left;
      margin-top: 15px;
      margin-left: 5px;
      cursor: move;
      color: #ddd;
    }
    .RIGHT-Xvote_count > div {
      line-height: 1em;
    }
    button.vote-button {
      color: $bright_text_color;
      width: 15px;
      height: 15px;
      padding: 0;
      border: none;
      box-shadow: none;
      --webkit-box-shadow: none;
      background: none;
    }
    .vote-count {
      color: $bright_text_color;
      width: 15px;
      height: 15px;
    }
    .logo {
      padding-top: 5px;
      float: left;
      img {
        width: 35px;
        height: 35px;
        margin: 0 10px;
      }

    }
    .name {
      padding-top: 11px;
      font-size: 14px;
      float: left;
      max-width: 250px;
      text-overflow: ellipsis;
      white-space: nowrap;
      overflow: hidden;
    }

  }
  .data {
    height: $drag_item_height;
    float: right;
    height: $drag_item_height;
    border-left: 1px solid $bright_text_color;

    .data-item {
      margin-top: 15px;
      padding-left: 15px;
    }

  }
  .details {
    clear: both;
    color: $dark_text_color;
    padding: 10px;
    font-size: 12px;
    display: none;
    border-top: 1px solid $bright_text_color;
  }
  .details-toggle {
    float: right;
    font-size: 11px;
    padding: 19px 2px 0 4px;
    height: $drag_item_height;
    min-width: 12px;
    height: $drag_item_height;
  }

}
// filter
.filter-header {
  cursor: pointer;
}
.votee-filter {
  background: #f2f2f2;
  color: $dark_text_color;
  padding: 5px;
  text-align: center;
  font-size: 12px;
  font-weight: bold;
}
.filter-details {
  display: none;
  margin-bottom: -4px;
  margin-left: -5px;
  margin-right: -4px;
  background: #FDFDFD;
  
}
.filter-toggle {
  float: right;

}
// topic specific

.wiki {
  padding-left: 0;
  padding-right: 0;
  .yin {
    position: absolute;
    top: 5px;
    right: 0;
    color: #f7f7f7;
    font-size: 50px;
    line-height: 50px;
  }

}
.topic-item {
  overflow: hidden;
  .header {
    border-left: 3px solid #2b7ea0;
    .name {
      color: #2b7ea0;
      font-size: 18px;
    }

  }
  .btn-highlight.contribution a {
    color: #fff;
  }
  .btn-highlight a,
  .btn-default a {
    text-decoration: none;
  }
  .btn-default{
    border-color: $bright_text_color;
  }
  .btn-default.contribution .content {
    color: #737373;
  }
  .btn-default.contribution .name {
    color: #ccc;

  }
  .contribution {
    float: right;
    width: 58px;
    height: 54px;
    font-size: 14px;
    font-weight: bold;
    text-align: center;
    margin-top: -1px;
    margin-right: -1px;
    margin-left: -1px;
    &.btn.btn-highlight {
      background-color: #f29393;
      border-color: #f06464;
      &:hover {
        background-color: #f06e6e;
      }

    }
    .glyphicon {
      font-size: 12px;
    }
    a {
      // color: #999;
    }
    &.danger {
      /*border: 1px solid #E06666;*/
      background: #ea9999;
      color: #fff;
      a {
        color: #fff;
      }
      .name {
        color: #fff;
      }
      .content {
        // padding-top: 6px;
      }

    }
    &.warning {
      a {
        text-decoration: none;
        color: #dadada;
      }

    }
    .content {
      // padding-top: 10px;
    }
    .name {
      font-size: 11px;
      text-align: center;
      margin-left: -2px;
      font-weight: normal;
      padding-top: 2px;

    }
    // border-right: 1px solid $bright_text_color;
  }

}
// metric specific

.rate {
  padding-left: 0;
  padding-right: 0;
  background: #f7f7f7;

  .yang {
    position: absolute;
    top: 5px;
    left: 0;
    color: #fff;
    font-size: 50px;
    line-height: 50px;
  }

}

.metric-year, .metric-unit {
  font-size: 12px;
  color: $dark_text_color;
}
.metric-value {
  color: #674ea7;
  font-size: 18px;
  font-weight: bold;
}

.no-metric-value {
  display: none;
}
.RIGHT-summary, .metric-item {
  color: $dark_text_color;
  width: 100%;
  background: #fff;
  overflow: hidden;

  .header {
    width: 60%;
    border-left: 3px solid #674ea7;

    .name {
      width: 70%;
    }

  }
  .data {
    width: 38%;
    .data-item {
      float: left;
      width: 90%;
    }
  }
  .timeline {
    .year, .credit {
      margin-top: 2px;
    }
  }
  
  .metric-details-link {
    background: #674ea7;
  }
  .metric-info {
    border-bottom: 1px solid $bright_text_color;
    .row {
      margin-left: 5px;
      margin-right: 5px;
      .row-icon {
        text-align: center;
        font-size: 17px;
        float: left;
        width: 30px;
        color: $bright_text_color;
      }
      .glyphicon-question-mark {
        font-weight: bold;
      }
      .row-data {
        margin-left: 40px;
      }
    }
    
  }
}

 // metric value details
.timeline {
  .year {
    width: 35px; 
  }
  .timeline-header {
    margin-top: 15px;
    .th {
      float: left;
      margin: 0px 10px 0px 10px;
      font-weight: bold;
    }
    a {
      color: $dark-text-color;
      background-color: $bright-text-color;
      border: 1px solid #cdcdcd;
      font-weight: normal;
      font-size: 0.8em;
      padding: 2px 5px 2px 5px;
    }
  }
  .timeline-body {
    .timeline-row {
      position: relative;
      clear: both;
      height: 60px;
      font-size: 12px;
      color: $dark_text_color;
      .td {
        float: left;
        margin: 0px 10px 0px 10px;
        .metric-value {
          font-size: 16px;
        }
      }
      .year, .credit {
        padding-top: 2px;
      }
      .search-result-list {
        display: inline-block;
        padding: 0px;
        .search-result-item {
          display: inline;
          padding-left: 0;
        }
      }
    }

  }
  .timeline-dot {
    position: absolute;
    top: 8px;
    left: 46px;
    height: 5px;
    width: 5px;
    background: gray;
  }
  .timeline-line {
    position: absolute;
    top: 10px;
    left: 48px;
    height: 65px;
    width: 1px;
    border-left: 1px solid gray;
  }
}

/*----------- General Variables ---------*/
        
        $heading_color: #555;
        $body_background: #ebebeb;
        $primary_color: #555;
        $anchor_color: #03998d;
        $body-txt-color: #777;
        $wanted-card-color: #b6060a;
        $closed-content-a-color: #729;
        $infographic-heading: #949494;
        $div-border-color: #f1f1f1;
        
        //button colors
        $button-color: #03998d;
        $button-color-hover: #009185;
        $button-border: #00756c;
        $cancel-button-color: #bebebe;
        $cancel-button-border: #9b9b9b;
        $cancel-button-hover: #b4b4b4;
        
        //claim colors
        $claim-tag-companybg: #d3741c;
        $claim-tag-companybg-hover: #bb5b00;
        $claim-tag-topicbg: #2b7ea0;
        $claim-tag-topicbg-hover: #01648d;
        $claim-tag-notexist: #f0f0f0;
        $claim-tag-notexist-hover: #ececec;
        
        $card-header-color: #dbdbdb;
        
        $claim-color: #03998d;
        $source-color: #ffc201;
        $article-color: #666;
        $heading-color: #999;
        
        @mixin box-shadow($x: 2px, $y: 2px, $blur: 5px, $color: rgba(0, 0, 0, .4), $inset: "") {
          @if ($inset != "") {
            -webkit-box-shadow: $inset $x $y $blur $color;
            -moz-box-shadow: $inset $x $y $blur $color;
            box-shadow: $inset $x $y $blur $color;
          }
          @else {
            -webkit-box-shadow: $x $y $blur $color;
            -moz-box-shadow: $x $y $blur $color;
            box-shadow: $x $y $blur $color;
          }
        
        }
        /*----------- Top banner ---------*/
        .container-topbar{
            max-width: 1250px;
            margin: 0 auto;
            .navbar-right {
              margin-right: 15px;
            }
          
        }
        
        div#openelectronics_slider_image {
          padding: 12px 25px;
        }
        #top-banner-wrapper {
          .campaign-content {
            font-size: 0.8em;
          }
          >div {
            overflow: inherit;
          }
          .column-1,
          .column-2,
          .column-3 {
            background: #fafafa;
            height: 38px;
            float: left;
            margin-left: 0.3%;
            margin-bottom: 20px;
            border: 0 solid #eaeaea;
            padding-bottom: 14%;
          }
          .column-1,
          .column-3 {
            width: 24%;
          }
          .column-1 {
            overflow: hidden;
            position: relative;
        
          }
          .slick-dots {
            bottom: -5%;
          }
          iframe {
            width: 100%;
            height: 175px;
          }
          .video-container {
            display: none;
          }
          .SELF-video_image {
            cursor: pointer;
            &:hover {
              @include box-shadow(0,0px,6px,rgba(0,0,0,.35));
              z-index: 9;
            }
        
          }
          .column-2 {
            position: relative;
            width: 50%;
            .wikirate-message {
              display: block;
              text-align: center;
              line-height: normal;
              padding: 30px;
              .homepage-heading-h1 {
                font-size: 1.5em;
              }
            }
        
          }
          .column-3 {
            .SELF-banner_column3 {
              overflow: hidden;
            }
            .banner-column-button a {
              text-align: center;
              display: block;
              font-size: 0.9em;
            }
            .banner-column-button {
              margin: 10%;
            }
          }
        
        }
        .SELF-wikirate_globe.card-content {
          width: 25%;
          padding: 30px 3px 18px 30px;
          max-width: 30%;
          float: left;
          overflow: hidden;
          img {
            max-height: 100%;
          }
        
        }
        /*----------- Content Wrapper --------------*/
        #main .video-container {
          visibility: hidden;
          height: 0;
          width: 0;
          opacity:0;
        }
        #page-wrapper {
          background: #fff;
          padding: 20px 12px;
        
        }
        #main-content-wrapper {
          margin: 0 5px;
        
        }
        .browse-button {
          text-align: center;
          margin-top: 5px;
          margin-left: 16px;
        
        }
        /*-----------  Grid  ---------*/
        .grid-row {
          display: inline-block;
          width: 100%;
          .grid-column {
            width: 49%;
            // background: green;
          }
        
        }
        /*----------- Companies and Topics  ---------*/
        #company-n-topic .grid-column {
          position: relative;
          //for slick-js icon position
        
        }
        #company-n-topic .card-header {
          display: none;
          // workaround for carousel
        
        }
        #company-n-topic {
          .company-list,
          .topic-list {
            .search-result-list {
              // workaround for carousel
              height: 220px;
              overflow: hidden;
              margin-left: 3.8%;
              width: 94%;
              max-width: 555px;
            }
            .search-result-item {
              display: inline-block;
              min-width: 130px;
              width: 175px;
              padding: 10px 0;
              margin-left: 11px;
            }
        
          }
          .company-list {
            float: left;
          }
          .topic-list {
            float: right;
          }
          .browse-button {
            margin-top: 35px;
          }
        
        }
        /*----------- Articles ---------*/
        
        #articles {
          .search-result-item {
            display: inline-block;
            margin: 1.4%;
            width: 30%;
          }
        
        }
        /*----------- Claims and Sources ---------*/
        
        #claims-sources {
        
          .card-content.SELF-homepage_claim_list,
          .card-content.SELF-homepage_source_list {
            padding: 5px 0;
            margin: 15px 3.5%;
            border: 1px solid #ddd;
            @include box-shadow(0,1px,6px,rgba(0,0,0,.13));
            height: 750px;  // Temporary fix for same height , it should be dynamic at somepoint...
            overflow: auto;
          }
          .card-content.SELF-homepage_claim_list {
            border-top: 8px solid $claim-color;
          }
          .card-content.SELF-homepage_source_list {
            border-top: 8px solid $source-color;
          }
          .claim-list,
          .source-list {
            width: 48%;
            .search-result-list {
            //  padding: 0 0 1000px;
              //same height workaround.
           //   margin-bottom: -1000px;
            }
        
          }
          .claim-list {
            float: left;
          }
          .source-list {
            float: right;
          }
          .browse-button {
            margin-top: 25px;
          }
        
        }
        /*----------- Headings ---------*/
        
        .homepage-heading-h1 {
          text-align: center;
          font-size: 1.6em;
          color: $heading-color;
          .count-color {
            color: $article-color;
            /* font-weight: 900;*/
          }
          .company-color {
            color: $claim-tag-companybg;
          }
          .topic-color {
            color: $claim-tag-topicbg;
          }
          .article-color {
            color: $article-color;
          }
          .claim-color {
            color: $claim-color;
          }
          .source-color {
            color: $source-color;
          }
        
        }
        .homepage-heading-h2 {
        font-size: 2em;
        font-weight: normal;
        padding: 14px 0 0;
        }
        .homepage-heading-h3{
        font-weight: normal;
        }
        /*----------- Media queries ---------*/
        
        @media screen and (max-width: 1150px) {
          .homepage-heading-h1 {
            font-size: 1.2em;
          }
          .homepage-heading-h2 {
            font-size: 1.6em;
          }
          .homepage-heading-h3 {
            font-size: 1.1em;
          }
          #top-banner-wrapper .column-2 .wikirate-message .homepage-heading-h1 {
            font-size: 1.2em;
          }
        
        }
        @media screen and (max-width: 1205px) {
          .homepage-heading-h1 {
            font-size: 1.1em;
          }
          .homepage-heading-h2 {
            font-size: 1.4em;
          }
          #top-banner-wrapper .column-2 .wikirate-message .homepage-heading-h1 {
            font-size: 1.1em;
          }
        
        }
        @media screen and (max-width: 990px) {
          .homepage-heading-h1 {
            font-size: 0.89em;
          }
          .homepage-heading-h2 {
            font-size: 1em;
          }
          .homepage-heading-h3 {
            font-size: 0.9em;
          }
          #top-banner-wrapper .column-2 .wikirate-message .homepage-heading-h1 {
            font-size: 0.9em;
          }
        
        }
        @media screen and (max-width: 48em) {
        
          #claims-sources {
            .claim-list,
            .source-list {
              width: 100%;
              clear: both;
              margin-bottom: 30px;
            }
            .card-content.SELF-homepage_source_list,
            .card-content.SELF-homepage_claim_list {
              height: auto;
            }
          }
        
        }
        @media screen and (max-width:47em ) {
        
          .homepage-heading-h1 {
            //        font-size:0.9em;
          }
          .homepage-heading-h2 {
            font-size: 1.8em;
          }
          #top-banner-wrapper .column-2 {
            .wikirate-message {
              padding: 1%;
              .homepage-heading-h1 {
                font-size: 1.5em;
              }
        
            }
            .SELF-wikirate_globe.card-content {
              padding: 1%;
              max-width: 16%;
            }
        
          }
          #top-banner-wrapper {
            .column-1,
            .column-3 {
              display: none;
            }
            .column-2 {
              width: 100%;
            }
          }
        
        }
        /*----------- Media queries- For mobile devices ---------*/
        
        @media screen and (max-width:40em ) {
        
          .grid-row,.grid-column{
            margin-bottom:24px;
          }
        
          .homepage-heading-h1 {
                   font-size:1em;
          }
          .homepage-heading-h2 {
            font-size: 1em;
          }
          #top-banner-wrapper {
        
            .column-2 {
              .wikirate-message {
                padding: 1%;
                .homepage-heading-h1 {
                  font-size: 0.9em;
                }
        
              }
              .SELF-wikirate_globe.card-content {
                padding: 1%;
                max-width: 16%;
              }
            }
        
          }
          #articles .search-result-item {
            display: inline-block;
            margin: 1.4%;
            width: 97%;
        
          }
          #company-n-topic {
            .company-list,
            .topic-list {
              width: 100%;
            }
        
          }
          body#wagn {
            min-width: 325px;
        
          }
          #claims-sources {
            .card-content.SELF-homepage_claim_list,
            .card-content.SELF-homepage_source_list {
              margin: 15px 0;
            }
        
          }
          #top-bar {
            .logo-bar img {
              margin-top: 0;
              height: 25px;
              border: none;
            }
            .account-info-bar {
              display: none;
              float: right;
              position: absolute;
              top: 6%;
              right: 35px;
