/*----------- General Variables ---------*/

$heading_color: #555;
$body_background: #ebebeb;
$primary_color: #555;
$anchor_color: #03998d;
$body-txt-color: #777;
$wanted-card-color: #b6060a;
$closed-content-a-color: #729;
$infographic-heading: #949494;
$div-border-color: #f1f1f1;

//button colors
$button-color: #03998d;
$button-color-hover: #009185;
$button-border: #00756c;
$cancel-button-color: #bebebe;
$cancel-button-border: #9b9b9b;
$cancel-button-hover: #b4b4b4;

//claim colors
$claim-tag-companybg: #d3741c;
$claim-tag-companybg-hover: #bb5b00;
$claim-tag-topicbg: #2b7ea0;
$claim-tag-topicbg-hover: #01648d;
$claim-tag-notexist: #f0f0f0;
$claim-tag-notexist-hover: #ececec;

$card-header-color: #dbdbdb;

$claim-color: #03998d;
$source-color: #ffc201;
$article-color: #666;
$heading-color: #999;

@mixin box-shadow($x: 2px, $y: 2px, $blur: 5px, $color: rgba(0, 0, 0, .4), $inset: "") {
  @if ($inset != "") {
    -webkit-box-shadow: $inset $x $y $blur $color;
    -moz-box-shadow: $inset $x $y $blur $color;
    box-shadow: $inset $x $y $blur $color;
  }
  @else {
    -webkit-box-shadow: $x $y $blur $color;
    -moz-box-shadow: $x $y $blur $color;
    box-shadow: $x $y $blur $color;
  }

}
/*----------- Top banner ---------*/

#top-banner-wrapper {
  >div {
    overflow: inherit;
  }
  .column-1,
  .column-2,
  .column-3 {
    background: #fafafa;
    height: 38px;
    float: left;
    margin-left: 0.3%;
    margin-bottom: 20px;
    border: 1px solid #eaeaea;
    padding-bottom: 11%;
  }
  .column-1,
  .column-3 {
    width: 24%;
  }
  .column-1 {
    overflow: hidden;
    position:relative;
    .slick-dots {
        bottom: -5%;
     }
    iframe {
      width: 100%;
      height: 175px;
    }
    .video-container{display:none;}
   .SELF-video_image{cursor:pointer;}

  }
  .column-2 {
    position: relative;
    width: 50%;
    .wikirate-message {
      display: block;
      text-align: center;
      line-height: normal;
      padding: 30px;
      .homepage-heading-h1 {
        font-size: 1.5em;
      }
    }

  }
  .column-3 {
    .SELF-banner_column3 {
      overflow: hidden;
    }
    .banner-column-button a {
      text-align: center;
      display: block;
      font-size: 0.9em;
    }
    .banner-column-button {
      margin: 10%;
    }
  }

}
.SELF-wikirate_globe.card-content {
  width: 25%;
  padding: 18px 16px;
  max-width: 30%;
  float: left;
  overflow: hidden;
  img {
    max-height: 100%;
  }

}
/*----------- Content Wrapper --------------*/
#main .video-container{
visibility:hidden;
height:0;
width:0;
}
#page-wrapper {
  background: #fff;
  padding: 20px 12px;

}
#main-content-wrapper {
  margin: 0 5px;

}
.browse-button {
  text-align: center;
  margin-top: 5px;
  margin-left: 16px;

}
/*-----------  Grid  ---------*/
.grid-row {
  display: inline-block;
  width: 100%;
  .grid-column {
    width: 49%;
    // background: green;
  }

}
/*----------- Companies and Topics  ---------*/
#company-n-topic .grid-column {
  position: relative;
  //for slick-js icon position

}
#company-n-topic .card-header {
  display: none;
  // workaround for carousel

}
#company-n-topic {
  .company-list,
  .topic-list {
    .search-result-list {
      // workaround for carousel
      height: 220px;
      overflow: hidden;
      margin-left: 5%;
      width: 92%;
    }
    .search-result-item {
      display: inline-block;
      min-width: 130px;
      width: 175px;
      padding: 10px 0;
      margin-left: 11px;
    }

  }
  .company-list {
    float: left;
  }
  .topic-list {
    float: right;
  }
  .browse-button {
    margin-top: 35px;
  }

}
/*----------- Articles ---------*/

#articles {
  .search-result-item {
    display: inline-block;
    margin: 1.4%;
    width: 30%;
  }

}
/*----------- Claims and Sources ---------*/

#claims-sources {

  .card-content.SELF-homepage_claim_list,
  .card-content.SELF-homepage_source_list {
    padding: 5px 0px;
    margin: 15px 3.5%;
    border: 1px solid #ddd;
    @include box-shadow(0,1px,6px,rgba(0,0,0,.13));
    height: 750px;
    // Temporary fix for same height , it should be dynamic at somepoint...
    overflow: hidden;
  }
  .card-content.SELF-homepage_claim_list {
    border-top: 8px solid $claim-color;
  }
  .card-content.SELF-homepage_source_list {
    border-top: 8px solid $source-color;
  }
  .claim-list,
  .source-list {
    width: 48%;
    .search-result-list {
      padding: 0px;
    }

  }
  .claim-list {
    float: left;
  }
  .source-list {
    float: right;
  }
  .browse-button {
    margin-top: 25px;
  }

}
/*----------- Headings ---------*/

.homepage-heading-h1 {
  text-align: center;
  font-size: 1.6em;
  color: $heading-color;
  .count-color {
    color: $article-color;
   /* font-weight: 900;*/
  }
  .company-color {
    color: $claim-tag-companybg;
  }
  .topic-color {
    color: $claim-tag-topicbg;
  }
  .article-color {
    color: $article-color;
  }
  .claim-color {
    color: $claim-color;
  }
  .source-color {
    color: $source-color;
  }
}
.homepage-heading-h2 {
  font-size: 1.9em;
  font-weight: normal;
  padding: 5px 0 0;

}
/*----------- Media queries ---------*/

@media screen and (max-width: 1150px) {
  .homepage-heading-h1 {
    font-size: 1.2em;
  }
  .homepage-heading-h2 {
    font-size: 1.6em;
  }
  #top-banner-wrapper .column-2 .wikirate-message .homepage-heading-h1 {
    font-size: 1.2em;
  }

}
@media screen and (max-width: 1205px) {
  .homepage-heading-h1 {
    font-size: 1.1em;
  }
  .homepage-heading-h2 {
    font-size: 1.4em;
  }
  #top-banner-wrapper .column-2 .wikirate-message .homepage-heading-h1 {
    font-size: 1.1em;
  }

}
@media screen and (max-width: 990px) {
  .homepage-heading-h1 {
    font-size: 0.89em;
  }
  .homepage-heading-h2 {
    font-size: 1em;
  }
  #top-banner-wrapper .column-2 .wikirate-message .homepage-heading-h1 {
    font-size: 0.9em;
  }

}
@media screen and (max-width: 48em) {

  #claims-sources {
    .claim-list,
    .source-list {
      width: 100%;
      clear: both;
      margin-bottom: 30px;
    }
    .card-content.SELF-homepage_source_list,
    .card-content.SELF-homepage_claim_list {
      height: auto;
    }
  }

}
@media screen and (max-width:47em ) {

  .homepage-heading-h1 {
      //        font-size:0.9em;
  }
  .homepage-heading-h2 {
            font-size: 1.8em;
  }
  #top-banner-wrapper .column-2 {
    .wikirate-message {
      padding:1%;
      .homepage-heading-h1 {
             font-size: 1.5em;
      }
    }
    .SELF-wikirate_globe.card-content{
      padding: 1%;
      max-width: 16%;
    }
  }

  #top-banner-wrapper {
    .column-1,
    .column-3 {
      display: none;
    }
    .column-2 {
      width: 100%;
    }
  }
}

/*----------- Media queries- For mobile devices ---------*/

@media screen and (max-width:40em ) {

  .homepage-heading-h1 {
      //        font-size:0.9em;
  }
  .homepage-heading-h2 {
            font-size: 1.2em;
  }
  #top-banner-wrapper {

    .column-2 {
    .wikirate-message {
      padding:1%;
      .homepage-heading-h1 {
             font-size: 1em;
      }
    }
    .SELF-wikirate_globe.card-content{
      padding: 1%;
      max-width: 16%;
    }
  }
  }

  #articles .search-result-item {
    display: inline-block;
    margin: 1.4%;
    width: 97%;
  }

  #company-n-topic {
    .company-list,
    .topic-list {
      width: 100%;
    }
  }

  body#wagn{
    min-width: 330px;
  }


#claims-sources {
    .card-content.SELF-homepage_claim_list,
    .card-content.SELF-homepage_source_list {
      margin:15px 0px;
    }
}

#top-bar {
  .logo-bar img {
    margin-top: 0;
    height: 25px;
    border: none;
  }
  .account-info-bar{
    display:none;
    margin: -9px 14px;
    float: right;
  }
  .search-bar-mobile{
    display:block;
    float:right;
  }
  .search-bar {
    width: 92%;
    display:none;
  }
}

a.slicknav_btn {
  float: left;
  margin: 5px 10px 0px 0px;
}

#top-bar-wrapper .slicknav_menu .slicknav_nav .account-info-bar .logged-out {
  display: none;
}

}

//***ALL COLOR VARS & MIXINS***//
$heading_color: #555;
$body_background: #ebebeb;
$primary_color: #555;
$anchor_color: #03998D;
$body-txt-color: #777;
$wanted-card-color: #b6060a;
$closed-content-a-color: #729;
$infographic-heading: #949494;
$div-border-color:#f1f1f1;

//button colors
$button-color: #03998D;
$button-color-hover: #009185;
$button-border: #00756C;
$cancel-button-color: #BEBEBE;
$cancel-button-border: #9B9B9B;
$cancel-button-hover:#B4B4B4;

//claim colors
$claim-tag-companybg: #D3741C;
$claim-tag-companybg-hover:#BB5B00;
$claim-tag-topicbg:#2B7EA0;
$claim-tag-topicbg-hover:#01648D;
$claim-tag-notexist:#f0f0f0;
$claim-tag-notexist-hover: #ececec;

$card-header-color: #fff;

//card colors
@mixin card-txt-color{
  color: #646464;
  a {
    color: #646464;
  }
}

//border radius mixin
@mixin border-radius($radius) {
  -webkit-border-radius: $radius;
  border-radius: $radius;
  background-clip: padding-box;  /* stops bg color from leaking outside the border: */
}
//***COLOR VARS - END***//

//***Shadow***//

@mixin box-shadow($x: 2px, $y: 2px, $blur: 5px, $color: rgba(0,0,0,.4), $inset: "") {
  @if ($inset != "") {
    -webkit-box-shadow: $inset $x $y $blur $color;
    -moz-box-shadow:    $inset $x $y $blur $color;
    box-shadow:         $inset $x $y $blur $color;
  } @else {
    -webkit-box-shadow: $x $y $blur $color;
    -moz-box-shadow:    $x $y $blur $color;
    box-shadow:         $x $y $blur $color;
  }
}

// ***Shadow- END***//

body {
  color: $body-txt-color;
  font-family: 'Droid Sans', sans-serif;
  line-height: 1.5em;
  font-size: 0.9em;
}

body#wagn {
  min-height: 100%;
  min-width: 480px;
  background: $body_background;
  padding: 0;
  margin: 0;
}

body#tinymce {
   padding: 4px;
}

a {
  color: $anchor_color;
}

a.wanted-card {
  color: $wanted-card-color;
}

/* many sites may want this external link behavior; it's here because it's a nuisance to override when not desired */
a.external-link,
.closed-content a.external-link {
  color: $closed-content-a-color;
  background: url(/assets/external_link.png) center right no-repeat;
  padding-right: 13px !important;
}

img {
  border: 0
}

h1, h2 {
  color: $heading_color;
}

h1 {
  font-size: 1.2em;
  font-weight: normal;
  line-height: 1.8em;
  padding: 5px 0;
}

.more-link {
  float: right;
}

.card-header {
  font-family: 'Droid Sans';
}
.card-content .titled-view > .card-header {
  line-height: 1.4em;
  margin-top: 1em;
}

.card-frame {
  border-radius: 4px;
  border: 0px solid #E5E5E5;
  > .card-header {
    background: $card-header-color;
font-size: 1.1em;
// font-weight: bold;
    @include card-txt-color;
  }
  > .instruction {
    background: #bbb;
  }
}


.RIGHT-logo.card-content img {
  max-height: 100%;
}

#Home{
  overflow: inherit;
}
#main {
    font-size: 1.1em;
    clear: both;
    padding: 70px 0%;
    max-width: 1260px;
    margin: 0 auto;
  .card-frame > .card-body > .titled-view {
    margin: 15px 0;
    > .card-body {
      margin: 5px 0 5px 3%;
    }
    > .card-content {
      padding: 10px 20px 15px 20px;
    }
  }
  .card-frame {
    border-radius: 0px;
    > .card-body > h1 {
      font-size: 2em;
      font-weight: normal;
      line-height: 2em;
      padding: 5px 0 0 0;
    }
  }
  > .card-slot > h1 {
    font-size: 1.5em;
    padding: 0 5px;
    .card-title {
      text-align: center;
      display: inline-block;
      width: 90%;
      /*text-indent: 10%;*/
      margin-left:5%;
    }
  }
}
.card-slot > .card-header .card-menu-link {
  display: none;
}
.card-slot.closed-view,
.card-slot:hover {
  > .card-header .card-menu-link {
    display: inline-block;
    margin-left: 10px;
  }
}




.revision-navigation,
.current {
  background: #FFFFFF;
}


body.home-layout #main {
  > .card-content {
    .SELF-wikirate_infographic {
text-align: center;
border-radius: 4px;
-moz-border-radius: 4px;
float: right;
border: 0px solid #E5E5E5;
background: #fff;
width: 41%;
padding-top: 20px;
margin-bottom: 12px;
      > .SELF-infographic {
        float: left;
        display: inline-block;
        width:68%;
      }
      span {
       font-size: 1.8em;
       color: $infographic-heading;
      }
      p {
        line-height: 2em;
        color: #d5d5d5;
        font-size: 1.3em;
      }
      > .wikirate-video-container {
        position: relative;
        padding-bottom: 17%;
        padding-top: 0px;
        height: 0;
        overflow: hidden;
        left: 14px;
        top: -14px;
        width: 30.2%;
        margin: 0px;
        iframe {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
        }
      }
    }
    .SELF-industry_of_the_month.content-view {
width: 100%;
text-align: center;
padding: 0px 0px 24px 0px;
float: left;
    }
    .home-left {
      width: 58%;
      float: left;
      .SELF-featured_topic.card-frame {
        margin-top: 40px;
      }
    }
    .home-right {
      width: 41%;
      float: right;
    }
    > #wikirate_tagline {
      text-align: center;
      overflow: visible;
      font-size: 2em;
      padding: 1.5em;
    }
  }
}


.STRUCTURE-company_feature,
.STRUCTURE-topic_feature {
  clear: both;
  padding: 20px 0;
  min-height: 200px;
}

.STRUCTURE-company_feature,
.STRUCTURE-topic_feature {
  > a {
    font-size: 1.3em;
  }

  h1 {
    font-size: 0.9em;
    font-style: italic;
    color: #aaa;
  }

  .TYPE-image.content-view {
    width: 25%;
    max-width: 100px;
    padding: 0 3% 10px 0;
    min-height: 200px;

    img {
        border-radius: 10px;
       -moz-border-radius: 10px;
    }
  }
}

/*~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/

body > .SELF-wikirate_footer {
  clear: both;
  display: block;
  padding: 45px 30px 10px 30px;
  text-align: center;
  font-size: 0.9em;
  background: #fff;
  border: 1px solid #E5E5E5;
a.external-link {
  padding-right: 0 !important;
  background: inherit !important;
}
[class*="SELF-logo_"] {
display: inline-block;
}
}

#credit,
#credit a {
  padding: 0 !important;
  background: none;
  border: 0px;
  margin-bottom: 15px;
  position: relative;
}

#credit {
  z-index: -0;
  left: 8px;
  display: block;
  a {
    right: 16px;
    position: relative;
    z-index: -1;
  }
}

.RIGHT-claim_count.content-view {
  display: inline;
  float: right;
  color: #999;
}

.search-result-item > .TYPE-page.content-view {
  .RIGHT-website .pointer-list {
    #font-size: 1.1em;
    #font-weight: bold;
    padding-bottom: 0;
  }
  .RIGHT-title {
    font-size: 0.9em;
    margin: 0 0 7px 3%;
  }
}



.RIGHT-image_source.content-view {
  font-size: 0.8em;
  font-style: italic;
}

.TYPE-analysi,
.TYPE-company,
.TYPE-topic {
  &.STRUCTURE-analysi_comparison a.internal-link,
  >.source-side > a {
    // display: block;
    // padding: 10px;
    // margin: 10px 5%;
    // float: right;
    // border: 2px solid #555;
    // border-radius: 10px;
    // background: #eee;
    // font-size: 1.2em;
  }
}
.TYPE-analysi {
  &.STRUCTURE-analysi_comparison a.internal-link,
  >.source-side > a {
    float: none;
    text-align: center;
  }
}



.TYPE-topic .STRUCTURE-topic_analysi_blurb,
.TYPE-company .STRUCTURE-company_analysi_blurb {
  padding: 10px 0;
  border-bottom: 1px solid #eee;
  > a {
    float: right;
  }
}

.STRUCTURE-analysi_blurb {
  > .RIGHT-article,
  > .RIGHT-claim {
    padding-left: 5%;
  }
  .RIGHT-claim {
    > .card-header {
      font-size: 0.9em;
      font-style: italic;
      color: #aaa;
      padding: 0;
    }
    .search-result-item {
      padding-bottom: 10px;
    }

  }
}


.STRUCTURE-icon_and_link.content-view > a > img,
.STRUCTURE-company_analysi_link.content-view > a > img,
.STRUCTURE-topic_analysi_link.content-view > a > img,
.STRUCTURE-company_link.content-view > a > img,
.STRUCTURE-topic_link.content-view > a > img {
  border-radius: 3px;
  -moz-border-radius: 3px;
  margin-right: 5px;
}

.STRUCTURE-topic_analysi_link_and_compare .STRUCTURE-compare_link,
.RIGHT-link_and_compare .RIGHT-compare_link {
  float: right;
}

.STRUCTURE-claim_summary {
  > h1 {
    font-size: 1.1em;
    padding-bottom: 0;
  }
  > .card-body {
    padding:  5px 0 18px 5%;
    margin: 0 0 5px 0px;
    border-bottom: 1px solid #eee;
    font-size: 0.8em;
    .fa {
      padding-right: 5px;
    }
    > div {
      margin-left: 5%;
      padding: 2px 0;
      text-indent: -3%;
      div {
        display: inline;
      }
    }
    .missing-view {
      display: none;
    }
    .RIGHT-source .source-title {
      font-style: italic;
    }
    .pointer-item, .search-result-item {
       text-indent: 10px;
       margin: 0;
       margin-right: 0px !important;
       padding: 2px;
      :after {
        content: ' ';
      }
    }
  }
}

#claim-sourcelink .pointer-item a {
  color: $body-txt-color;
    &:hover{
      color:$anchor_color;
    }
 }

.analysis-link > a,
.RIGHT-company.titled-view > .card-content .item-link > a,
.RIGHT-topic.titled-view > .card-content  .item-link > a {
    // margin: 3px 10px;
}

.analysis-link > a,
.RIGHT-company.card-content .item-link > a,
.RIGHT-topic.card-content .item-link > a,
#company-and-topic .company-name,
#company-and-topic  .topic-name {
  display: inline-block;
  text-indent: 0;
  border-radius: 3px;
  font-size: 0.9em;
  color: #fff;
  text-decoration: none;
}

.RIGHT-company.card-content .item-link > a,
.RIGHT-topic.card-content .item-link > a,
.analysis-link > a > span{
  display: inline-block;
  padding: 0px 6px !important;
  margin-bottom: 4px;
}

#company-and-topic{
  .company-name, .topic-name {
    padding: 1px 3px !important;
  }
  .company-name.no-content,.topic-name.no-content{
    background:none!important;
  }
}

.RIGHT-company.card-content .item-link > a,
.analysis-link > a > .company,
#company-and-topic .company-name {
  background: $claim-tag-companybg;
  border-top-left-radius: 3px;
  border-bottom-left-radius: 3px;
}
.RIGHT-company.card-content .item-link > a:hover,
.analysis-link > a:hover > .company,
#company-and-topic .company-name:hover {
  background: $claim-tag-companybg-hover;
}


.RIGHT-topic.card-content .item-link > a,
.analysis-link > a > .topic,
#company-and-topic  .topic-name {
  background:  $claim-tag-topicbg;
  border-top-right-radius: 3px;
  border-bottom-right-radius: 3px;
}
.RIGHT-topic.card-content .item-link > a:hover,
.analysis-link > a:hover > .topic,
#company-and-topic  .topic-name:hover {
  background: $claim-tag-topicbg-hover;
}

.card-content .item-link > a.wanted-card {
  background: $claim-tag-notexist;
  border-top-left-radius: 3px;
  border-bottom-left-radius: 3px;
  color: $wanted-card-color;
  &:hover{
    background: $claim-tag-notexist-hover;
  }
}


