.nav-tabs > li {
  &.active > a {
    border-radius: 0px;
    border: 0px solid #E6E6E6;
    border-bottom: 4px solid #03998D;
    &:hover, &:focus {
      border-radius: 0px;
      border: 0px solid #E6E6E6;
      border-bottom: 4px solid #03998D;
    }
  }
  > a {
    background-color: #fff;
    border-radius: 0px;
    border: 0px solid #E6E6E6;
    border-bottom: 4px solid #DADADA;
    margin: 3px -1px;
    padding: 18px 20px;
    font-size: 15px;
    color: #8E8E8E;
  }
}

