//***ALL COLOR VARS & MIXINS***//
$heading_color: #555;
$body_background: #ebebeb;
$primary_color: #555;
$anchor_color: #03998d;
$body-txt-color: #777;
$wanted-card-color: #b6060a;
$closed-content-a-color: #729;
$infographic-heading: #949494;
$div-border-color: #f1f1f1;

//button colors
$button-color: #03998d;
$button-color-hover: #00A99B;
$button-border: #00756c;
$cancel-button-color: #b2b2b2;
$cancel-button-border: #bebebe;
$cancel-button-hover: #cfcfcf;
$cancel-button-background: #fff;

//claim colors
$claim-tag-companybg: #d3741c;
$claim-tag-companybg-hover: #bb5b00;
$claim-tag-topicbg: #2b7ea0;
$claim-tag-topicbg-hover: #01648d;
$claim-tag-notexist: #f0f0f0;
$claim-tag-notexist-hover: #ececec;

$card-header-color: #fff;

// //snippet for all buttons in Wikirate. Should use this .button primary class anywhere a button is required for a positive action(like submit, Go, etc)
button,
.button-primary,
.right-sidebar-button a,
.TYPE-page .source-side .internal-link,
.TYPE-analysi.STRUCTURE-analysi_comparison a.internal-link,
.ui-dialog .ui-dialog-buttonpane button {
  margin-right: 1%;
  background: $button-color;
  border: none;
  color: #fff;
  -webkit-border-radius: 1px;
  border-radius: 1px;
  background-clip: padding-box;
  border-bottom: 4px solid $button-color;
  padding: 10px 12px 6px;
  cursor: pointer;
  text-decoration: none !important;
  font-size: 0.7em;
  outline: none;
  -webkit-transition: background 200ms linear;
  -moz-transition: background 200ms linear;
  -o-transition: background 200ms linear;
  transition: background 200ms linear;
  &:hover {
    background: $button-color-hover;
    padding: 10px 12px 6px;
    border-bottom: 4px solid $button-border;

  }
  &:active {
    background: $button-color-hover;
    border-top: 2px solid $button-border;
    border-bottom: 0;
    padding: 9px 12px;

  }
  &.create-cancel-button,
  &.cancel-button,
  &.button-secondary,
  &.grey-color {
    background: $cancel-button-background;
    color: $cancel-button-color;
    border: 1px solid $cancel-button-border;
    padding-bottom: 8px;
    &:hover {
      background: $cancel-button-hover;
      padding: 10px 11px 6px 12px;
      border-top: 1px solid $cancel-button-border;
      border-right: 1px solid $cancel-button-border;
      border-left: 1px solid $cancel-button-border;
      border-bottom: 3px solid #9b9b9b;
      color: #fff;
    }
    &:active {
      background: $cancel-button-hover;
      border-top: 2px solid $cancel-button-border;
      border-bottom: 0;
      padding: 9px 12px;
    }
  }

}

//Tweak to large buttons in topic,company and article page
.right-sidebar-button a {
  text-align: center;
  display: block;
  font-size: 0.9em;
  margin-bottom: 10px;
}

//Seconday button for anchors
.button-secondary-a a,
.grey-color a {
  background: $cancel-button-background;
  color: $cancel-button-color;
  border: 1px solid $cancel-button-border;
  padding-bottom: 8px;
  &:hover {
    background: $cancel-button-hover;
    padding: 10px 11px 6px 12px;
    border-top: 1px solid $cancel-button-border;
    border-right: 1px solid $cancel-button-border;
    border-left: 1px solid $cancel-button-border;
    border-bottom: 3px solid #9b9b9b;
    color: #fff;
  }
  &:active {
    background: $cancel-button-hover;
    border-top: 2px solid $cancel-button-border;
    border-bottom: 0;
    padding: 9px 12px;
  }
}

/*----------- General Variables ---------*/

$heading_color: #555;
$body_background: #ebebeb;
$primary_color: #555;
$anchor_color: #03998d;
$body-txt-color: #777;
$wanted-card-color: #b6060a;
$closed-content-a-color: #729;
$infographic-heading: #949494;
$div-border-color: #f1f1f1;

//button colors
$button-color: #03998d;
$button-color-hover: #009185;
$button-border: #00756c;
$cancel-button-color: #bebebe;
$cancel-button-border: #9b9b9b;
$cancel-button-hover: #b4b4b4;

//claim colors
$claim-tag-companybg: #d3741c;
$claim-tag-companybg-hover: #bb5b00;
$claim-tag-topicbg: #2b7ea0;
$claim-tag-topicbg-hover: #01648d;
$claim-tag-notexist: #f0f0f0;
$claim-tag-notexist-hover: #ececec;

$card-header-color: #dbdbdb;

$claim-color: #03998d;
$source-color: #ffc201;
$article-color: #666;
$heading-color: #999;

@mixin box-shadow($x: 2px, $y: 2px, $blur: 5px, $color: rgba(0,0,0,.4), $inset: "") {
  @if ($inset != "") {
    -webkit-box-shadow: $inset $x $y $blur $color;
    -moz-box-shadow:    $inset $x $y $blur $color;
    box-shadow:         $inset $x $y $blur $color;
  } @else {
    -webkit-box-shadow: $x $y $blur $color;
    -moz-box-shadow:    $x $y $blur $color;
    box-shadow:         $x $y $blur $color;
  }
}

/*----------- Top banner ---------*/

#top-banner-wrapper {
  .column-1,
  .column-2,
  .column-3 {
    background: #fafafa;
    height: 175px;
    float: left;
    margin-left: 0.4%;
    margin-bottom: 20px;
    border: 1px solid #eaeaea;
  }
  .column-1,
  .column-3 {
    width: 24%;
  }
  .column-1{
    iframe{
      width:100%;
      height:175px;
    }
  }
  .column-2 {
    position:relative;
    width: 50%;
    .wikirate-message {
      display: block;
      text-align: center;
      line-height: normal;
      padding: 30px;
      .homepage-heading-h1{
        font-size: 1.5em;
      }
    }
  }
  .column-3 {
    .SELF-banner_column3{
       overflow:hidden;
      }
    .banner-column-button a {
      text-align: center;
      display: block;
      font-size: 0.9em;
      margin: 30px 20px;
    }
  }
}

#wikirate_globe {
  height: 130px;
  padding: 18px 16px;
  max-width: 30%;
  float: left;
  overflow:hidden;
}
#wikirate_globe img {
  max-height: 100%;
}

/*----------- Content Wrapper --------------*/
#page-wrapper {
  background: #fff;
  padding: 20px 12px;

}
#main-content-wrapper {
  margin: 0 5px;

}
.browse-button {
  text-align: center;
  margin-top: 5px;
  margin-left: 16px;

}
/*-----------  Grid  ---------*/
.grid-row {
  display: inline-block;
  width: 100%;
  .grid-column {
    width: 49%;
    // background: green;
  }

}
/*----------- Companies and Topics  ---------*/
#company-n-topic .grid-column {
  position: relative;
  //for slick-js icon position

}
#company-n-topic .card-header {
  display: none;
  // workaround for carousel

}
#company-n-topic {
  .company-list,
  .topic-list {
    .search-result-list {
      // workaround for carousel
      height: 220px;
      overflow: hidden;
      margin-left: 26px;
      width: 550px;
    }
    .search-result-item {
      display: inline-block;
      min-width: 130px;
      width: 175px;
      padding: 10px 0;
      margin-left: 11px;
    }
  }
  .company-list {
    float: left;
  }
  .topic-list {
    float: right;
  }
  .browse-button {
    margin-top: 35px;
  }

}
/*----------- Articles ---------*/

#articles {
  .search-result-item {
    display: inline-block;
    margin: 10px;
  }

}
/*----------- Claims and Sources ---------*/

#claims-sources {

  .card-content.SELF-homepage_claim_list,
  .card-content.SELF-homepage_source_list {
    padding: 5px 15px;
    margin: 15px 15px;
    border: 1px solid #ddd;
    @include box-shadow(0,1px,6px,rgba(0,0,0,.13));
    height: 750px; // Temporary fix for same height , it should be dynamic at somepoint...
    overflow: hidden;
  }
  .card-content.SELF-homepage_claim_list {
    border-top:8px solid $claim-color;
  }
  .card-content.SELF-homepage_source_list {
    border-top:8px solid $source-color;
  }
  .claim-list,
  .source-list {
    width: 48%;
    .search-result-list {
      padding: 15px;
    }

  }
  .claim-list {
    float: left;
  }
  .source-list {
    float: right;
  }
  .browse-button {
    margin-top: 25px;
  }
}

/*----------- Headings ---------*/

.homepage-heading-h1  {
  text-align: center;
  font-size:1.6em;
  color: $heading-color;
  .count-color{
    color:$article-color;
    font-weight:900;
  }
  .company-color{
    color:$claim-tag-companybg;
  }
  .topic-color{
    color:$claim-tag-topicbg;
  }
  .article-color{
    color: $article-color;
  }
  .claim-color{
    color: $claim-color;
  }
  .source-color{
    color:$source-color
  }
}
.homepage-heading-h2 {
  font-size: 1.9em;
  font-weight: normal;
  padding: 5px 0 0 0;
}

@media screen and (max-width: 1150px) {
    .homepage-heading-h1  {
      font-size:1.2em;
    }
    .homepage-heading-h2 {
      font-size: 1.6em;
    }
    #top-banner-wrapper .column-2 .wikirate-message .homepage-heading-h1 {
      font-size: 1.2em;
    }
}

@media screen and (max-width: 1085px) {
    .homepage-heading-h1  {
      font-size:1.1em;
    }
    .homepage-heading-h2 {
      font-size: 1.4em;
    }
    #top-banner-wrapper .column-2 .wikirate-message .homepage-heading-h1 {
      font-size: 1.1em;
    }
}

@media screen and (max-width: 1085px) {
    .homepage-heading-h1  {
      font-size:0.9em;
    }
    .homepage-heading-h2 {
      font-size: 1em;
    }
    #top-banner-wrapper .column-2 .wikirate-message .homepage-heading-h1 {
      font-size: 0.9em;
    }
}

@mixin box-shadow($x: 2px, $y: 2px, $blur: 5px, $color: rgba(0,0,0,.4), $inset: "") {
  @if ($inset != "") {
    -webkit-box-shadow: $inset $x $y $blur $color;
    -moz-box-shadow:    $inset $x $y $blur $color;
    box-shadow:         $inset $x $y $blur $color;
  } @else {
    -webkit-box-shadow: $x $y $blur $color;
    -moz-box-shadow:    $x $y $blur $color;
    box-shadow:         $x $y $blur $color;
  }
}

@mixin opacity($trans) {
  filter: alpha(opacity=($trans * 100));
  -ms-filter: "progid:DXImageTransform.Microsoft.Alpha(Opacity=#{$trans * 100})";
  -moz-opacity: $trans;
  -khtml-opacity: $trans;
  opacity: $trans;
}

@mixin vertical-align {
  position: relative;
  top: 50%;
  -webkit-transform: translateY(-50%);
  -ms-transform: translateY(-50%);
  transform: translateY(-50%);
}

.STRUCTURE-topic_overview,
.STRUCTURE-company_overview {
  //box-shadow: 0px 0px 12px 1px rgba(221, 221, 221, 0.87);
  //border-radius: 10px;
  //-moz-border-radius: 10px;
  text-align: center;
  border: 1px solid rgb(213, 213, 213);
  width: 100%;
  font-size: 0.8em;
  @include box-shadow(0,1px,6px,rgba(0,0,0,.13));
  &:hover .TYPE-image{
    @include opacity(1);
  }
  a{
    color: #999;
  }
  a:hover{
    text-decoration: none;
  }

  .card-title {
    display: inline-block;
    height: 60px;
  }
  .TYPE-image {
    width: 100%;
    padding: 0px 0px;
    height: 116px;
    overflow: hidden;
    text-align: center;
    @include opacity(1);
    img {
      max-height: 100%;
      text-align: center;
      display: inline-block;
     @include vertical-align;

    }
  }
  /*.claim-count {
    color: #999;
    .RIGHT-claim_count {
      color: #444;
      float: none;
    }
  }*/
  .box-middle{
    padding: 4px 0px 4px;
    border-top: 1px solid #d5d5d5;
    border-bottom: 1px solid #d5d5d5;
    border-bottom-style: double;
    border-top-style: double;

    span{
      display: block;
    }
    .card-title{
      padding: 0px 2px;
      white-space: nowrap;
      overflow: hidden;
      text-overflow: ellipsis;
      width: 170px;
      height: 22px;
    }
  }
  .box-bottom{
    overflow:auto;
    background-color: #f0f0f0;
    span{
      display: block;
    }
    .contribution-count{
      float: left;
      width: 49%;
      border-right: 1px solid #d5d5d5;
      border-right-style: double;
      span:nth-child(2) {
        font-weight: bold;
      }
    }
    .claim-count{
      float: right;
      width: 49%;
      span:nth-child(2) {
        font-weight: bold;
      }
    }
  }
}

//claim colors
$claim-tag-companybg: #d3741c;
$claim-tag-companybg-hover: #bb5b00;
$claim-tag-topicbg: #2b7ea0;
$claim-tag-topicbg-hover: #01648d;
$claim-tag-notexist: #f0f0f0;
$claim-tag-notexist-hover: #ececec;
$anchor_color: #03998d;

$card-header-color: #dbdbdb;



@mixin box-shadow($x: 2px, $y: 2px, $blur: 5px, $color: rgba(0,0,0,.4), $inset: "") {
  @if ($inset != "") {
