  -ms-filter: "progid:DXImageTransform.Microsoft.Alpha(Opacity=#{$trans * 100})";
  -moz-opacity: $trans;
  -khtml-opacity: $trans;
  opacity: $trans;
}

@mixin vertical-align {
  position: relative;
  top: 50%;
  -webkit-transform: translateY(-50%);
  -ms-transform: translateY(-50%);
  transform: translateY(-50%);
}
/*----------- Article items --------------*/
.SELF-article-current.TYPE-search .search-result-item {
  display: inline-block;
  margin: 5px 5px;
}
.article-item {
    display: inline-block;
    width: 360px;
    text-align: center;
    border: 1px solid #ddd;
    overflow: hidden;
    height: 380px;
    @include box-shadow(0,1px,6px,rgba(0,0,0,.13));
    margin: 2px 3px;

    .mouse-hover{
      @include opacity(0.3);
    }
    &:hover .mouse-hover{
      @include opacity(1);
    }
  /*----------- Article images and titles --------------*/
  .article-images {
    color: #fff;
    position:relative;
    .company-image,
    .topic-image {
      float: left;
      .TYPE-image {
          height: 120px;
          width: 180px;
          overflow: hidden;
          img{
            @include vertical-align;
          }
      }

    }
    .venn-icon-pos{
      position: absolute;
      font-size: 55px;
      left: 155px;
      top: 28px;
      z-index: 1;
      .SELF-venn_icon {
          margin-top: -62px;
       }
       img{
         height:24px
       }
    }
    .company-n-topic-title {
      padding: 4px 5px;
      font-size: 0.8em;
      white-space: nowrap;
      overflow: hidden;
      text-overflow: ellipsis;
      width: 170px;
    }
    .company-title {
      background: $claim-tag-companybg;
      :hover {
        background: $claim-tag-companybg-hover;
      }

    }
    .topic-title {
      background: $claim-tag-topicbg;
      :hover {
        background: $claim-tag-topicbg-hover;
      }
    }

  }
  /*----------- Article content --------------*/
  .article-content {
    display: inline-block;
    padding: 0 14px;
    color: #ADADAD!important;
    .article-edits {
      padding: 12px 0;
      float: left;
      width: 100%;
    }
    .article-desc {
        text-align: left;
        font-size: 0.9em;
        float: left;
        height: 162px;
      h1{
        font-size: 0.9em;
      }
    }
    .article-claim-count {
      padding: 15px 0 10px;
      font-size: 0.8em;
      color: $anchor_color;
    }
    .article-edit-count {
      float: left;
    }
    .edits-by {
      float: left;
      width: 94%;
      border: 0;
      .known-card {
        float: left;
      }
    }
  }
}

@charset "UTF-8";

// Default Variables

$slick-font-path: "./fonts/" !default;
$slick-font-family: "FontAwesome" !default;
$slick-loader-path: "./" !default;
$slick-arrow-color: #888 !default;
$slick-dot-color: black !default;
$slick-dot-color-active: $slick-dot-color !default;
$slick-prev-character: '\f104' !default;
$slick-next-character: '\f105' !default;
$slick-dot-character: '\f111' !default;
$slick-dot-size: 6px !default;
$slick-opacity-default: .75 !default;
$slick-opacity-on-hover: 1 !default;
$slick-opacity-not-active: .25 !default;


// @function slick-image-url($url) {
//   @if function-exists(image-url) {
//     @return image-url($url, false, false);
//   }
//   @else  {
//     @return url($slick-loader-path + $url);
//   }
// }

// @function slick-font-url($url) {
//   @if function-exists(font-url) {
//     @return font-url($url);
//   }
//   @else  {
//     @return url($slick-font-path + $url);
//   }
// }

/* Slider */

.slick-slider {
    position: relative;
    display: block;
    box-sizing: border-box;
    -moz-box-sizing: border-box;
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -khtml-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    -ms-touch-action: pan-y;
    touch-action: pan-y;
    -webkit-tap-highlight-color: transparent;
}
.slick-list {
    position: relative;
    overflow: hidden;
    display: block;
    margin: 0;
    padding: 0;

    &:focus {
        outline: none;
    }

    .slick-loading & {
        background: #fff slick-image-url("ajax-loader.gif") center center no-repeat;
    }

    &.dragging {
        cursor: pointer;
        cursor: hand;
    }
}
.slick-slider .slick-track {
    -webkit-transform: translate3d(0, 0, 0);
    -moz-transform: translate3d(0, 0, 0);
    -ms-transform: translate3d(0, 0, 0);
    -o-transform: translate3d(0, 0, 0);
    transform: translate3d(0, 0, 0);
}

.slick-track {
    position: relative;
    left: 0;
    top: 0;
    display: block;

    &:before,
    &:after {
        content: "";
        display: table;
    }

    &:after {
        clear: both;
    }

    .slick-loading & {
        visibility: hidden;
    }
}
.slick-slide {
    float: left;
    height: 100%;
    min-height: 1px;
    [dir="rtl"] & {
        float: right;
    }
    img {
        display: block;
    }
    &.slick-loading img {
        display: none;
    }

    display: none;

    &.dragging img {
        pointer-events: none;
    }

    .slick-initialized & {
        display: block;
    }

    .slick-loading & {
        visibility: hidden;
    }

    .slick-vertical & {
        display: block;
        height: auto;
        border: 1px solid transparent;
    }
}

/* Icons */
@if $slick-font-family == "slick" {
  @font-face {
      font-family:"slick";
      src:    slick-font-url("slick.eot");
      src:    slick-font-url("slick.eot?#iefix") format("embedded-opentype"),
              slick-font-url("slick.woff") format("woff"),
              slick-font-url("slick.ttf") format("truetype"),
              slick-font-url("slick.svg#slick") format("svg");
      font-weight: normal;
      font-style: normal;
  }
}

/* Arrows */

.slick-prev,
.slick-next {
    position: absolute;
    display: block;
    height: 20px;
    width: 20px;
    line-height: 0;
    font-size: 0;
    cursor: pointer;
    background: transparent;
    color: transparent;
    top: 50%;
    margin-top: -10px;
    padding: 0;
    border: none;
    outline: none;
    &:hover, &:focus {
      outline: none;
      background: #f1f1f1;
      border: 0;
      padding: 0px 0px 2px 1px;
      color: #000;
      height: 22px;
      border-radius: 0;
      &:before {
        opacity: $slick-opacity-on-hover;
      }
    }
    &.slick-disabled:before {
        opacity: $slick-opacity-not-active;
    }
}
.slick-prev:before, .slick-next:before {
    font-family: $slick-font-family;
    font-size: 20px;
    line-height: 1;
    color: $slick-arrow-color;
    opacity: $slick-opacity-default;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}
.slick-prev {
    left: 0px;
    [dir="rtl"] & {
        left:  auto;
        right: -25px;
    }
    &:before {
        content: $slick-prev-character;
        [dir="rtl"] & {
            content: $slick-next-character;
        }
    }
}
.slick-next {
    right: -8px;
    [dir="rtl"] & {
        left:  -25px;
        right: auto;
    }
    &:before {
        content: $slick-next-character;
        [dir="rtl"] & {
            content: $slick-prev-character;
        }
    }
}

/* Dots */

.slick-slider {
    margin-bottom: 30px;
}
.slick-dots {
      position: absolute;
      bottom: 20px;
      list-style: none;
      display: block;
      text-align: center;
      padding: 0;
      width: 100%;
      left: 5px;

    li {
        position: relative;
        display: inline-block;
        height: 20px;
        width: 20px;
        margin: 0 5px;
        padding: 0;
        cursor: pointer;

        button {
            border: 0;
            background: transparent;
            display: block;
            height: 20px;
            width: 20px;
            outline: none;
            line-height: 0;
            font-size: 0;
            color: transparent;
            padding: 5px;
            cursor: pointer;
            &:hover, &:focus {
                outline: none;
                &:before {
                  opacity: $slick-opacity-on-hover;
                }
            }

            &:before {
                position: absolute;
                top: 0;
                left: 0;
                content: $slick-dot-character;
                width: 20px;
                height: 20px;
                font-family: $slick-font-family;
                font-size: $slick-dot-size;
                line-height: 20px;
                text-align: center;
                color: $slick-dot-color;
                opacity: $slick-opacity-not-active;
                -webkit-font-smoothing: antialiased;
                -moz-osx-font-smoothing: grayscale;
            }

        }

        &.slick-active button:before {
            color: $slick-dot-color-active;
            opacity: $slick-opacity-default;
        }
    }
}

/*----------- Navigation color variables ---------*/
$nav-srch-background: #fff;
$nav-srch-txt-color: #a8a8a8;
$navmenu-anchor-color: #fff;
$navmenu-hover-bgcolor: #03998d;
$navsbmenu-bgcolor: #03998d;
$navsbmenu-anchor-color: #00ac9e;

/*----------- General variables ---------*/

$anchor_color: #03998d;
$body-txt-color: #777;


/*----------- Mixins ---------*/

@mixin searchbox-color {
  background: $nav-srch-background !important;
  color: $nav-srch-txt-color !important;
}
@mixin searchbox-color-focus {
  background-color: #f3f4de !important;
  color: #a8a8a8 !important;
}

@mixin box-shadow($x: 2px, $y: 2px, $blur: 5px, $color: rgba(0,0,0,.4), $inset: "") {
  @if ($inset != "") {
    -webkit-box-shadow: $inset $x $y $blur $color;
    -moz-box-shadow:    $inset $x $y $blur $color;
    box-shadow:         $inset $x $y $blur $color;
  } @else {
    -webkit-box-shadow: $x $y $blur $color;
    -moz-box-shadow:    $x $y $blur $color;
    box-shadow:         $x $y $blur $color;
  }
}

/*----------- Top bar ---------*/

#top-bar-wrapper {
  background: #fff;
  position: fixed;
  width: 100%;
  -webkit-transition:  200ms linear;
  -moz-transition:  200ms linear;
  -o-transition:  200ms linear;
  transition: 200ms linear;
  border-bottom: 1px solid #ebebeb;
  z-index:9999;
}

#top-bar {
  padding: 5px 3%;
  margin: 0 auto;
  max-width: 1260px;
  .logo-bar {
    float: left;
    img {
      margin-top: 3px;
      height: 40px;
      border: none;
    }

  }
  /*//----------- Top bar - Navigation menu ---------*/
  .menu-bar {
    float: left;
    width: 31%;
    padding: 5px;
    margin-left: 12px;

    a {
      color: $navmenu-anchor-color;
      display: block;
      margin: 0;
      letter-spacing: 0.1em;
    }
    .main-links {
      width: 100%;
      font-family: FontAwesome;
      color: $navmenu-anchor-color;
      > div {
        color: $anchor_color;
        float: left;
        padding: 10px 3%;
        cursor: pointer;
        span {
          display: block;
          margin: 0;
        }

      }
      > div:hover {
        background: $navmenu-hover-bgcolor;
        color: $navmenu-anchor-color;
      }

    }
    ul {
      display: none;
      position: absolute;
      z-index: 1;
      border-radius: 0;
      border: none;
      padding: 0;
      > li {
        padding: 0;
      }
      a {
        border: 0;
        font-size: 0.9em;
        width: auto;
        min-width: 120px;
        padding: 5px 28px;
        background: $navsbmenu-bgcolor;
        border-radius: inherit;
      }
      a:hover {
        background: $navsbmenu-anchor-color;
      }
    }
    .menu-bar-border{
      border-bottom-color: #03998d;
      border: 0.2em solid #03998d;
      margin: -6px 0px !important;
      padding: 3px 8px;
    }
  }

  /*//----------- Top bar - Search box ---------*/

  .search-bar {
    float: left;
    padding: 8px;
    width: 22%;
    input{
      width:100%;
      padding: 0px 10px;
    }
  }

  /*//----------- Top bar - User sign up/account info ---------*/

  .account-info-bar {
    text-align: right;
  }
  .account-info-bar .logged-out {
    margin-top: 10px;
  }


  #logging {
    display: block;
    a {
      margin-left: 10px;
    }
    a.button-primary {
    }
    a:hover {
    }
  }
}

#logging.logged-in a {
  font-size: .8em;
}
#logging a#my-card-link {
  display: block;
  font-size: 1.2em;
}

