}
body.mceContentBody {
  margin: 4px;
}
*/
input[type=text],
input[type=password],
textarea,
select,
body#tinymce{
    background: $input_color;
    border: solid 1px $border_color;
    // @include border-radius(3px); removing border for new home page ui pattern 
  &:focus {
    @include box-shadow(0px,0px,0px,1px,$input_focus_color);
    outline:0;
  }
  &:hover {
    border-color: $input_focus_color;
  }
}
input[type=text],
input[type=password]
{
  height: $input_textbox_height;
}
.history-view > .card-content,
.conflict-view,
body#tinymce {
  background: $input_color;
  @include border-radius(0)
