.list-drag-and-drop, .yinyang-list {
  clear: both;
  margin: 10px;
  padding: 5px;
  background: none;
  border: none;
  box-shadow: none;
  --webkit-box-shadow: none;
}

@mixin yinyang-frame($header-color) {
  > .card-body.card-content {
    padding: 0;
  }
  > .card-header {
    background: $header-color;
    .panel-title {
      color: #fff;
       a {
        color: inherit !important;
        font-size: 16px;
      } 
    }
  }
}

#main > .card-frame.TYPE-company, .card-frame.TYPE-company { @include yinyang-frame($company-header-bg); }
#main > .card-frame.TYPE-topic,   .card-frame.TYPE-topic   { @include yinyang-frame($topic-header-bg); }
#main > .card-frame.TYPE-metric,  .card-frame.TYPE-metric  {
  @include yinyang-frame($metric-header-bg);
  > .card-body.card-content {
    .yinyang {
      .wiki, .rate{
        padding-top: 0px;
      }
    }
  }
}

.TYPE-metric {
  .rate {
    padding-top: 34px;
    .RIGHT-methodology {
      margin-top: 20px;
      padding-top: 15px;
    }
    .chosen-container {
      height: 12px;
      margin-left: -18px;
    }
  }
  .wiki {
    .metric-info .row {
      padding-bottom: 14px;
      .RIGHT-discussion .card-header {
      margin-top: 0px !important;
      }
    }
    .name {
      font-size: 1.5em;
      padding: 24px 24px;
      margin-left: 24px;
      position: relative;
      color: $metric-header-bg;
    }
    .metric-info .header {
      height: 100px;
      margin-top: 10px;
      margin-left: -1px;
      padding-left: 10px;
      border-bottom: 1px solid $bright-text-color;
      margin-bottom: 20px;
