    }
  }
}


.drag-item, .yinyang-row{
  padding: 0;
  margin-bottom: 6px;
  border: 1px solid $bright-text-color;
  background: rgb(251, 251, 251);
  color: rgb(204, 204, 204);
  .contribution-item {
    overflow: hidden;
    .header {
      .name {
        font-size: 18px;
      }
    }
    &.metric-item .header .name {
      color: $metric-header-bg;
    }
    &.company-item .header .name {
      color: $company-header-bg;
    }
    &.topic-item .header .name {
      color: $topic-header-bg;
    }
  }
  .value-item {
    color: $dark-text-color;
    width: 100%;
    background: #fff;
    overflow: hidden;
    .header {
      width: 60%;
      cursor: move;
      .name {
        width: 70%;
      }
    }
    .data {
      width: 38%;
      .data-item {
        float: left;
        width: 90%;
      }
    }
  }
  
  .topic-item .header {
    border-left: 3px solid $topic-header-bg;
  }
  .company-item .header {
    border-left: 3px solid $company-header-bg;
  }
  .metric-item {
    .header {
      border-left: 3px solid $metric-header-bg;
    }
    .timeline {
      .year, .credit {
        margin-top: 2px;
      }
    }
    .metric-info {
      border-bottom: 1px solid $bright-text-color;
      .row {
        margin-left: 5px;
        margin-right: 5px;
        .row-icon {
          text-align: center;
          font-size: 17px;
          float: left;
          width: 30px;
          color: $bright-text-color;
        }
        .glyphicon-question-mark {
          font-weight: bold;
        }
        .row-data {
          margin-left: 40px;
        }
      }
    }
  }

  .header {
    height: $drag-item-height;
    float: left;
    .handle {
      float: left;
      margin-top: 15px;
      margin-left: 5px;
      cursor: move;
      color: #ddd;
    }
    .RIGHT-Xvote_count > div {
      line-height: 1em;
    }
    button.vote-button {
      color: $dark-text-color;
      width: 15px;
      height: 15px;
      padding: 0;
      border: none;
      box-shadow: none;
      --webkit-box-shadow: none;
      background: none;
    }
    .vote-count {
      color: $dark-text-color;
      width: 15px;
      height: 15px;
    }
    .logo {
      padding-top: 5px;
      float: left;
      img {
        max-width: 35px;
        max-height: 35px;
        margin: 0 10px;
      }

    }
    .name {
      padding-top: 11px;
      font-size: 14px;
      float: left;
      max-width: 250px;
      text-overflow: ellipsis;
      white-space: nowrap;
      overflow: hidden;
    }
  }
  
  
  .data {
    height: $drag-item-height;
    float: right;
    height: $drag-item-height;
    border-left: 1px solid $bright-text-color;
    
    .btn-highlight a,
    .btn-default a {
      text-decoration: none;
    }
    .btn-default{
      border-color: $bright-text-color;
    }
    .data-item {
      margin-top: 15px;
      padding-left: 15px;
    }
    
    .contribution {
      float: right;
      width: 58px;
      height: 54px;
      font-size: 14px;
      font-weight: bold;
      text-align: center;
      margin-top: -1px;
      margin-right: -1px;
      margin-left: -1px;
      &.company-count {
        width: 108px;
      }
      &.metric-details {
        width: 108px;
        padding-top: 15px;
      }
      &.btn-default {
        .name {
          color: #ccc;
        }
        .content {
          color: #737373;
        }
      }
      &.btn.btn-highlight {
        background-color: #f29393;
        border-color: #f06464;
        a {
          color: #fff;
        }
        &:hover {
          background-color: #f06e6e;
        }
      }
      .glyphicon {
        font-size: 12px;
      }
      &.danger {
        /*border: 1px solid #E06666;*/
        background: #ea9999;
        color: #fff;
        a {
          color: #fff;
        }
        .name {
          color: #fff;
        }
      }
      &.warning {
        a {
          text-decoration: none;
          color: #dadada;
        }
      }
      .name {
        font-size: 11px;
        text-align: center;
        margin-left: -2px;
        font-weight: normal;
        padding-top: 2px;
      }
      // border-right: 1px solid $bright-text-color;
    }
  }
  .show-with-details {
   display: none;
  }
  .details {
    clear: both;
    color: $dark-text-color;
    padding: 10px;
    font-size: 12px;
    display: none;
    border-top: 1px solid $bright-text-color;
  }
  .details-toggle {
    float: right;
    font-size: 11px;
    padding: 19px 4px 0 4px;
    min-width: 12px;
    height: $drag-item-height;
    cursor:pointer;
  }
}


