.TYPE-metric.new-view { 
  ul.nav-tabs li { 
    padding: 28px; 
    background: #eee; 
    margin: 5px;
  }
  fieldset.card-editor.editor {
    clear: both;
  }
  .name-editor {
    .RIGHT-designer, .RIGHT-Xtitle {
      &.card-editor {
        width: 45%;
        float: left;
        .card-title {
          font-size: 0.8em;
        }
      
        input {
          width: 100%;
        }
      }
    }
    .plus {
      float: left;
      font-size: 30px;
      font-weight: bold;
      text-align: center;
      position: relative;
      top: 16px;
      left: 0px;
      width: 10%;
    }
  }
}

.LTYPE_RTYPE-metric-company.content-view {
  span.metric-value {
    &.low {
      color: white;
      padding: 5px;
      background: red;
    }
    &.middle {
      color: white;
      padding: 5px;
      background: darkorange;
    }
    &.high {
      color: white;
      padding: 5px;
      background: green;
    }
  }
}
