.drag-item, .yinyang-row{
  padding: 0;
  margin-bottom: -1px;
  border: 1px solid $bright-text-color;
  // background: rgb(251, 251, 251);
  color: rgb(204, 204, 204);
  
  .metric-item{
    &:hover{
      .header, .no-data {
        border-color: $metric-color;
      }
    }
  }
  .company-item{
    &:hover{
      .header{
        border-color: $company-color!important;
      }
    }
  }
  &:hover {
    .handle{
      color: $dark-text-color2 !important;
      background-color: #F5F5F5 !important;
      border-right: 1px solid $bright-text-color !important;
    }
  }
  
  .contribution-item {
    overflow: hidden;
    cursor: move;
    &.metric-item .header .name {
      color:$dark-text-color;
    }
    &.company-item .header .name {
      color:$dark-text-color;

    }
    &.topic-item .header .name {
      color:$dark-text-color;

    }
    >.data {
        width: auto!important;
    }
  }
  .value-item {
    color: $dark-text-color;
    width: 100%;
    background: #fff;
    overflow: hidden;
    
    .header {
      width: 60%;
      .name {
        width: 70%;
      }
    }
    
    .data {
      width: 38%;
      .data-item {
        float: left;
        width: 90%;
      }
      &:hover{
        background-color: #fafafa;
        cursor: pointer;
      }
    }
    
    .no-data {
      width: 100%;
      .name {
        width: none;
      }
    }
    
    .metric-thumbnail {
      border: none;
      font-size: inherit;
    }
  }

  
  .topic-item .header {
    border-left: 3px solid rgba($topic-color,0.14);
  }
  .company-item .header {
    border-left: 3px solid rgba($company-color,0.14)!important;
  }
  .value-item {
    .no-data {
      border-left: 3px solid rgba($metric-color, 0.14);
    }
    .header {
      border-left: 3px solid rgba($metric-color, 0.14);
    }
    .metric-info {
      .header .row .row-data 
        { 
          h4 {
            float: left;
          }
        img {
            float: left;
            padding-right: 12px;
          }  
      }
    }
  }

  .header, .no-data {
    height: $drag-item-height;
    float: left;
    .handle {
      float: left;
      cursor: move;
      color: #ddd;
        margin-top: 0px;
        margin-left: 0px;
        padding: 16px 2px;
        border-right: 1px solid #fff;
        margin-right: 2px;
    }
    .RIGHT-Xvote_count > div {
      line-height: 1em;
    }
    button.vote-button {
      color: $dark-text-color;
      width: 15px;
      height: 15px;
      padding: 0;
      border: none;
      box-shadow: none;
      --webkit-box-shadow: none;
      background: none;
    }
    .vote-count {
      color: $dark-text-color;
      width: 15px;
      height: 15px;
    }
    .logo {
      padding-top: 5px;
      float: left;
      img {
        max-width: 35px;
        max-height: 35px;
        margin: 0 10px;
      }

    }
    .name {
      padding-top: 11px;
      padding-left:5px;
      font-size: 14px;
      float: left;
      max-width: 250px;
      text-overflow: ellipsis;
      white-space: nowrap;
      overflow: hidden;
    }
  }
  
  
  .data {
    height: $drag-item-height;
    float: right;
    height: $drag-item-height;
    border-left: 1px solid $bright-text-color;
    
    .btn-highlight a,
    .btn-default a {
      text-decoration: none;
    }
    .btn-default{
      border-color: $bright-text-color;
    }
    .data-item {
      margin-top: 15px;
      padding-left: 15px;
    }
    
    .contribution {
      float: right;
      width: 58px;
      height: 54px;
      font-size: 14px;
      font-weight: bold;
      text-align: center;
      margin-top: -1px;
      margin-right: -1px;
      margin-left: -1px;
      &.company-count {
        width:175px;
      }
      &.metric-details {
        width: 108px;
        padding-top: 15px;
      }
      &.btn-default {
        .name {
          color: #ccc;
        }
        .content {
          color: #737373;
        }
      }
      &.btn.btn-highlight {
        background-color: #f29393;
        border-color: #f06464;
        a {
          color: #fff;
        }
        &:hover {
          background-color: #f06e6e;
        }
      }
      .glyphicon {
        font-size: 12px;
      }
      &.danger {
        /*border: 1px solid #E06666;*/
        background: #ea9999;
        color: #fff;
        a {
          color: #fff;
        }
        .name {
          color: #fff;
        }
      }
      &.warning {
        a {
          text-decoration: none;
          color: #dadada;
        }
      }
      .name {
        font-size: 11px;
        text-align: center;
        margin-left: -2px;
        font-weight: normal;
        padding-top: 2px;
      }
      // border-right: 1px solid $bright-text-color;
    }
  }
  .show-with-details {
   display: none;
  }
  .details {
    clear: both;
    color: $dark-text-color;
    padding: 0px;
    font-size: 12px;
    display: none;
    border-top: 1px solid $bright-text-color;
    position: absolute;
    background-color: #fff;
    border: 1px solid #EAEAEA;
    z-index: 1;
    top: 0px;
    left: 100%;
    width: 100%;
    overflow-y: auto;
    height: 100%;
    margin-left: 1px;
    padding-bottom:100px;
    @include box-shadow(0,0px,14px,rgba(0,0,0,.22));
    &.stick{
      position: fixed;
      z-index: 1;
      top: 65px;
      left: 50%;
      max-width: 610px;
      width:50%;
      height: 100%;
      margin-left: 0px;
      // box-shadow: 0px 0px 14px 0px rgba(0, 0, 0, 0.22);
       @include box-shadow(0,0px,14px,rgba(0,0,0,.22));
    }
    .row-data {
      margin-left: 58px!important;
    }
  }
  
  .details-toggle {
    float: right;
    font-size: 11px;
    padding: 19px 4px 0 4px;
    min-width: 12px;
    height: $drag-item-height;
    cursor:pointer;
  }
}

 .TYPE-analysis .details-x{
      position: fixed;
      z-index: 1;
