value.content:
$header-bgcolor: #f9f9f9;
$header-color: #bbb;
$mycontributions-bgcolor: #eee;
$metric-color: #674ea7;
$claim-color: #03998d;
$source-color: #ffc201;
$article-color: #666;
$campaign-color: #555;
$border-color: #bbb;
$icon-color: #eee;
$dark-header-color: #999;


.open-view.TYPE-user {
  > .card-frame > .card-body {
    padding: 0;
  }
  .col-profile {
    padding-left: 0px;
    padding-right: 0px;
    .TYPE_PLUS_RIGHT-user-image.content-view img {
      width: 100%;
    }
    > .open-view > .card-frame > .card-header {
      background-color: $header-bgcolor;
      color: $header-color;
      height: 60px;
      > .card-header-title > .card-title {
        font-size: 18px;
      }
    }
  }  
}

.TYPE_PLUS_RIGHT-user-showcase.open-view {
  .showcase {
    margin-top: 15px;
    color: $header-color;
    > i {
      color: $header-color;
    }
  }  
}


.TYPE_PLUS_RIGHT-user-Xfollow.open-view {
  .card-header-title {
    width: 100%;
    a {
      float: right;
      color: #03998d;
      font-size: 0.7em;
    }
  }
  .list-group-item {
    border: none;
  }
}

.TYPE_PLUS_RIGHT-user-activity.open-view {
  color: $header-color;
  .activity {
    font-size: 0.7em;
    white-space: nowrap;
    overflow-x: hidden;
    span {
      vertical-align: top;
      &.glyphicon {
        font-size: 0.7em;
        margin-top: 5px;
      }
    }
    div {
      display: inline-block;
    }
  }
}

.TYPE_PLUS_RIGHT-user-my_contribution.open-view > .card-frame {
  > .card-header {
    color: $dark-header-color;
    padding: 0;
    .card-header-title {
      > span {
        padding-left: 10px;
        padding-top: 10px;
      }
      > .pull-right {
        padding-right: 10px;
        padding-top: 10px;
      }
      width: 100%;
      text-align: center;
      .card-title {
        float: left;
      }
      .counts {
        height: 59px;
        background: white;
        display: inline-block;
        border-left: 1px solid $border-color;
        .item {
          .metrics {
            color: $metric-color;
          }
          .claims {
            color: $claim-color;
          }
          .sources {
            color: $source-color;
          }
          padding: 5px 20px 3px 20px;
          span {
            font-size: 20px;
            font-weight: bold;
          }
          p {
            font-size: 15px;
            color: $header-color;
          }
          float: left;
          height: 100%;
          border-right: 1px solid $border-color;
          background: white;
        }
      }
    }
  }
  > .card-body {
    background-color: $mycontributions-bgcolor;
  
    .TYPE_PLUS_RIGHT-user-metric > .card-frame > .card-header {
      border-left: 5px solid $metric-color;
    } 
    .TYPE_PLUS_RIGHT-user-claim > .card-frame > .card-header {
      border-left: 5px solid $claim-color;
    } 
    .TYPE_PLUS_RIGHT-user-source > .card-frame > .card-header {
      border-left: 5px solid $source-color;
    } 
   .TYPE_PLUS_RIGHT-user-campaign > .card-frame > .card-header {
      border-left: 5px solid $campaign-color;
    } 
    .open-view, .closed-view {
      > .card-frame {
        > .card-body p {
          text-align: center;
        }
        .nav.nav-pills {
          margin-left: -15px;
          margin-top: -15px;
        }
        > .card-header {
          background-color: #fff;
          font-size: 18px;
          i {
            color: $icon-color;
          }
          span.item-count {
            font-size: 18px;
            color: $icon-color;
            margin-right: 10px;
          }
          a.toggler {
            font-size: 12px;
            color: $dark-header-color;
          }
        }
      }
    }
    
  }
}

.TYPE_PLUS_RIGHT-user-claim, .TYPE_PLUS_RIGHT-user-source, .TYPE_PLUS_RIGHT-user-metric, .TYPE_PLUS_RIGHT-user-campaign, .TYPE_PLUS_RIGHT-user-analysi{
  &.open-view > .card-frame > .card-header > .card-header-title {
    width: 100%;
  }
  &.closed-view > .card-frame {
    padding-right: 0;
    > .card-header {
      width: 100%;
      background-color: #fff;
      > .card-header-title {
        width: 100%;
      }
    }
  }
}

.content-view.TYPE_PLUS_RIGHT-html-campaign_edited_by, .showcase {
  .content-view.TYPE-campaign {
    display: inline-block;
    width: 300px;
    background: $mycontributions-bgcolor;
    padding: 5px;
    text-align: center;
    border: 1px solid #ddd;
    h4 {
      color: $dark-header-color;
    }
    p {
      font-size: 14px;
      color: $header-color;
    }
  }
}

.closed-content div.dropzone {
  display: none !important;
}
.dropzone {
  display: none;
  position: fixed;
  top: 51px;
  background: $header-bgcolor;
  border: 1px dashed black;
  height: 100px;
  width: 800px;
  z-index: 100;
  text-align: center;
  padding: 10px;
}


.closed-content div.removezone {
  display: none !important;
}
.removezone {
  display: none;
  position: fixed;
  bottom: 0px;
  background: $header-bgcolor;
  border: 1px dashed black;
  width: 800px;
  height: 100px;
  z-index: 100;
  text-align: center;
  padding: 10px;
}

.open-view.TYPE_PLUS_RIGHT-user-metric .yinyang-row .metric-item .header {
  border: none;
}

.open-view.TYPE_PLUS_RIGHT-user-analysi, .showcase {
  .article-item {
    width: 350px;
    height: 147px;
  }
}

