$drag-item-height: 52px;
$dark-text-color: #999;
$bright-text-color: #e5e5e5;
$company-header-bg: #d3741c;
$topic-header-bg: #2b7ea0;
$metric-header-bg: #674ea7;


.yinyang {
  border-top: 1px solid $bright-text-color;
  h4 {
    text-align: center;
    color: #999;
    margin-bottom: 10px;
  }
  
  .wiki {
    padding-left: 0;
    padding-right: 0;
    .yin {
      position: absolute;
      top: 5px;
      right: 0;
      color: #f7f7f7;
      font-size: 50px;
      line-height: 50px;
    }
  }
  
  .rate {
    padding-left: 0;
    padding-right: 0;
    background: #f7f7f7;
  
    .yang {
      position: absolute;
      top: 5px;
      left: 0;
      color: #fff;
      font-size: 50px;
      line-height: 50px;
    }
  }
  
  .header-row {
    font-size: 10px;
    color: $bright-text-color;
    text-align: center;
    margin-top: 10px;
    .header-header {
      float: left;
      margin-left: 80px;
    }
    .data-header {
      width: 161px;
      float: right;
      margin-right: 15px;
    }
  }
  
  .empty-message {
    display: none;
    padding: 5px;
    border: 1px dashed #ddd;
    text-align: center;
    height: 32px;
    color: #ddd;
    background: #fff;
    font-weight: normal;
  }
  .unsaved-message {
    display: none;
    height: 25px;
  }
}

.list-drag-and-drop, .yinyang-list {
  clear: both;
  margin: 10px;
  padding: 5px;
  background: none;
  border: none;
  box-shadow: none;
  --webkit-box-shadow: none;
}

@mixin yinyang-frame($header-color) {
  > .card-body.card-content {
    padding: 0;
  }
  > .card-header {
    background: $header-color;
    .panel-title {
      color: #fff;
       a {
        color: inherit !important;
        font-size: 16px;
      } 
    }
  }
}

#main > .card-frame.TYPE-company, .card-frame.TYPE-company { @include yinyang-frame($company-header-bg); }
#main > .card-frame.TYPE-topic,   .card-frame.TYPE-topic   { @include yinyang-frame($topic-header-bg); }
#main > .card-frame.TYPE-metric,  .card-frame.TYPE-metric  {
  @include yinyang-frame($metric-header-bg);
  > .card-body.card-content {
    .yinyang {
      .wiki, .rate{
        padding-top: 0px;
      }
    }
  }
}

.TYPE-metric {
  .rate {
    padding-top: 34px;
    .RIGHT-methodology {
      margin-top: 20px;
      padding-top: 15px;
    }
    .chosen-container {
      height: 12px;
      margin-left: -18px;
    }
  }
  .wiki {
    .metric-info .row {
      padding-bottom: 14px;
      .RIGHT-discussion .card-header {
      margin-top: 0px !important;
      }
    }
    .name {
      font-size: 1.5em;
      padding: 24px 24px;
      margin-left: 24px;
      position: relative;
      color: $metric-header-bg;
    }
    .metric-info .header {
      height: 100px;
      margin-top: 10px;
      margin-left: -1px;
      padding-left: 10px;
      border-bottom: 1px solid $bright-text-color;
      margin-bottom: 20px;
    }
  }
}


.TYPE-company, .TYPE-topic, .TYPE-metric {
  &.card-content {
    padding: 0;
  }
  .top {
    height: 100px;
    > .TYPE_PLUS_RIGHT-company-logo {
      height: 100px;
      padding: 10px;
      border-right: 1px solid $bright-text-color;
    }
    .TYPE_PLUS_RIGHT-topic-image {
      height: 100px;
      width: 100px;
      border-right: 1px solid $bright-text-color;
      img {
        width: 100%;
      } 
    }
    > .TYPE_PLUS_RIGHT-company-about, .TYPE_PLUS_RIGHT-topic-about {
      margin-left: 100px;
      width: inherit;
      padding: 10px 10px 5px 10px;
    }
    a.readmore-js-toggle {
      width: inherit;
      margin: 14px;
    }
  }
}


.drag-item, .yinyang-row{
  padding: 0;
  margin-bottom: 6px;
  border: 1px solid $bright-text-color;
  background: rgb(251, 251, 251);
  color: rgb(204, 204, 204);
  .contribution-item {
    overflow: hidden;
    .header {
      .name {
        font-size: 18px;
      }
    }
    &.metric-item .header .name {
      color: $metric-header-bg;
    }
    &.company-item .header .name {
      color: $company-header-bg;
    }
    &.topic-item .header .name {
      color: $topic-header-bg;
    }
  }
  .value-item {
    color: $dark-text-color;
    width: 100%;
    background: #fff;
    overflow: hidden;
    .header {
      width: 60%;
      cursor: move;
      .name {
        width: 70%;
      }
    }
    .data {
      width: 38%;
      .data-item {
        float: left;
        width: 90%;
      }
    }
  }
  
  .topic-item .header {
    border-left: 3px solid $topic-header-bg;
  }
  .company-item .header {
    border-left: 3px solid $company-header-bg;
  }
  .metric-item {
    .header {
      border-left: 3px solid $metric-header-bg;
    }
    .timeline {
      .year, .credit {
        margin-top: 2px;
      }
    }
    .metric-info {
      border-bottom: 1px solid $bright-text-color;
      .row {
        margin-left: 5px;
        margin-right: 5px;
        .row-icon {
          text-align: center;
          font-size: 17px;
          float: left;
          width: 30px;
          color: $bright-text-color;
        }
        .glyphicon-question-mark {
          font-weight: bold;
        }
        .row-data {
          margin-left: 40px;
        }
      }
    }
  }

  .header {
    height: $drag-item-height;
    float: left;
    .handle {
      float: left;
      margin-top: 15px;
      margin-left: 5px;
      cursor: move;
      color: #ddd;
    }
    .RIGHT-Xvote_count > div {
      line-height: 1em;
    }
    button.vote-button {
      color: $dark-text-color;
      width: 15px;
      height: 15px;
      padding: 0;
      border: none;
      box-shadow: none;
      --webkit-box-shadow: none;
      background: none;
    }
    .vote-count {
      color: $dark-text-color;
      width: 15px;
      height: 15px;
    }
    .logo {
      padding-top: 5px;
      float: left;
      img {
        max-width: 35px;
        max-height: 35px;
        margin: 0 10px;
      }

    }
    .name {
      padding-top: 11px;
      font-size: 14px;
      float: left;
      max-width: 250px;
      text-overflow: ellipsis;
      white-space: nowrap;
      overflow: hidden;
    }
  }
  
  
  .data {
    height: $drag-item-height;
    float: right;
    height: $drag-item-height;
    border-left: 1px solid $bright-text-color;
    
    .btn-highlight a,
    .btn-default a {
      text-decoration: none;
    }
    .btn-default{
      border-color: $bright-text-color;
    }
    .data-item {
      margin-top: 15px;
      padding-left: 15px;
    }
    
    .contribution {
      float: right;
      width: 58px;
      height: 54px;
      font-size: 14px;
      font-weight: bold;
      text-align: center;
      margin-top: -1px;
      margin-right: -1px;
      margin-left: -1px;
      &.company-count {
        width: 108px;
      }
      &.metric-details {
        width: 108px;
        padding-top: 15px;
      }
      &.btn-default {
        .name {
          color: #ccc;
        }
        .content {
          color: #737373;
        }
      }
      &.btn.btn-highlight {
        background-color: #f29393;
        border-color: #f06464;
        a {
          color: #fff;
        }
        &:hover {
          background-color: #f06e6e;
        }
      }
      .glyphicon {
        font-size: 12px;
      }
      &.danger {
        /*border: 1px solid #E06666;*/
        background: #ea9999;
        color: #fff;
        a {
          color: #fff;
        }
        .name {
          color: #fff;
        }
      }
      &.warning {
        a {
          text-decoration: none;
          color: #dadada;
        }
      }
      .name {
        font-size: 11px;
        text-align: center;
        margin-left: -2px;
        font-weight: normal;
        padding-top: 2px;
      }
      // border-right: 1px solid $bright-text-color;
    }
  }
  .show-with-details {
   display: none;
  }
  .details {
    clear: both;
    color: $dark-text-color;
    padding: 10px;
    font-size: 12px;
    display: none;
    border-top: 1px solid $bright-text-color;
  }
  .details-toggle {
    float: right;
    font-size: 11px;
    padding: 19px 4px 0 4px;
    min-width: 12px;
    height: $drag-item-height;
    cursor:pointer;
  }
}


// filter
.filter-header {
  cursor: pointer;
}
.votee-filter {
  background: #f2f2f2;
  color: $dark-text-color;
  padding: 5px;
  text-align: center;
  font-size: 12px;
  font-weight: bold;
}
.filter-details {
  display: none;
  margin-bottom: -4px;
  margin-left: -5px;
  margin-right: -4px;
  background: #FDFDFD;
}
.filter-toggle {
  float: right;
}
