value.content:
$header-bgcolor: #f9f9f9;
$header-color: #bbb;
$mycontributions-bgcolor: #f4f4f4;
$metric-color: #674ea7;
$claim-color: #03998d;
$source-color: #ffc201;
$article-color: #666;
$campaign-color: #555;
$article-color: #555;
$border-color: #e5e5e5;
$icon-color: #eee;
$dark-header-color: #999;


.open-view.TYPE-user {
   > .card-frame > .card-body {
    padding: 0;
  }
}

.content-view.RIGHT-contribution, .open-view.RIGHT-contribution {
  .contribution-content{
    background-color:$mycontributions-bgcolor;
  }
  > .card-frame > .card-body {
    padding: 0;
  }
  .col-profile {
    padding-left: 0px;
    padding-right: 0px;
    background-color:#fff;
    .TYPE_PLUS_RIGHT-user-image.content-view img {
      width: 100%;
    }
    .TYPE-image.RIGHT-logo {
      overflow: hidden;
      width: 100%;
      overflow: hidden;
      padding: 10% 0px;
      text-align: center;
    }
    > .open-view > .card-frame > .card-header, .content-header {
      background-color: $header-bgcolor;
      color: $header-color;
      height: 60px;
      > .card-header-title > .card-title {
        font-size: 19px;
        float: left!important;
        margin-top: 6px;
      }
    }
    .content-header{
        border-right: 1px solid rgba(0,0,0,0.05);
        padding: 10px 10px 4px 10px;
        border-color: #ddd;
    }
  
    .profile-about-content{
        .about-content{
          padding: 5%;
        }    
    }
  }  
}

.RIGHT-showcase.open-view {
  .showcase {
    margin-top: 15px;
    color: $header-color;
    > i {
      color: $header-color;
    }
  }  
}


.RIGHT-Xfollow.profile-view {
  .card-header-title {
    width: 100%;
    a {
      float: right;
      color: #03998d;
      font-size: 0.7em;
    }
  }
  .list-group-item {
    border: none;
  }
}

.RIGHT-activity.open-view {
  color: $header-color;
  .activity {
    font-size: 0.7em;
    white-space: nowrap;
    overflow-x: hidden;
    span {
      vertical-align: top;
      &.glyphicon {
        font-size: 0.7em;
        margin-top: 5px;
      }
    }
    div {
      display: inline-block;
    }
  }
}

.RIGHT-my_contribution.open-view > .card-frame {
  > .card-header {
    color: $dark-header-color;
    padding: 0;
    .card-header-title {
      > span {
        padding-left: 10px;
        padding-top: 10px;
      }
      > .pull-right {
        padding-right: 10px;
        padding-top: 10px;
      }
      width: 100%;
      text-align: center;
      .card-title {
        float: left;
      }
      .counts {
        height: 59px;
        background: white;
        display: inline-block;
        border-left: 1px solid $border-color;
        .item {
          .metrics {
            color: $metric-color;
          }
          .claims {
            color: $claim-color;
          }
          .sources {
            color: $source-color;
          }
          .articles {
            color: $article-color;
          }
          .campaigns {
            color: $campaign-color;
          }
          padding: 5px 20px 3px 20px;
          span {
            font-size: 20px;
            font-weight: bold;
          }
          p {
            font-size: 15px;
            color: $header-color;
          }
          float: left;
          height: 100%;
          border-right: 1px solid $border-color;
          background: white;
        }
      }
    }
  }
  > .card-body {
    /* every contribution section has an invisible top margin to adjust the inpage links
       the overflow hidden ensures that the contribution count links in the header are clickable
       and not covered by that top margin */
    overflow-y : hidden;
    
    background-color: $mycontributions-bgcolor;
  
    .RIGHT-contributed_metric > .card-frame > .card-header {
      border-left: 5px solid $metric-color;
    } 
    .RIGHT-contributed_claim > .card-frame > .card-header {
      border-left: 5px solid $claim-color;
    } 
    .RIGHT-contributed_source > .card-frame > .card-header {
      border-left: 5px solid $source-color;
    } 
   .RIGHT-contributed_campaign > .card-frame > .card-header {
      border-left: 5px solid $campaign-color;
    } 
    .RIGHT-contributed_analsyi > .card-frame > .card-header {
      border-left: 5px solid $article-color;
    } 
    
    .open-view.RIGHT-contributed_analysi> .card-frame .nav.nav-pills {
      width: 139px;
    }
    .open-view, .closed-view {
      margin-bottom: 10px;
      > .card-frame {
        > .card-body p {
          text-align: center;
        }
        .nav.nav-pills {
            margin-left: auto;
            margin-top: -15px;
            text-align: center;
            margin-right: auto;
            border: 1px solid $border-color;
            background-color: #F9F9F9;
            font-size:12px;
        }
        > .card-header {
          background-color: #fff;
          font-size: 18px;
          i {
            color: $icon-color;
          }
          span.item-count {
            font-size: 18px;
            color: $icon-color;
            margin-right: 10px;
          }
          a.toggler {
            font-size: 12px;
            color: $dark-header-color;
            z-index: 5;
            &.disabled {
              color: $icon-color;
            }
          }
        }
      }
    }
    
  }
}

.RIGHT-contributed_analysi {
  &.open-view, &.closed-view {
    .card-frame > .card-header > .card-header-title > img {
      width: 20px;
      opacity: 0.3;
    }
  }
}

.RIGHT-contributed_claim, .RIGHT-contributed_source, .RIGHT-contributed_metric, .RIGHT-contributed_campaign, .RIGHT-contributed_analysi{
  &.open-view > .card-frame > .card-header > .card-header-title {
    width: 100%;
  }
  &.closed-view > .card-frame {
    padding-right: 0;
    > .card-header {
      width: 100%;
      background-color: #fff;
      > .card-header-title {
        width: 100%;
      }
    }
  }
  &:before { 
    content: '';
    display: block; 
    position: relative; 
    width: 0; 
    height: 70px; 
    margin-top: -70px 
  }
}



.content-view.TYPE_PLUS_RIGHT-html-campaign_edited_by, .showcase {
  .content-view.TYPE-campaign {
    display: inline-block;
    width: 300px;
    background: $mycontributions-bgcolor;
    padding: 5px;
    text-align: center;
    border: 1px solid #ddd;
    h4 {
      color: $dark-header-color;
    }
    p {
      font-size: 14px;
      color: $header-color;
    }
  }
}

.closed-content div.dropzone {
  display: none !important;
}
.dropzone,.removezone {
  display: none;
  position: fixed;
  background: #E7E7E7;
  border-color: rgb(176, 176, 176);
  padding: 110px;
  z-index: 100;
  min-width: 914px;
  text-align: center;
  padding: 54px;
  left: 34%;
  color: #838383;
  border-width: 3px;
  border-style: dashed;
}

.dropzone{
  top: 67px;
}
.removezone {
  bottom: 0;

}

.closed-content div.removezone {
  display: none !important;
}


.open-view.RIGHT-contributed_metric .yinyang-row .metric-item .header {
  border: none;
}

.open-view.RIGHT-contributed_analysi, .showcase {
  .article-item {
    width: 350px;
    height: 147px;
  }
}

$drag-item-height: 52px;
$dark-text-color: #999;
$bright-text-color: #e5e5e5;
$dark-text-color2: #bebebe;
$company-header-bg: #d3741c;
$topic-header-bg: #2b7ea0;
$metric-header-bg: #674ea7;


.TYPE-analysi > .card-frame > .card-body.panel-body >.yinyang {
    border :0
}

.yinyang {
  border-top: 1px solid $bright-text-color;
  h4 {
    text-align: center;
    color: #999;
    margin-bottom: 10px;
  }
  
  .wiki {
    padding-left: 0;
    padding-right: 0;
    .yin {
      position: absolute;
      top: 5px;
      right: 0;
      color: #f7f7f7;
      font-size: 50px;
      line-height: 50px;
    }
  }
  
  .rate {
    padding-left: 0;
    padding-right: 0;
    background: #f7f7f7;
  
    .yang {
      position: absolute;
      top: 5px;
      left: 0;
      color: #fff;
      font-size: 50px;
      line-height: 50px;
    }
  }
  
  .header-row {
    font-size: 10px;
    color: $dark-text-color2;
    text-align: center;
    margin-top: 10px;
    .header-header {
      float: left;
      margin-left: 80px;
    }
    .data-header {
      width: 161px;
      float: right;
      margin-right: 15px;
    }
  }
  
  .empty-message {
    display: none;
    padding: 5px;
    border: 1px dashed #ddd;
    text-align: center;
    height: 32px;
    color: #ddd;
    background: #fff;
    font-weight: normal;
  }
  .unsaved-message {
    display: none;
    height: 25px;
  }
  .up_vote-container, .down_vote-container {
    background: #dfdfdf;
    h5 {
      text-align: center;
    }
  }
}

.list-drag-and-drop, .yinyang-list {
  clear: both;
  margin: 22px 10px 10px;
  padding: 5px;
  background: none;
  border: none;
  box-shadow: none;
  --webkit-box-shadow: none;
}

@mixin yinyang-frame($header-color) {
  > .card-body.card-content {
    padding: 0;
  }
  > .card-header {
    background: $header-color;
    .panel-title {
      color: #fff;
       a {
        color: inherit !important;
        font-size: 16px;
      } 
    }
  }
}

#main > .card-frame.TYPE-company, .card-frame.TYPE-company { @include yinyang-frame($company-header-bg); }
#main > .card-frame.TYPE-topic,   .card-frame.TYPE-topic   { @include yinyang-frame($topic-header-bg); }
#main > .card-frame.TYPE-metric,  .card-frame.TYPE-metric  {
  @include yinyang-frame($metric-header-bg);
  > .card-body.card-content {
    .yinyang {
      .wiki, .rate{
        padding-top: 0px;
      }
    }
  }
}

.TYPE-metric {
  .rate {
    padding-top: 34px;
    .RIGHT-methodology {
      margin-top: 20px;
      padding-top: 15px;
    }
    .chosen-container {
      height: 12px;
      margin-left: -18px;
    }
  }
  .wiki {
    .metric-info .row {
      padding-bottom: 14px;
      .RIGHT-discussion .card-header {
      margin-top: 0px !important;
      }
    }
    .name {
      font-size: 1.5em;
      padding: 24px 24px;
      margin-left: 24px;
      position: relative;
      color: $metric-header-bg;
    }
    .metric-info .header {
      height: 100px;
      margin-top: 10px;
      margin-left: -1px;
      padding-left: 10px;
      border-bottom: 1px solid $bright-text-color;
      margin-bottom: 20px;
    }
  }
}
.TYPE-company {
  .top{
    height:238px!important;
  }
}

.TYPE-company, .TYPE-topic, .TYPE-metric {
  &.card-content {
    padding: 0;
  }
  .top {
    height: 100px;
    > .TYPE_PLUS_RIGHT-company-logo {
      height: 100px;
      padding: 10px;
      border-right: 1px solid $bright-text-color;
    }
    .TYPE_PLUS_RIGHT-topic-image {
      height: 100px;
      width: 100px;
      border-right: 1px solid $bright-text-color;
      img {
        width: 100%;
      } 
    }
    > .TYPE_PLUS_RIGHT-company-about, .TYPE_PLUS_RIGHT-topic-about {
      margin-left: 100px;
      width: inherit;
      padding: 10px 10px 5px 10px;
      font-size:14px;
    }
    a.readmore-js-toggle {
      width: inherit;
      margin: 14px;
    }
  }
}

.TYPE-company .top>.TYPE_PLUS_RIGHT-company-about{
  margin-left: 17%;
}

.drag-item, .yinyang-row{
  padding: 0;
  margin-bottom: 6px;
  border: 1px solid $bright-text-color;
  background: rgb(251, 251, 251);
  color: rgb(204, 204, 204);
  
  &:hover {
    .handle{
      color: $dark-text-color2 !important;
      background-color: #F5F5F5 !important;
      border-right: 1px solid $bright-text-color !important;
    }
  }
  
  .contribution-item {
    overflow: hidden;
    .header {
      .name {
        font-size: 18px;
      }
    }
    &.metric-item .header .name {
      color: $metric-header-bg;
    }
    &.company-item .header .name {
      color: $company-header-bg;
    }
    &.topic-item .header .name {
      color: $topic-header-bg;
    }
  }
  .value-item {
    color: $dark-text-color;
    width: 100%;
    background: #fff;
    overflow: hidden;
    
    .header {
      width: 60%;
      cursor: move;
      .name {
        width: 70%;
      }
    }
    
    .data {
      width: 38%;
      .data-item {
        float: left;
        width: 90%;
      }
      &:hover{
        background-color: #fafafa;
        cursor: pointer;
      }
    }
    
   
  }
  
  .topic-item .header {
    border-left: 3px solid $topic-header-bg;
  }
  .company-item .header {
    border-left: 3px solid $company-header-bg;
  }
  .metric-item {
    .header {
      border-left: 3px solid $metric-header-bg;
    }
    .timeline {
      .year, .credit {
        margin-top: 2px;
      }
    }
    .metric-info {
      border-bottom: 1px solid $bright-text-color;
      .row {
        margin-left: 5px;
        margin-right: 5px;
        .row-icon {
          text-align: center;
          font-size: 17px;
          float: left;
          width: 30px;
          color: $bright-text-color;
        }
        .glyphicon-question-mark {
          font-weight: bold;
        }
        .row-data {
          margin-left: 40px;
        }
      }
    }
  }

  .header {
    height: $drag-item-height;
    float: left;
    .handle {
      float: left;
      cursor: move;
      color: #ddd;
        margin-top: 0px;
        margin-left: 0px;
        padding: 16px 2px;
        border-right: 1px solid #fff;
        margin-right: 2px;
    }
    .RIGHT-Xvote_count > div {
      line-height: 1em;
    }
    button.vote-button {
      color: $dark-text-color;
      width: 15px;
      height: 15px;
      padding: 0;
      border: none;
      box-shadow: none;
      --webkit-box-shadow: none;
      background: none;
    }
    .vote-count {
      color: $dark-text-color;
      width: 15px;
      height: 15px;
    }
    .logo {
      padding-top: 5px;
      float: left;
      img {
        max-width: 35px;
        max-height: 35px;
        margin: 0 10px;
      }

    }
    .name {
      padding-top: 11px;
      font-size: 14px;
      float: left;
      max-width: 250px;
      text-overflow: ellipsis;
      white-space: nowrap;
      overflow: hidden;
    }
  }
  
  
  .data {
    height: $drag-item-height;
    float: right;
    height: $drag-item-height;
    border-left: 1px solid $bright-text-color;
    
    .btn-highlight a,
    .btn-default a {
      text-decoration: none;
    }
    .btn-default{
      border-color: $bright-text-color;
    }
    .data-item {
      margin-top: 15px;
      padding-left: 15px;
    }
    
    .contribution {
      float: right;
      width: 58px;
      height: 54px;
      font-size: 14px;
      font-weight: bold;
      text-align: center;
      margin-top: -1px;
      margin-right: -1px;
      margin-left: -1px;
      &.company-count {
        width: 108px;
      }
      &.metric-details {
        width: 108px;
        padding-top: 15px;
      }
      &.btn-default {
        .name {
          color: #ccc;
        }
        .content {
          color: #737373;
        }
      }
      &.btn.btn-highlight {
        background-color: #f29393;
        border-color: #f06464;
        a {
          color: #fff;
        }
        &:hover {
          background-color: #f06e6e;
        }
      }
      .glyphicon {
        font-size: 12px;
      }
      &.danger {
        /*border: 1px solid #E06666;*/
        background: #ea9999;
        color: #fff;
        a {
          color: #fff;
        }
        .name {
          color: #fff;
        }
      }
      &.warning {
        a {
          text-decoration: none;
          color: #dadada;
        }
      }
      .name {
        font-size: 11px;
        text-align: center;
        margin-left: -2px;
        font-weight: normal;
        padding-top: 2px;
      }
      // border-right: 1px solid $bright-text-color;
    }
  }
  .show-with-details {
   display: none;
  }
  .details {
    clear: both;
    color: $dark-text-color;
    padding: 10px;
    font-size: 12px;
    display: none;
    border-top: 1px solid $bright-text-color;
  }
  .details-toggle {
    float: right;
    font-size: 11px;
    padding: 19px 4px 0 4px;
    min-width: 12px;
    height: $drag-item-height;
    cursor:pointer;
  }
}


// filter
.filter-header {
  cursor: pointer;
}
.votee-filter {
  background: #f2f2f2;
  color: $dark-text-color;
  padding: 5px;
  text-align: center;
  font-size: 12px;
  font-weight: bold;
}
.filter-details {
  display: none;
  margin-bottom: -4px;
  margin-left: -5px;
  margin-right: -4px;
  background: #FDFDFD;
}
.filter-toggle {
  float: right;
}

.TYPE-topic .RIGHT-company_sidebar {
  margin: 22px 10px 10px;
}

