fieldset {
  > .button-area,
  > .editor,
  > legend,
  > legend > .instruction {
    float: none;
    text-align: left;
    display: inline-block;
    width: 99%;
    padding: 0 5%;
    position: relative;

    >p {
      display: none;
      position: absolute;
      left: 18px;
      opacity: 0.8;
      top: 50%;
      margin-top: -18px;
      margin-left: 8px;
      width: auto;
      color: #fff;
      background: #000;
      padding: 5px;
      text-align: center;
      border-radius: 3px;
      font-size: 0.8em;
      z-index: 1;
      &:after {
        content: '';
        position: absolute;
        top: 50%;
        right: 100%;
        margin-top: -8px;
        width: 0;
        height: 0;
        border-right: 8px solid #000;
        border-top: 8px solid transparent;
        border-bottom: 8px solid transparent;
      }
    }

  }
  > legend h2 {
    display: inline;
  }
  > legend > .instruction {
    padding: 4px 0;
    width: 70%;
  }

}
