value.content:
//***ALL COLOR VARS & MIXINS***//
$heading_color: #555;
$body_background: #ebebeb;
$primary_color: #555;
$anchor_color: #03998d;
$body-txt-color: #4f4f4f;
$wanted-card-color: #b6060a;
$closed-content-a-color: #729;
$infographic-heading: #949494;
$div-border-color: #f1f1f1;

//button colors
$button-color: #03998d;
$button-color-hover: #009185;
$button-border: #00756c;
$cancel-button-color: #bebebe;
$cancel-button-border: #9b9b9b;
$cancel-button-hover: #b4b4b4;

//claim colors
$claim-tag-companybg: #d3741c;
$claim-tag-companybg-hover: #bb5b00;
$claim-tag-topicbg: #2b7ea0;
$claim-tag-topicbg-hover: #01648d;
$claim-tag-notexist: #f0f0f0;
$claim-tag-notexist-hover: #ececec;

$card-header-color: #fff;

//card colors
@mixin card-txt-color {
  color: #646464;
  a {
    color: #646464;
  }

}
//border radius mixin
@mixin border-radius($radius) {
  -webkit-border-radius: $radius;
  border-radius: $radius;
  background-clip: padding-box;
  /* stops bg color from leaking outside the border: */

}
//***COLOR VARS - END***//

//***Shadow***//

@mixin box-shadow($x: 2px, $y: 2px, $blur: 5px, $color: rgba(0, 0, 0, .4), $inset: "") {
  @if ($inset != "") {
    -webkit-box-shadow: $inset $x $y $blur $color;
    -moz-box-shadow: $inset $x $y $blur $color;
    box-shadow: $inset $x $y $blur $color;
  }
  @else {
    -webkit-box-shadow: $x $y $blur $color;
    -moz-box-shadow: $x $y $blur $color;
    box-shadow: $x $y $blur $color;
  }

}
// ***Shadow- END***//

body {
  // override bootstrap font 
  color: $body-txt-color;
  font-family: 'Droid Sans',verdana, arial, helvetica, sans-serif!important;
  // line-height: 1.5em;
  // font-size: 0.9em;

}
body#wagn {
  min-height: 100%;
  min-width: 480px;
  background: $body_background;
  padding: 0;
  margin: 0;

}
body#tinymce {
  padding: 4px;

}
sup {
  line-height: 0;

}
a {
  color: $anchor_color;

}
a.wanted-card {
  color: $wanted-card-color;

}
/* many sites may want this external link behavior; it's here because it's a nuisance to override when not desired */
a.external-link,
.closed-content a.external-link {
  color: $closed-content-a-color;
  background: url("/assets/external_link.png") center right no-repeat;
  padding-right: 13px !important;

}
img {
  border: 0;
}
h1,
h2 {
  color: $heading_color;

}
h1 {
  font-size: 1.2em;
  font-weight: normal;
  line-height: 1.8em;
  padding: 5px 0;

}
.more-link {
  float: right;

}
.card-header {
  //font-family: 'Droid Sans';
}
.card-content .titled-view > .card-header {
  line-height: 1.4em;
  margin-top: 1em;

}
.card-frame {
  border-radius: 4px;
  border: 0 solid #e5e5e5;
  > .card-header {
    background: $card-header-color;
    font-size: 1.1em;
    // font-weight: bold;
    @include card-txt-color;
    border:0;
  }
  > .instruction {
    background: #bbb;
  }

}
.RIGHT-logo.card-content img {
  max-height: 100%;

}
#Home {
  overflow: inherit;
}
#main {
  font-size: 1.1em;
  clear: both;
  padding: 70px 1%;
  max-width: 1260px;
  margin: 0 auto;
  .card-frame > .card-body > .titled-view {
    margin: 15px 0;
    > .card-body {
      margin: 5px 0 5px 3%;
    }
    > .card-content {
      padding: 10px 20px 15px;
    }

  }
  .card-frame {
    border-radius: 0;
    padding: 0 2%!important;
    > .card-body > h1 {
      font-size: 2em;
      font-weight: normal;
      line-height: 2em;
      padding: 5px 0 0;
    }

  }
  > .card-slot > h1 {
    font-size: 1.5em;
    padding: 0 5px;
    .card-title {
      text-align: center;
      display: inline-block;
      width: 90%;
      /*text-indent: 10%;*/
      margin-left: 5%;
    }
  }

}
.card-slot > .card-header .card-menu-link {
  display: none;
}
.card-slot.closed-view,
.card-slot:hover {
  > .card-header .card-menu-link {
    display: inline-block;
    margin-left: 10px;
  }

}
.revision-navigation,
.current {
  background: #fff;

}
body.home-layout #main {
  > .card-content {
    .SELF-wikirate_infographic {
      text-align: center;
      border-radius: 4px;
      -moz-border-radius: 4px;
      float: right;
      border: 0 solid #e5e5e5;
      background: #fff;
      width: 41%;
      padding-top: 20px;
      margin-bottom: 12px;
      > .SELF-infographic {
        float: left;
        display: inline-block;
        width: 68%;
      }
      span {
        font-size: 1.8em;
        color: $infographic-heading;
      }
      p {
        line-height: 2em;
        color: #d5d5d5;
        font-size: 1.3em;
      }
      > .wikirate-video-container {
        position: relative;
        padding-bottom: 17%;
        padding-top: 0;
        height: 0;
        overflow: hidden;
        left: 14px;
        top: -14px;
        width: 30.2%;
        margin: 0;
        iframe {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
        }
      }

    }
    .SELF-industry_of_the_month.content-view {
      width: 100%;
      text-align: center;
      padding: 0 0 24px;
      float: left;
    }
    .home-left {
      width: 58%;
      float: left;
      .SELF-featured_topic.card-frame {
        margin-top: 40px;
      }

    }
    .home-right {
      width: 41%;
      float: right;
    }
    > #wikirate_tagline {
      text-align: center;
      overflow: visible;
      font-size: 2em;
      padding: 1.5em;
    }
  }

}
.STRUCTURE-company_feature,
.STRUCTURE-topic_feature {
  clear: both;
  padding: 20px 0;
  min-height: 200px;

}
.STRUCTURE-company_feature,
.STRUCTURE-topic_feature {
  > a {
    font-size: 1.3em;

  }
  h1 {
    font-size: 0.9em;
    font-style: italic;
    color: #aaa;

  }
  .TYPE-image.content-view {
    width: 25%;
    max-width: 100px;
    padding: 0 3% 10px 0;
    min-height: 200px;

    img {
      border-radius: 10px;
      -moz-border-radius: 10px;
    }
  }

}
/*~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/

body > .SELF-wikirate_footer {
  clear: both;
  display: block;
  padding: 45px 30px 10px;
  text-align: center;
  font-size: 0.9em;
  background: #fff;
  border: 1px solid #e5e5e5;
  a.external-link {
    padding-right: 0 !important;
    background: inherit !important;
  }
  [class*="SELF-logo_"] {
    display: inline-block;
  }

}
#credit,
#credit a {
  padding: 0 !important;
  background: none;
  border: 0;
  margin-bottom: 15px;
  position: relative;

}
#credit {
  z-index: -0;
  left: 8px;
  display: block;
  a {
    right: 16px;
    position: relative;
    z-index: -1;
  }

}
.RIGHT-claim_count.content-view {
  display: inline;
  float: right;
  color: #999;

}
.search-result-item > .TYPE-page.content-view {
  .RIGHT-website .pointer-list {
    #font-size: 1.1em;
    #font-weight: bold;
    padding-bottom: 0;
  }
  .RIGHT-title {
    font-size: 0.9em;
    margin: 0 0 7px 3%;
  }

}
.RIGHT-image_source.content-view {
  font-size: 0.8em;
  font-style: italic;

}
.TYPE-analysi,
.TYPE-company,
.TYPE-topic {
  &.STRUCTURE-analysi_comparison a.internal-link,
  >.source-side > a {
    // display: block;
    // padding: 10px;
    // margin: 10px 5%;
    // float: right;
    // border: 2px solid #555;
    // border-radius: 10px;
    // background: #eee;
    // font-size: 1.2em;
  }

}
.TYPE-analysi {
  &.STRUCTURE-analysi_comparison a.internal-link,
  >.source-side > a {
    float: none;
    text-align: center;
  }

}
.TYPE-topic .STRUCTURE-topic_analysi_blurb,
.TYPE-company .STRUCTURE-company_analysi_blurb {
  padding: 10px 0;
  border-bottom: 1px solid #eee;
  > a {
    float: right;
  }

}
.STRUCTURE-analysi_blurb {
  > .RIGHT-article,
  > .RIGHT-claim {
    padding-left: 5%;
  }
  .RIGHT-claim {
    > .card-header {
      font-size: 0.9em;
      font-style: italic;
      color: #aaa;
      padding: 0;
    }
    .search-result-item {
      padding-bottom: 10px;
    }
  }

}
.STRUCTURE-icon_and_link.content-view > a > img,
.STRUCTURE-company_analysi_link.content-view > a > img,
.STRUCTURE-topic_analysi_link.content-view > a > img,
.STRUCTURE-company_link.content-view > a > img,
.STRUCTURE-topic_link.content-view > a > img {
  border-radius: 3px;
  -moz-border-radius: 3px;
  margin-right: 5px;

}
.STRUCTURE-topic_analysi_link_and_compare .STRUCTURE-compare_link,
.RIGHT-link_and_compare .RIGHT-compare_link {
  float: right;

}
.STRUCTURE-claim_summary {
  > h1 {
    font-size: 1.1em;
    padding-bottom: 0;
  }
  > .card-body {
    padding: 5px 0 18px 5%;
    margin: 0 0 5px;
    border-bottom: 1px solid #eee;
    font-size: 0.8em;
    .fa {
      padding-right: 5px;
    }
    > div {
      margin-left: 5%;
      padding: 2px 0;
      text-indent: -3%;
      div {
        display: inline;
      }

    }
    .missing-view {
      display: none;
    }
    .RIGHT-source .source-title {
      font-style: italic;
    }
    .pointer-item,
    .search-result-item {
      text-indent: 10px;
      margin: 0;
      padding: 2px;
      :after {
        content: ' ';
      }
    }
  }

}
#claim-sourcelink .pointer-item a {
  color: $body-txt-color;
  &:hover {
    color: $anchor_color;
  }

}
.analysis-link > a,
.RIGHT-company.titled-view > .card-content .item-link > a,
.RIGHT-topic.titled-view > .card-content .item-link > a {
  // margin: 3px 10px;

}
.analysis-link > a,
.RIGHT-company.card-content .item-link > a,
.RIGHT-topic.card-content .item-link > a,
#company-and-topic .company-name,
#company-and-topic .topic-name {
  display: inline-block;
  text-indent: 0;
  border-radius: 3px;
  font-size: 0.9em;
  color: #fff;
  text-decoration: none;

}
.RIGHT-company.card-content .item-link > a,
.RIGHT-topic.card-content .item-link > a,
.analysis-link > a > span {
  display: inline-block;
  padding: 3px 8px !important;
  margin-bottom: 6px;

}
#company-and-topic {
  .company-name,
  .topic-name {
    padding: 1px 3px !important;
  }
  .company-name.no-content,
  .topic-name.no-content {
    background: none!important;
  }

}
.RIGHT-company.card-content .item-link > a,
.analysis-link > a > .company,
#company-and-topic .company-name {
  background: $claim-tag-companybg;
  border-top-left-radius: 3px;
  border-bottom-left-radius: 3px;
}
.RIGHT-company.card-content .item-link > a:hover,
.analysis-link > a:hover > .company,
#company-and-topic .company-name:hover {
  background: $claim-tag-companybg-hover;

}
.RIGHT-topic.card-content .item-link > a,
.analysis-link > a > .topic,
#company-and-topic .topic-name {
  background: $claim-tag-topicbg;
  border-top-right-radius: 3px;
  border-bottom-right-radius: 3px;
}
.RIGHT-topic.card-content .item-link > a:hover,
.analysis-link > a:hover > .topic,
#company-and-topic .topic-name:hover {
  background: $claim-tag-topicbg-hover;

}
.card-content .item-link > a.wanted-card {
  background: $claim-tag-notexist;
  border-top-left-radius: 3px;
  border-bottom-left-radius: 3px;
  color: $wanted-card-color;
  &:hover {
    background: $claim-tag-notexist-hover;
  }

}
#main > .TYPE-claim {
  > .card-header {
    background-color: #fff;
  }
  .source-side {
    border-right: 1px solid $div-border-color;
  }

}
.RIGHT-tag,
.RIGHT-year {
  &.labeled-view {
    font-size: 0.9em;
    color: #aaa;
    .card-content a {
      color: #aaa;
      font-style: italic;
    }

  }
  &.missing-view,
  &.labeled-view {
    label,
    .card-content a,
    a.missing-labeled {
      text-align: left;
      min-width: 30px;
    }
  }

}
.TYPE-claim.card-content .source-side > .RIGHT-tag {
  margin-top: 20px;

}
#main > .SELF-topic,
#main > .SELF-company {
  > .card-content {
    .pointer-item,
    .search-result-item {
      display: inline-block;
      min-width: 130px;
      width: 17%;
      padding: 10px 1%;
      float: left;
    }
  }

}
.STRUCTURE-labeled_source_frame {
  label {
    display: inline-block;
    padding-right: 10px;
    float: left;
  }
  .RIGHT-title {
    display: inline-block;
    font-size: 1.2em;
    line-height: 1.2em;
  }
  em {
    float: right;
  }
  .pointer-list,
  .pointer-item {
    display: inline;
    padding-left: 0;
  }

}
.comment-box {
  background: inherit;
}
.cited-claim-number {
  float: left;
  padding: 1px 8px;
  border-radius: 10px;
  -border-radius: 10px;
  font-size: 0.8em;
  margin: 15px 10px 0 0;
  background: #03998d;
  color: white;
  /* hack for hash jump */
  margin-top: 70px;
}
.claim-clipboard {
  float: left;
  margin: 15px 10px 0 0;
  padding: 4px;
  color: #03998d;
  background: white;

}
.TYPE-analysi.card-content {
  .search-no-results {
    display: none;
  }
  > .search-result-list {
    padding: 0;
  }
  .side-by-side {
    text-align: center;
    padding: 0;
    margin-bottom: 20px;
    .company-link {
      float: left;
    }
    .topic-link {
      float: right;
    }
    .TYPE-image {
      display: inline-block;
      padding: 10px;
      border-radius: 2px;
      -moz-border-radius: 2px;
      border: 0 solid #ededed;
    }
    img {
      max-height: 130px;
    }
    .analysis-name {
      font-size: 1.4em;
      line-height: 1.6em;
    }
    .analysis-joint {
      font-size: 0.8em;
    }

  }
  > .source-side {
    > h1 {
      margin-top: 40px;
      padding: 0;
    }
    > .TYPE-search > .card-content {
      padding-left: 3%;
    }

  }
  .RIGHT-right_sidebar.card-content {
    > h1 {
      padding: 0;
    }
    > h2 {
      font-weight: normal;
      font-size: 1em;
      padding: 0 0 15px 3%;
    }
    > .card-content {
      padding-bottom: 25px;
    }
  }

}
.RIGHT-right_sidebar .source-title {
  display: block;
  padding: 3px 0 3px 5%;
}
.RIGHT-wikirate_source .content-editor select {
  height: 200px;

}
.STRUCTURE-website_summary.titled-view {
  padding-bottom: 20px;
  border-bottom: 1px solid $div-border-color;
  margin-bottom: 20px;
  > .card-content {
    padding-left: 5%;
  }
  .RIGHT-page {
    > h1 {
      font-size: 0.8em;
    }
    padding-left: 5%;
    .search-result-item {
      line-height: 2em;
    }
  }

}
.SELF-company_comparison.card-content {
  table {
    width: 100%;
  }
  th {
    padding: 5px 1%;
  }
  td {
    padding: 5px 2%;
    width: 50%;
  }
  > div {
    width: 46%;
    padding: 5px 2%;
  }
  .left-side {
    float: left;
  }
  .right-side {
    float: right;
  }

}
.closed-content label {
  display: inline;

}
.TYPE_PLUS_RIGHT-company-label img {
  margin: 0 2px 5px 0;
  border-radius: 4px;
  -moz-border-radius: 4px;
}
.ui-widget-content a {
  color: #279;

}
//'make a claim' button in source page
.TYPE-page .source-side .internal-link {
  padding: 0;

}
//To hide 'make a claim' in new and edit source page. It is not required there.
.TYPE-page.new-view .source-side .internal-link,
.TYPE-page.edit-view .source-side .internal-link {
  display: none;

}
fieldset {
  > .button-area,
  > .editor,
  > legend,
  > legend > .instruction {
    float: none;
    text-align: left;
    display: inline-block;
    width: 90%;
    padding: 0 5%;
    position: relative;

    >p {
      display: none;
      position: absolute;
      left: 18px;
      opacity: 0.8;
      top: 50%;
      margin-top: -18px;
      margin-left: 8px;
      width: auto;
      color: #fff;
      background: #000;
      padding: 5px;
      text-align: center;
      border-radius: 3px;
      font-size: 0.8em;
      z-index: 1;
      &:after {
        content: '';
        position: absolute;
        top: 50%;
        right: 100%;
        margin-top: -8px;
        width: 0;
        height: 0;
        border-right: 8px solid #000;
        border-top: 8px solid transparent;
        border-bottom: 8px solid transparent;
      }
    }

  }
  > legend h2 {
    display: inline;
  }
  > legend > .instruction {
    padding: 4px 0;
    width: 70%;
  }

}
// To divide source and meta sides of content in Company, Claim, Topic , Analysis and Source pages
.source-side,
.meta-side {
  float: left;
  padding: 0;
  margin-left: 2.5%;

}
.TYPE-analysi .source-side {
  width: 63% !important;
}
.TYPE-analysi .meta-side {
  width: 30% !important;

}
.TYPE-analysi .source-side,
.TYPE-company .source-side,
.TYPE-topic .source-side {
  width: 70%;
  border-right: 1px solid $div-border-color;
  padding-right: 20px;
  .ui-tabs {
    border: 0;
  }

}
.TYPE-analysi .meta-side,
.TYPE-company .meta-side,
.TYPE-topic .meta-side {
  width: 22%;
}
.SELF-source,
.TYPE-claim {
  .creator-credit {
    text-align: center;
    .search-result-list {
      display: inline-block;
    }

  }
  .preview-visit-link {
    float: right;
  }
  .remove-source {
    display: none;
    float: right;
    cursor: pointer;
  }

}
.TYPE-claim .source-side,
.TYPE-page .source-side {
  width: 45%;
  margin-left: 2.5%;
  padding-right: 2.5%;
}
.TYPE-claim .meta-side,
.TYPE-page .meta-side {
  width: 44.5%;
  position: static;
  > .card-slot {
    margin: 20px 0;
  }
  > .RIGHT-source > .card-header {
    margin-top: 0;
  }

}
.sourcebox > input {
  width: 100%;

}
/* .TYPE-claim .meta-side .RIGHT-source, */

.sourcebox {
  background: #fafafa;
  display: inline-block;
  height: 66px;
  width: 96%;
  padding: 12px;
  > button {
    float: right;
    margin: 11px 0;
  }

}
.TYPE-claim,
.TYPE-page {
  .sourceErrorMsg {
    color: red;
    font-size: 0.7em;
    float: left;
    margin-top: 8px;
  }
  .addSourceLoader {
    font-size: 0.7em;
    float: left;
    color: $anchor_color;
  }

}
.preview-view.TYPE-page {
  overflow: hidden;
}
.STRUCTURE-source_preview,
.STRUCTURE-source_summary {
  background: #fafafa;
  margin: 12px 0;
  padding: 4px 20px;
  .preview-page-link {
    font-weight: bold;
    padding: 3px 0;
    display: block;
  }
  .RIGHT-description {
    font-size: 0.9em;
    margin-top: 3px;
  }
  img {
    width: 100px;
    max-height: 100px;
    max-width: 20%;
    width: auto;
    height: auto;
    background-color: #eee;
    border: 0 !important;
    float: left;
    margin: 5px 3% 0 0;
  }
  .preview-visit-link {
    clear: both;
  }

}
//////

.RIGHT-compare_link.card-content,
.STRUCTURE-compare_link {
  &.card-content.content-view {
    display: inline;
    a {
      background: $button-color;
      font-size: 10px;
      padding: 3px 5px;
      margin-left: 10px;
      color: white;
      border-radius: 3px;
      -moz-border-radius: 3px;
    }
  }

}
@media all and (max-width: 1280px) and (min-width: 1024px) {
  body>.SELF-wikirate_footer > .consortium-logo-container {
    width: 102%;
  }

}
.consortium-logo-container .TYPE-image.card-content img {
  max-width: 100%;
  max-height: 55px;

}
#social-icons a {
  display: inline-block;

}
.claim-counting {
  float: right;
  margin-right: 6%;

}
.RIGHT-company > div> select {
  width: 40%;
}
.RIGHT-topic > div> select {
  width: 40%;
}
.claim_p_disclosure_link {
  margin-left: 7.5%;
  margin-bottom: 26px;
  float: left;
  display: none;
  color: $anchor_color;
  cursor: pointer;

}
.RIGHT-company,
fieldset.company {
  >div>div>.chosen-choices li.search-choice {
