  //max-height:200px
}
.TYPE-user {
  > .card-header {
    background-color: #fff;
  }
  >.card-body {
    min-height: 500px;
  }
  .user-profile-left {
    width: 220px;
    display: inline-block;
    float: left;
    border-right: 1px solid $div-border-color;
    .TYPE-image {
      float: left;
      margin: 0 0 22px 24px;
      padding: 0;
      height: auto;
      width: 65%;
      border: 8px solid #eee;
      overflow: hidden;
      img {
        max-width: 100%;
        margin-bottom: -5px;
      }
    }

  }
  .join-date {
    text-align: center;
  }
  .user-profile-right {
    width: 75%;
    padding-left: 10px;
    float: left;
  }
  .user-profile-right-top {
    min-height: 150px;
    >span {
      font-size: 1.5em;
    }
    .location {
      >span {
        float: left;
        line-height: 1.5em;
      }
      font-size: 0.8em;
    }

  }
  .user-profile-right-bottom-contribution {
    width: 50%;
    float: left;
    >h1 {
      margin-top: 1em;
      border-bottom: 1px solid $div-border-color;
      padding: 1px 0;
    }
    .ui-widget-content {
      border-style: none;
    }

  }
  .TYPE_PLUS_RIGHT-user-discussion.titled-view {
    width: 45%;
    float: right;
    h1 {
      border-bottom: 1px solid $div-border-color;
    }

  }
  .related-card-count {
    

  }
  .TYPE_PLUS_RIGHT-user-following {
    >h1 {
      border-bottom: 1px solid $div-border-color;
    }
    .user-following-title {
      font-size: 1.5em;
      border-bottom: 1px solid $div-border-color;
      font-weight: bold;
      display: block;
      padding-top: 20px;
    }
    .search-result-item {
      margin: 6px;
      background: #fafafa;
      padding: 10px;
    }
    float: left;
    width: 100%;
  }
  .change-view {
    width: 100%;
    display: inline-block;
    .item-name {
      float: left;
      width: 75%;
    }
    .last-edit {
      text-align: right;
      float: right;
      width: 25%;
    }

  }
  #tabs {
    min-height: 300px;
  }
  .empty-profile-tab {
    padding-top: 25%;
    text-align: center;
  }

}
