.article-item, .article-header{
  display: inline-block;
  width: 96%;
  text-align: center;
  border: 1px solid #ddd;
  overflow: hidden;
  height: 370px;
  @include box-shadow(0,1px,6px,rgba(0,0,0,.13));
  margin: 2px 3px;
  position:relative;

  .mouse-hover{
    @include opacity(0.3);
  }
  &:hover {border: 1px solid #B6B6B6;@include box-shadow(0,1px,6px,rgba(0,0,0,.23));}
  &:hover .mouse-hover{
    @include opacity(1);
  }
  &:hover .article-content{
    color: #777 !important; //default body color
  }
  /*----------- Article images and titles --------------*/
  .article-images {
    color: #fff;
    position:relative;
    .company-image{
      .TYPE-image{
        width:100%!important;
      }
    }
    .company-image,
    .topic-image {
      float: left;
      width:50%;
      .TYPE-image {
        height: 120px;
        width: 180px;
        overflow: hidden;
        img{
          @include vertical-align;
        }
      }

    }
    .venn-icon-pos{
      position: absolute;
      font-size: 55px;
      left: 42.5%;
      top: 28px;
      z-index: 1;
      .SELF-venn_icon {
        margin-top: -58px;
      }
      img{
        height:24px
      }
    }
    .company-n-topic-title {
      padding: 4px 5px;
      font-size: 0.8em;
      white-space: nowrap;
      overflow: hidden;
      text-overflow: ellipsis;
      width: 100%;
    }
    .company-title {
      background: $claim-tag-companybg;
      :hover {
        background: $claim-tag-companybg-hover;
      }

    }
    .topic-title {
      background: $claim-tag-topicbg;
      :hover {
        background: $claim-tag-topicbg-hover;
      }
    }

  }
  /*----------- Article content --------------*/
  .article-content {
    display: inline-block;
    padding: 0 14px;
    color: #ADADAD!important;
    .article-edits {
      padding: 12px 0;
      float: left;
      width: 100%;
      height: 50px;
    }
    .article-desc {
      text-align: left;
      font-size: 0.9em;
      float: left;
      height: auto;
      text-overflow: ellipsis;
      overflow: hidden;
      line-height: 18px;
      h1,h2{
          font-size: 0.9em;
          margin: 0;
          font-weight: normal;
          padding: 0;
          display: none;
      }
    }
    .article-claim-count {
      font-size: 0.8em;
      position: absolute;
      bottom: 8px;
      left: 25%;
      width: 50%;
    }
    .article-edit-count {
      float: left;
    }
    .edits-by {
      float: left;
      width: 94%;
      border: 0;
      text-align: left;
      .known-card {
        // float: left;
      }
    }
  }
}

.article-header{
  display: inline-block;
  width: 100%;
  height:auto;
  text-align: center;
  border: 0px solid #ddd;
  box-shadow:none;
  -webkit-box-shadow: none;
  -moz-box-shadow: none;
  &:hover{
    box-shadow:none;
    -webkit-box-shadow: none;
