.TYPE-user .RIGHT-Xfollow .tab-content {
  font-size: 13px;
  ul {
    padding-left: 0;
    li {
      padding-bottom: 2px;
    }
  }
  button {
    width: 25px;
    height: 25px;
    font-size: 10px;
    padding: 3px;
  }
}

/*----------- Navigation color variables ---------*/
$nav-srch-background: #fff;
$nav-srch-txt-color: #a8a8a8;
$navmenu-anchor-color: #fff;
$navmenu-hover-bgcolor: #03998d;
$navsbmenu-bgcolor: #03998d;
$navsbmenu-anchor-color: #00ac9e;

/*----------- General variables ---------*/

$anchor_color: #03998d;
$body-txt-color: #777;


/*----------- Mixins ---------*/

@mixin searchbox-color {
  background: $nav-srch-background !important;
  color: $nav-srch-txt-color !important;
}
@mixin searchbox-color-focus {
  background-color: #f3f4de !important;
  color: #a8a8a8 !important;
}

@mixin box-shadow($x: 2px, $y: 2px, $blur: 5px, $color: rgba(0,0,0,.4), $inset: "") {
  @if ($inset != "") {
    -webkit-box-shadow: $inset $x $y $blur $color;
    -moz-box-shadow:    $inset $x $y $blur $color;
    box-shadow:         $inset $x $y $blur $color;
  } @else {
    -webkit-box-shadow: $x $y $blur $color;
    -moz-box-shadow:    $x $y $blur $color;
    box-shadow:         $x $y $blur $color;
  }
}

/*----------- Top bar ---------*/
.navbar-brand .internal-link img{
  max-width: 195px;
  margin-top: -10px;
  overflow: hidden;
}

.account-info-bar .logged-out a{
    margin: 8px;
  } 
#top-bar-wrapper {
  background: #fff;
  position: fixed;
  width: 100%;
  -webkit-transition:  200ms linear;
  -moz-transition:  200ms linear;
  -o-transition:  200ms linear;
  transition: 200ms linear;
  border-bottom: 1px solid #ebebeb;
  z-index:9999;
  .slicknav_menu{
    .menu-bar{
      display:none;
    }
  }
  .form-group{
     margin-bottom:0;
  }
}

   /*-------------- Topbar Mobile -------------*/
    .slicknav_menu{
      background:#fff;
      .plus-icon{
            font-family: FontAwesome;
            float:left;
            margin-right:  5px;
         }
    }
    .slicknav_nav a{
      color: $anchor_color;
    }
    a.slicknav_btn {
      float: left;
      margin: 14px 5px;
    }
    .slicknav_btn{
      background-color:#fff;
    }
    .slicknav_menu .slicknav_icon-bar{
      background-color: $anchor_color;
      @include box-shadow(0,0,0,rgba(0,0,0,.13));
    }
    #top-bar-wrapper .slicknav_menu {
      display: none;
    }
    @media screen and (max-width: 48em) {
      /* #menu is the original menu */
      #top-bar-wrapper>.top-bar-desktop {
        display: none;
      }
      #top-bar-wrapper .slicknav_menu {
        display: block;
      }
    }


#top-bar {
  padding: 5px 2%;
  margin: 0 auto;
  max-width: 1260px;
  .search-bar-mobile{
    float:right;
    display:none;
  }
  .logo-bar {
    float: left;
    img {
      margin-top: 3px;
      height: 40px;
      border: none;
    }

  }
  /*//----------- Top bar - Navigation menu ---------*/

  .menu-bar-mobile{
    display:none;
    float: left;
    padding: 5px;
    margin-left: 12px;
  }
  .menu-bar {
    float: left;
    width: 32%;
    padding: 5px;
    margin-left: 12px;

    a {
      color: $navmenu-anchor-color;
      display: block;
      margin: 0;
      letter-spacing: 0.1em;
    }
    .main-links {
      width: 100%;
      color: $navmenu-anchor-color;
      > div {
        color: $anchor_color;
        float: left;
        padding: 10px 2%;
        cursor: pointer;
        span {
          display: block;
          margin: 0;
        }

      }
      > div:hover {
        background: $navmenu-hover-bgcolor;
        color: $navmenu-anchor-color;
      }

    }
    ul {
      display: none;
      position: absolute;
      z-index: 1;
      border-radius: 0;
      border: none;
      padding: 0;
      > li {
        padding: 0;
      }
      a {
        border: 0;
        font-size: 0.9em;
        width: auto;
        min-width: 120px;
        padding: 5px 28px;
        background: $navsbmenu-bgcolor;
        border-radius: inherit;
      }
      a:hover {
        background: $navsbmenu-anchor-color;
      }
    }
    .menu-bar-border{
      border-bottom-color: #03998d;
      border: 1px solid #03998d;
      margin: -6px 0px !important;
      padding: 3px 8px;
      .plus-icon{
            font-family: FontAwesome;
            float:left;
margin-right:  5px;
         }
    }


  }

  /*//----------- Top bar - Search box ---------*/

  .search-bar {
    float: left;
    padding: 8px;
    width: 22%;
    input{
      width:100%;
      padding: 0px 10px;
    }
  }

  /*//----------- Top bar - User sign up/account info ---------*/

  .account-info-bar {
    text-align: right;
  }
  


  #logging {
    display: block;
    a {
      margin-left: 10px;
    }
    a.button-primary {
    }
    a:hover {
    }
  }
}

#logging.logged-in a {
  font-size: .8em;
}
#logging a#my-card-link {
  display: block;
  font-size: 1.2em;
}

