  border-bottom-width: 0;
  padding: 0;
  white-space: nowrap;
  cursor:pointer;
}
.ui-tabs .ui-tabs-nav .ui-tabs-anchor {
  float: left;
  padding: .5em 1em;
  text-decoration: none;
}
.ui-tabs .ui-tabs-nav li.ui-tabs-active {
