.ui-tooltip {
  background: #000;
  border-radius: 6px;
  color: white;
  border: none;
  padding: 0;
  opacity: 1;
}
.ui-tooltip-content {
  position: relative;
  padding: 1em;
}
.ui-tooltip-content::after {
  content: '';
  position: absolute;
  border-style: solid;
  display: block;
  width: 0;
}
.ui-tooltip-content::after {
  top: 18px;
  left: -10px;
  border-color: transparent #000;
  border-width: 10px 10px 10px 0;

}
