            -webkit-box-shadow: $x $y $blur $color;
            -moz-box-shadow: $x $y $blur $color;
            box-shadow: $x $y $blur $color;
          }
        
        }
        /*----------- Top banner ---------*/
        div#openelectronics_slider_image {
          padding: 12px 25px;
        }
        #top-banner-wrapper {
          .campaign-content {
            font-size: 0.8em;
          }
          >div {
            overflow: inherit;
          }
          .column-1,
          .column-2,
          .column-3 {
            background: #fafafa;
            height: 38px;
            float: left;
            margin-left: 0.3%;
            margin-bottom: 20px;
            border: 0 solid #eaeaea;
            padding-bottom: 14%;
          }
          .column-1,
          .column-3 {
            width: 24%;
          }
          .column-1 {
            overflow: hidden;
            position: relative;
        
          }
          .slick-dots {
            bottom: -5%;
          }
          iframe {
            width: 100%;
            height: 175px;
          }
          .video-container {
            display: none;
          }
          .SELF-video_image {
            cursor: pointer;
            &:hover {
              @include box-shadow(0,0px,6px,rgba(0,0,0,.35));
              z-index: 9;
            }
        
          }
          .column-2 {
            position: relative;
            width: 50%;
            .wikirate-message {
              display: block;
              text-align: center;
              line-height: normal;
              padding: 30px;
              .homepage-heading-h1 {
                font-size: 1.5em;
              }
            }
        
          }
          .column-3 {
            .SELF-banner_column3 {
              overflow: hidden;
            }
            .banner-column-button a {
              text-align: center;
              display: block;
              font-size: 0.9em;
            }
            .banner-column-button {
              margin: 10%;
            }
          }
        
        }
        .SELF-wikirate_globe.card-content {
          width: 25%;
          padding: 30px 3px 18px 30px;
          max-width: 30%;
          float: left;
          overflow: hidden;
          img {
            max-height: 100%;
          }
        
        }
        /*----------- Content Wrapper --------------*/
        #main .video-container {
          visibility: hidden;
          height: 0;
          width: 0;
        }
        #page-wrapper {
          background: #fff;
          padding: 20px 12px;
        
        }
        #main-content-wrapper {
          margin: 0 5px;
        
        }
        .browse-button {
          text-align: center;
          margin-top: 5px;
          margin-left: 16px;
        
        }
        /*-----------  Grid  ---------*/
        .grid-row {
          display: inline-block;
          width: 100%;
          .grid-column {
            width: 49%;
            // background: green;
          }
        
        }
        /*----------- Companies and Topics  ---------*/
        #company-n-topic .grid-column {
          position: relative;
          //for slick-js icon position
        
        }
        #company-n-topic .card-header {
          display: none;
          // workaround for carousel
        
        }
        #company-n-topic {
          .company-list,
          .topic-list {
            .search-result-list {
              // workaround for carousel
              height: 220px;
              overflow: hidden;
              margin-left: 3.8%;
              width: 94%;
              max-width: 555px;
            }
            .search-result-item {
              display: inline-block;
              min-width: 130px;
              width: 175px;
              padding: 10px 0;
              margin-left: 11px;
            }
        
          }
          .company-list {
            float: left;
          }
          .topic-list {
            float: right;
          }
          .browse-button {
            margin-top: 35px;
          }
        
        }
        /*----------- Articles ---------*/
        
        #articles {
          .search-result-item {
            display: inline-block;
            margin: 1.4%;
            width: 30%;
          }
        
        }
        /*----------- Claims and Sources ---------*/
        
        #claims-sources {
        
          .card-content.SELF-homepage_claim_list,
          .card-content.SELF-homepage_source_list {
            padding: 5px 0;
            margin: 15px 3.5%;
            border: 1px solid #ddd;
            @include box-shadow(0,1px,6px,rgba(0,0,0,.13));
            height: 750px;  // Temporary fix for same height , it should be dynamic at somepoint...
            overflow: auto;
          }
          .card-content.SELF-homepage_claim_list {
            border-top: 8px solid $claim-color;
          }
          .card-content.SELF-homepage_source_list {
            border-top: 8px solid $source-color;
          }
          .claim-list,
          .source-list {
            width: 48%;
            .search-result-list {
            //  padding: 0 0 1000px;
              //same height workaround.
           //   margin-bottom: -1000px;
            }
        
          }
          .claim-list {
            float: left;
          }
          .source-list {
            float: right;
          }
          .browse-button {
            margin-top: 25px;
          }
        
        }
        /*----------- Headings ---------*/
        
        .homepage-heading-h1 {
          text-align: center;
          font-size: 1.6em;
          color: $heading-color;
          .count-color {
            color: $article-color;
            /* font-weight: 900;*/
          }
          .company-color {
            color: $claim-tag-companybg;
          }
          .topic-color {
            color: $claim-tag-topicbg;
          }
          .article-color {
            color: $article-color;
          }
          .claim-color {
            color: $claim-color;
          }
          .source-color {
            color: $source-color;
          }
        
        }
        .homepage-heading-h2 {
        font-size: 2em;
        font-weight: normal;
        padding: 14px 0 0;
        }
        .homepage-heading-h3{
        font-weight: normal;
        }
        /*----------- Media queries ---------*/
        
        @media screen and (max-width: 1150px) {
          .homepage-heading-h1 {
            font-size: 1.2em;
          }
          .homepage-heading-h2 {
            font-size: 1.6em;
          }
          .homepage-heading-h3 {
            font-size: 1.1em;
          }
          #top-banner-wrapper .column-2 .wikirate-message .homepage-heading-h1 {
            font-size: 1.2em;
          }
        
        }
        @media screen and (max-width: 1205px) {
          .homepage-heading-h1 {
            font-size: 1.1em;
          }
          .homepage-heading-h2 {
            font-size: 1.4em;
          }
          #top-banner-wrapper .column-2 .wikirate-message .homepage-heading-h1 {
            font-size: 1.1em;
          }
        
        }
        @media screen and (max-width: 990px) {
          .homepage-heading-h1 {
            font-size: 0.89em;
          }
          .homepage-heading-h2 {
            font-size: 1em;
          }
          .homepage-heading-h3 {
            font-size: 0.9em;
          }
          #top-banner-wrapper .column-2 .wikirate-message .homepage-heading-h1 {
            font-size: 0.9em;
          }
        
        }
        @media screen and (max-width: 48em) {
        
          #claims-sources {
            .claim-list,
            .source-list {
              width: 100%;
              clear: both;
              margin-bottom: 30px;
            }
            .card-content.SELF-homepage_source_list,
            .card-content.SELF-homepage_claim_list {
              height: auto;
            }
          }
        
        }
        @media screen and (max-width:47em ) {
        
          .homepage-heading-h1 {
            //        font-size:0.9em;
          }
          .homepage-heading-h2 {
            font-size: 1.8em;
          }
          #top-banner-wrapper .column-2 {
            .wikirate-message {
              padding: 1%;
              .homepage-heading-h1 {
                font-size: 1.5em;
              }
        
            }
            .SELF-wikirate_globe.card-content {
              padding: 1%;
              max-width: 16%;
            }
        
          }
          #top-banner-wrapper {
            .column-1,
            .column-3 {
              display: none;
            }
            .column-2 {
              width: 100%;
            }
          }
        
        }
        /*----------- Media queries- For mobile devices ---------*/
        
        @media screen and (max-width:40em ) {
        
          .grid-row,.grid-column{
            margin-bottom:24px;
          }
        
          .homepage-heading-h1 {
                   font-size:1em;
          }
          .homepage-heading-h2 {
            font-size: 1em;
          }
          #top-banner-wrapper {
        
            .column-2 {
              .wikirate-message {
                padding: 1%;
                .homepage-heading-h1 {
                  font-size: 0.9em;
                }
        
              }
              .SELF-wikirate_globe.card-content {
                padding: 1%;
                max-width: 16%;
              }
            }
        
          }
          #articles .search-result-item {
            display: inline-block;
            margin: 1.4%;
            width: 97%;
        
          }
          #company-n-topic {
            .company-list,
            .topic-list {
              width: 100%;
            }
        
          }
          body#wagn {
            min-width: 325px;
        
          }
          #claims-sources {
            .card-content.SELF-homepage_claim_list,
            .card-content.SELF-homepage_source_list {
              margin: 15px 0;
            }
        
          }
          #top-bar {
            .logo-bar img {
              margin-top: 0;
              height: 25px;
              border: none;
            }
            .account-info-bar {
              display: none;
              float: right;
              position: absolute;
              top: 6%;
              right: 35px;
              font-size: 0.6em;
            }
            #logging a{
              margin-left:4px;
            }
            .search-bar-mobile {
              display: block;
              float: right;
            }
            .search-bar {
              width: 92%;
              display: none;
            }
        
          }
          a.slicknav_btn {
            float: left;
            margin: 6px 0px 0 -6px;
        
          }
          #page-wrapper {
            padding: 6px 4px;
          }
        
          #main{
            padding: 48px 1%;
          }
        
          #top-bar-wrapper .slicknav_menu .slicknav_nav .account-info-bar .logged-out {
            display: none;
        
          }
        
          #top-banner-wrapper .column-2 .SELF-wikirate_globe.card-content {
            display:none;
          }
        
          .TYPE-claim .source-side,
          .TYPE-page .source-side,
          .TYPE-claim .meta-side,
          .TYPE-page .meta-side,
          .TYPE-analysi .meta-side,
          .TYPE-company .meta-side,
          .TYPE-topic .meta-side,
          .TYPE-analysi .source-side,
          .TYPE-company .source-side,
          .TYPE-topic .source-side {
            width: 100%!important;
            margin-left: 0;
          }
          .TYPE-topic .TYPE_PLUS_RIGHT-topic-analysis_with_article .analysis-blurb-content,
          .TYPE-topic .TYPE_PLUS_RIGHT-company-analysis_with_article .analysis-blurb-content,
          .TYPE-company .TYPE_PLUS_RIGHT-topic-analysis_with_article .analysis-blurb-content,
          .TYPE-company .TYPE_PLUS_RIGHT-company-analysis_with_article .analysis-blurb-content {
            width: 100%;
        
          }
          .ui-tabs .ui-tabs-panel {
            padding: 0;
        
          }
          .TYPE_PLUS_RIGHT-user-Xfollowing {
            display: none;
        
          }
          .TYPE-user .user-profile-left,
          .TYPE-user .user-profile-right,
          .TYPE-user .user-profile-right-bottom-contribution,
          .TYPE-user .TYPE_PLUS_RIGHT-user-discussion.titled-view {
            width: 100%;
        
          }
          .card-slot>.card-header .card-menu-link {
            margin: -40px -3px;
        
          }
          .header-vote {
            float: left;
            border-right: 0;
            margin-right: 0;
            padding: 0;
          }
          .header-citation {
            float: right;
            margin-top: -2%;
            padding: 0;
            border-left: 0;
            margin-left: 0;
          }
          .header-title {
            padding: 0 10px;
            text-align: center;
            width: 100%;
            float: left;
          }
        }
