@import url("http://fonts.googleapis.com/css?family=Droid+Sans:400,700");
body {
  color: #777;
  font-family: 'Droid Sans', sans-serif;
  line-height: 1.5em;
  font-size: 0.9em;
}

body#wagn {
  min-height: 100%;
  min-width: 480px;
  /*border-top: 4px solid #2c2c2c;*/
  background: #f3f3f3;
  padding: 0;
  margin: 0;
}

$heading_color: #555;
a {
  color: #009933;
}

a.wanted-card {
  color: #b6060a;
}

/* many sites may want this external link behavior; it's here because it's a nuisance to override when not desired */
a.external-link,
.closed-content a.external-link {
  color: #729;
  background: url(/assets/external_link.png) center right no-repeat;
  padding-right: 13px !important;
}

h1, h2 {
  color: $heading_color;
}

h1 {
  font-size: 1.2em;
  font-weight: normal;
  line-height: 1.8em;
  padding: 5px 0;
}

input.navbox {
  border: 0px solid #AAA !important;
  background: #FFFFFF !important;
  color: #a8a8a8 !important;
  padding-left: 3px;
  border-radius: 3px;
}
input.navbox:focus {
  background-color: #f3f4de !important;
  color: #a8a8a8 !important;
  outline: none;
}


#logo-bar {
  padding: 0 3%;
  img {
    margin-top: 3px;
    height: 40px;
  }
  #logging {
    float:right;
    font-size: 1.1em;
    margin-top: 10px;
    a {
      color: #72b671;
      text-shadow: #fff 1px 1px 0px;
      margin-left: 10px;
    }
    a:hover {
      color: #777777;
    }
  }
}


#menu-bar {
  background: #76ba75;
  padding: 0 3%;
  border-bottom: 1px solid #999;
  -moz-box-shadow: 0px 0px 2px 0px #ccc;
  -webkit-box-shadow: 0px 0px 2px 0px #ccc;
  box-shadow: 0px 0px 2px 0px #ccc;
  text-shadow: #5c995b 1px 1px 0px;
  margin-bottom: 20px;
  
  .navbox-form {
    display: inline-block;
    float: right;
    width: 33%
  }
  .navbox {
    height: 25px;
    width: 100%;
    margin-top: 8px;
  }
  a {
    color: #fff;
    display: block;
    margin: 0;
    text-transform: uppercase;
    letter-spacing: 0.1em;
  }
  
  .main-links { 
    > div {
      display: inline-block;
      padding: 10px 3%;
      cursor: pointer;
      span {
        display: block;
        margin: 0;
        text-transform: uppercase;
        letter-spacing: 0.1em;
        color: #fff;
      }
    }
    > div:hover {
      background: #83C782;
    } 
  }


  ul {
    display: none;
    position: absolute;
    z-index: 1;
    border-radius: 0;
    border: none;
    padding: 0;
    > li {
      padding: 0;
    }
    a {
      border: 0;
      font-size: 0.9em;
      width: auto;
      min-width: 120px;
      padding: 5px 28px;
      background: #83C782;
      border-radius: inherit;
    }
    a:hover {
      background: #8FD18E;
    }
  }
}


.card-header {
  font-family: 'Droid Sans';
}

.card-frame {
  border-radius: 4px;
  border: 1px solid #E5E5E5;
  > .card-header {
    background: #76BA75;
    font-size: 1.1em;
    color: white;
    a {
      color: white;
    }
  }
  > .instruction {
    background: #bbb;
  }
}


#main {
  font-size: 1.1em;
  clear: both;
  padding: 20px 3%;
  .card-frame > .card-body > .titled-view {
    margin: 15px 0;
    > .card-body {
      margin: 5px 0 5px 3%;
    }
    > .card-content {
      padding: 10px 20px 15px 20px;
    }
  }
  .card-frame {
    border-radius: 15px;
    > .card-body > h1 {
      font-size: 2em;
      font-weight: normal;
      line-height: 2em;
      padding: 5px 0 0 0;
    }
  }
  > .card-slot > h1 {
    font-size: 1.5em;
    padding: 0 5px;
    .card-title {
      text-align: center;
      display: inline-block;
      width: 90%;
    }
  }
}
.card-slot > .card-header .card-menu-link {
  display: none;
}
.card-slot:hover > .card-header .card-menu-link {
  display: inline-block;
}



.revision-navigation,
.current {
  background: #FFFFFF;
}



body.home-layout #main {

  > .card-content {
    .home-left {
      width: 65%;
      float: left;
      .SELF-infographic {
         text-align: center;
         background: #fff;
         padding: 10px;
         border-radius: 15px;
        -moz-border-radius: 15px;
         border: 1px solid #E5E5E5;
      }
    }
    .home-right {
      width: 33%;
      float: right;
    }
    > #wikirate_tagline {
      text-align: center;
      overflow: visible;
      font-size: 2em;
      padding: 1.5em;
    }
  }
}

.STRUCTURE-company_feature,
.STRUCTURE-topic_feature {
  clear: both;
  padding: 20px 0;
  min-height: 200px;
}

.STRUCTURE-company_feature,
.STRUCTURE-topic_feature {
  > a {
    font-size: 1.3em;
  }

  h1 {
    font-size: 0.9em;
    font-style: italic;
    color: #aaa;
  }

  .TYPE-image.content-view {
    width: 25%;
    max-width: 100px;
    padding: 0 3% 10px 0;
    min-height: 200px;
    
    img {
        border-radius: 10px;
       -moz-border-radius: 10px;
    }
  }
}

/*~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/

div.SELF-wikirate_footer {
  clear: both;
  display: block;
  padding: 45px 30px 10px 30px;
  text-align: center;
  font-size: 0.9em;
}

#credit,
#credit a {
  padding: 0 !important;
  background: none;
  border: 0px;
  margin-bottom: 15px;
  position: relative;
}

#credit {
  z-index: -0;
  left: 8px;
  display: block;
  a {
    right: 16px;
    position: relative;
    z-index: -1;
  }
}

.RIGHT-claim_count.content-view {
  display: inline;
  float: right;
  color: #999;
}

.search-result-item > .TYPE-page.content-view {
  .RIGHT-website .pointer-list {
    font-size: 1.1em;
    font-weight: bold;
    padding-bottom: 0;
  }
  .RIGHT-title {
    font-size: 0.9em;
    margin: 0 0 7px 3%;
  }
}



.RIGHT-image_source.content-view {
  font-size: 0.8em;
  font-style: italic;
}

.TYPE-analysi,
.TYPE-company, 
.TYPE-topic {
  >.source-side > a {
    display: block;
    padding: 10px;
    margin: 10px 5%;
    float: right;
    border: 2px solid #555;
    border-radius: 10px;
    background: #eee;
    font-size: 1.2em;
  }
}
.TYPE-analysi >.source-side > a {
  float: none;
  text-align: center;
}



.TYPE-topic .STRUCTURE-topic_analysi_blurb,
.TYPE-company .STRUCTURE-company_analysi_blurb {
  background: #eee;
  padding: 10px;
  border-radius: 10px;
  -moz-border-radius: 10px;
  margin: 10px 0 10px 30px;
  > h1 {
    font-size: 1.4em;
  }
  > a {
    float: right;
  }
}

.STRUCTURE-analysi_blurb {
  .RIGHT-claim {
    > .card-content {
      padding: 0 0 0 10%;
    }
    .search-result-item {
      padding-bottom: 10px;  
    }
  }
  > a {
    display: inline-block;
    color: #555;
    font-style: italic;
    padding-top: 5px;
  }
}


.STRUCTURE-icon_and_link.content-view > a > img,
.STRUCTURE-company_analysi_link.content-view > a > img,
.STRUCTURE-topic_analysi_link.content-view > a > img,
.STRUCTURE-company_link.content-view > a > img,
.STRUCTURE-topic_link.content-view > a > img {
  border-radius: 3px;
  -moz-border-radius: 3px;
  margin-right: 5px;
}


.STRUCTURE-claim_summary {
  > h1 {
    font-size: 1.1em;
    padding-bottom: 0;
  }
  > .card-body {
    padding:  5px 0 18px 5%;
    margin: 0 0 5px 0px;
    border-bottom: 1px solid #eee;
    font-size: 0.8em;
    .fa {
      padding-right: 5px;
    }
    > div {
      margin-left: 5%;
      padding: 2px 0;
      text-indent: -3%;
      div {  
        display: inline;
      }
    }
    .missing-view {
      display: none;
    }
    .RIGHT-source .source-title {
      font-style: italic;
    }
    .pointer-item, .search-result-item {
      text-indent: 10px;
      margin: 0;
      margin-right: 6px !important;
      :after {
        content: ' ';
      }
    }
  }
}

#main > .SELF-topic,
#main > .SELF-company {
  > .card-content > .TYPE-search.card-content > .search-result-list > .search-result-item {
    display: inline-block;
    min-width: 130px;
    width: 17%;
    padding: 10px 1%;
  }
}

.STRUCTURE-topic_overview,
.STRUCTURE-company_overview {
  padding: 5px;
  box-shadow: 2px 2px 2px 2px #ddd;
  border-radius: 10px;
  -moz-border-radius: 10px;
  text-align: center;
  width: 100%;
  min-height: 250px;
  > a {
    font-size: 1em;
    line-height: 1.6em;
    display: block;
  }
  .card-title {
    display: inline-block;
    height: 60px;
  }
  .TYPE-image {
    width: 80%;
    padding: 5px 10%;
    height: 150px;
    img {
      max-height: 100%;
    }
  }
  .claim-count {
    color: #999;
    .RIGHT-claim_count {
      color: #444;
      float: none;
    }
  }
}


.STRUCTURE-labeled_source_frame {
  label {
    display: inline-block;
    padding-right: 10px;
    float: left;
  }
  .RIGHT-title {
    display: inline-block;
    font-size: 1.2em;
    line-height: 1.2em;
  }
  em {
    float: right;
  }
  .pointer-list,
  .pointer-item {
    display: inline;
    padding-left: 0;
  }
}


fieldset.RIGHT-link input.card-content {
  margin-bottom: 30px;
}



.comment-box {
  background: inherit;
}

.TYPE-analysi.card-content {
  .search-no-results {
    display: none;
  }
  > .search-result-list {
    padding: 0;
  }
  .SELF-analysi_image,
  .RIGHT-image {
    display: inline-block;
    padding: 10px;
  }
  .SELF-analysi_image img {
    height: 50px;
    padding-bottom: 25px;
  }
  .source-side > .side-by-side {
    text-align: center;
    > a { 
      > .RIGHT-logo img,
      > .RIGHT-image img {
        border: 4px solid #666;
        border-radius: 10px;
        -mox-border-radius: 10px;
        max-height: 150px;
      }
    }
  }
}


.RIGHT-wikirate_source .content-editor select {
  height: 200px;
}

.closed-content label {
  display: inline;
}

.TYPE_PLUS_RIGHT-company-label img {
  margin: 0 2px 5px 0;
  border-radius: 4px;
  -moz-border-radius: 4px;
}

fieldset {
  > .button-area,
  > .editor,
  > legend,
  > legend > .instruction {
    float: none;
    text-align: left;
    width: 90%;
    padding: 0 5%;
  }
  > legend > .instruction {
    padding: 4px 0;
  }
}




.source-side {
  width: 67%;
  float: left;
  padding: 0;
  /*margin: 0 1%;*/
  margin-left: 1.6%; 
  iframe {
    width: 100%;
    height: 500px;
  }
}

.meta-side {
  width: 28%;
  float: right;
  position: static;
  padding: 0;
  margin: 0 1%;
  > .card-slot {
    margin: 20px 0;
  }
}
