(function() {
  $(window).ready(function() {
    var getFrameslot;
    getFrameslot = function(field) {
      var fieldset, frameslot;
      fieldset = field.closest('fieldset');
      frameslot = fieldset.find('.card-slot');
      if (frameslot[0]) {
        frameslot = $(frameslot[0]);
      } else {
        frameslot = $('<div class="card-slot"></div>');
        fieldset.append(frameslot);
      }
      return frameslot;
    };
    $('.TYPE-claim .RIGHT-link').on('change', 'input', function() {
      var field, frameslot;
      field = $(this);
      frameslot = getFrameslot(field);
      frameslot.empty();
      $.ajax(wagn.rootPath + '/card/create', {
        data: {
          success: {
            view: 'content'
          },
          slot: {
            structure: 'labeled source frame'
          },
          quickframe: true,
          card: {
            type_code: 'webpage',
            cards: {
              '+Link': {
                'content': field.val()
              }
            }
          }
        },
        success: function(data) {
          return frameslot.slotSuccess(data);
        },
        error: function(xhr) {
          return frameslot.slotError(xhr.status, xhr.responseText);
        }
      });
      return false;
    });
    return $('.TYPE-page .RIGHT-link').on('change', 'input', function() {
      var field, frameslot;
      field = $(this);
      frameslot = getFrameslot(field);
      return frameslot.html($('<iframe src="' + field.val() + '"></iframe>'));
    });
  });

}).call(this);

