$navmenu-anchor-color: #fff;
$navmenu-hover-bgcolor: #03998D;
$navsbmenu-bgcolor: #03998D;
$navsbmenu-anchor-color: #00AC9E;

@mixin searchbox-color{
  background: $nav-srch-background !important;
  color: $nav-srch-txt-color !important;
}
@mixin searchbox-color-focus{
  background-color: #f3f4de !important;
  color: #a8a8a8 !important;
}

$card-header-color: #dbdbdb;

//card colors
@mixin card-txt-color{
  color: #646464;
  a {
    color: #646464;
  }
}

//border radius mixin
@mixin border-radius($radius) {
  -webkit-border-radius: $radius;
  border-radius: $radius;
  background-clip: padding-box;  /* stops bg color from leaking outside the border: */
}
//***COLOR VARS - END***//


body {
  color: $body-txt-color;
  font-family: 'Droid Sans', sans-serif;
  line-height: 1.5em;
  font-size: 0.9em;
}

body#wagn {
  min-height: 100%;
  min-width: 480px;
  background: $body_background;
  padding: 0;
  margin: 0;
}

body#tinymce {
   padding: 4px;
}

a {
  color: $anchor_color;
}

a.wanted-card {
  color: $wanted-card-color;
}

/* many sites may want this external link behavior; it's here because it's a nuisance to override when not desired */
a.external-link,
.closed-content a.external-link {
  color: $closed-content-a-color;
  background: url(/assets/external_link.png) center right no-repeat;
  padding-right: 13px !important;
}

img {
  border: 0
}

h1, h2 {
  color: $heading_color;
}

h1 {
  font-size: 1.2em;
  font-weight: normal;
  line-height: 1.8em;
  padding: 5px 0;
}

// Search box
input.navbox {
  @include searchbox-color;
  border: 0px solid #AAA !important;
  padding-left: 3px;
  border-radius: 3px;

  &:focus {
    @include searchbox-color-focus;
    outline: none;
  }
}

#logo-bar {
  padding: 0 3%;
  background:#fff;
  img {
    margin-top: 3px;
    height: 40px;
    border:none;
  }
  #logging {
    float:right;
    font-size: 1.1em;
    margin-top: 10px;
    a {
      /*color: #72b671;*/
      color: $anchor_color;
      /*text-shadow: #fff 1px 1px 0px;*/
      margin-left: 10px;
    }
    a:hover {
      color: $body-txt-color;
    }
  }
}


#menu-bar {
  background: $primary_color;
  padding: 0 3%;
  // border-bottom: 1px solid #999;
  // -moz-box-shadow: 0px 0px 2px 0px #ccc;
  // -webkit-box-shadow: 0px 0px 2px 0px #ccc;
  // box-shadow: 0px 0px 2px 0px #ccc;
  // text-shadow: #5c995b 1px 1px 0px;
  margin-bottom: 20px;

  .navbox-form {
    display: inline-block;
    float: right;
    width: 33%
  }
  .navbox {
    height: 25px;
    width: 100%;
    margin-top: 8px;
  }
  a {
    color: $navmenu-anchor-color;
    display: block;
    margin: 0;
    text-transform: uppercase;
    letter-spacing: 0.1em;
  }

  .main-links {
    min-height: 40px;
    > div {
      float: left;
      padding: 10px 3%;
      cursor: pointer;
      span {
        display: block;
        margin: 0;
        text-transform: uppercase;
        letter-spacing: 0.1em;
        color: $navmenu-anchor-color;
      }
    }
    > div:hover {
      background: $navmenu-hover-bgcolor;
    }
  }


  ul {
    display: none;
    position: absolute;
    z-index: 1;
    border-radius: 0;
    border: none;
    padding: 0;
    > li {
      padding: 0;
    }
    a {
      border: 0;
      font-size: 0.9em;
      width: auto;
      min-width: 120px;
      padding: 5px 28px;
      background: $navsbmenu-bgcolor;
      border-radius: inherit;
    }
    a:hover {
      background: $navsbmenu-anchor-color;
    }
  }
}

.more-link {
  float: right;
}

.card-header {
  font-family: 'Droid Sans';
}
.card-content .titled-view > .card-header {
  line-height: 1.4em;
  margin-top: 1em;
}

.card-frame {
  border-radius: 4px;
  border: 0px solid #E5E5E5;
  > .card-header {
    background: $card-header-color;
font-size: 1.1em;
// font-weight: bold;
    @include card-txt-color;
  }
  > .instruction {
    background: #bbb;
  }
}


#main {
  font-size: 1.1em;
  clear: both;
  padding: 20px 3%;
  .card-frame > .card-body > .titled-view {
    margin: 15px 0;
    > .card-body {
      margin: 5px 0 5px 3%;
    }
    > .card-content {
      padding: 10px 20px 15px 20px;
    }
  }
  .card-frame {
    border-radius: 4px;
    > .card-body > h1 {
      font-size: 2em;
      font-weight: normal;
      line-height: 2em;
      padding: 5px 0 0 0;
    }
  }
  > .card-slot > h1 {
    font-size: 1.5em;
    padding: 0 5px;
    .card-title {
      text-align: center;
      display: inline-block;
      width: 90%;
      /*text-indent: 10%;*/
      margin-left:5%;
    }
  }
}
.card-slot > .card-header .card-menu-link {
  display: none;
}
.card-slot.closed-view,
.card-slot:hover {
  > .card-header .card-menu-link {
    display: inline-block;
    margin-left: 10px;
  }
}




.revision-navigation,
.current {
  background: #FFFFFF;
}


body.home-layout #main {
  > .card-content {
    .SELF-wikirate_infographic {
      text-align: center;
      background: #fff;
      padding: 10px;
      border-radius: 4px;
      -moz-border-radius: 4px;
      border: 0px solid #E5E5E5;
      > .SELF-infographic {
        float: left;
        display: inline-block;
        width:68%;
      }
      span {
       font-size: 1.8em;
       color: $infographic-heading;
      }
      p {
        line-height: 2em;
        color: #d5d5d5;
        font-size: 1.3em;
      }
      > .wikirate-video-container {
        position: relative;
        padding-bottom: 17%;
        padding-top: 0px;
        height: 0;
        overflow: hidden;
        left: 14px;
        top: -14px;
        width: 30.2%;
        margin: 0px;
        iframe {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
        }
      }
    }
    .SELF-industry_of_the_month.content-view {
      min-width: 90%;
      text-align: center;
      padding: 20px 3%;
    }
    .home-left {
      width: 65%;
      float: left;
      .SELF-featured_topic.card-frame {
        margin-top: 40px;
      }
    }
    .home-right {
      width: 33%;
      float: right;
    }
    > #wikirate_tagline {
      text-align: center;
      overflow: visible;
      font-size: 2em;
      padding: 1.5em;
    }
  }
}


.STRUCTURE-company_feature,
.STRUCTURE-topic_feature {
  clear: both;
  padding: 20px 0;
  min-height: 200px;
}

.STRUCTURE-company_feature,
.STRUCTURE-topic_feature {
