    font-size: 0.9em;
    font-style: italic;
    color: #aaa;
  }

  .TYPE-image.content-view {
    width: 25%;
    max-width: 100px;
    padding: 0 3% 10px 0;
    min-height: 200px;

    img {
        border-radius: 10px;
       -moz-border-radius: 10px;
    }
  }
}

