}

//snippet for all buttons in Wikirate. Should use this .button primary class anywhere a button is required for a positive action(like submit, Go, etc)
button, .button-primary, .TYPE-page .source-side .internal-link, .new-claim-button a, .ui-dialog .ui-dialog-buttonpane button{
   margin-right: 1%;
   background: $button-color;
   border: none;
   color: #fff;
   @include border-radius(3px);
   border-bottom: 3px solid $button-border;
   padding: 5px 12px 4px 12px;
   cursor: pointer;
   text-decoration:none!important;
   font-size: 0.7em;
   outline:none;
   &:hover{
     background:$button-color-hover;
    //  padding: 5px 12px 3px 12px;
    //  border-bottom: 4px solid $button-border;
   }
   &:active{
     background:$button-color-hover;
     border-top: 3px solid $button-border;
     border-bottom: 0;
     padding: 2px 12px 6px 12px;

   }
   &.create-cancel-button,&.cancel-button,&.button-secondary{
     background: $cancel-button-color;
     border-bottom: 3px solid $cancel-button-border;
     &:hover{
       background: $cancel-button-hover;
      //  padding: 5px 12px 3px 12px;
      //  border-bottom: 4px solid $cancel-button-border;
     }
     &:active{
       background:$cancel-button-hover;
       border-top: 3px solid $cancel-button-border;
       border-bottom: 0;
       padding: 2px 12px 6px 12px;
     }
   }
 }

 //Tweak to large buttons in topic,company and article page
 .new-claim-button a {
     text-align: center;
     display: block;
     font-size: 0.9em;
     margin-bottom: 10px;
  }


fieldset {
 > .button-area,
 > .editor,
 > legend,
 > legend > .instruction {
   float: none;
   text-align: left;
   display:inline-block;
   width: 90%;
   padding: 0 5%;
  position:relative;

>p {
  display: none;
  position: absolute;
  left: 18px;
  opacity: 0.8;
  top: 50%;
  margin-top: -18px;
  margin-left: 8px;
  width: auto;
  color: #FFFFFF;
  background: #000000;
  padding: 5px;
  text-align: center;
  border-radius: 3px;
  font-size: 0.8em;
  z-index:1;
      &:after {
          content: '';
          position: absolute;
          top: 50%;
          right: 100%;
          margin-top: -8px;
          width: 0;
          height: 0;
          border-right: 8px solid #000000;
          border-top: 8px solid transparent;
          border-bottom: 8px solid transparent;
          }
        }
  }
 > legend
   h2{
     display:inline;
   }
 > legend > .instruction {
   padding: 4px 0;
   width: 70%;
 }
}

