}
.TYPE-claim .meta-side, .TYPE-page .meta-side {
  width: 44.5%;
  position: static;
  > .card-slot {
    margin: 20px 0;
  }
  > .RIGHT-source > .card-header {
      margin-top: 0;
    }
  }


.sourcebox > input {
  width:100%;
}
/* .TYPE-claim .meta-side .RIGHT-source, */


.sourcebox {
  background: #FAFAFA;
  display: inline-block;
  height: 66px;
  width: 96%;
  padding: 12px;
  > button {
    float: right;
    margin: 11px 0px;
  }
}

.TYPE-claim, .TYPE-page {
  .sourceErrorMsg{
    color: red;
    font-size: 0.7em;
    float: left;
    margin-top: 8px;
  }
  .addSourceLoader{
    font-size: 0.7em;
    float: left;
    color:$anchor_color;
  }
}

.STRUCTURE-source_preview {
  background: #fafafa;
  margin: 12px 0px;
  padding: 4px 20px;
  .preview-page-link {
    font-weight: bold;
    padding: 3px 0;
    display: block;
  }
  .RIGHT-description {
    font-size: 0.9em;
    margin-top: 3px;
  }
  img {
    width: 100px;
    max-height: 100px;
    max-width: 20%;
    width: auto;
    height: auto;
    background-color: #eee;
    border: 0 !important;
    float: left;
    margin: 5px 3% 0 0;
  }
  .preview-visit-link {
    clear: both;
  }
}

//////




.RIGHT-compare_link.card-content,
.STRUCTURE-compare_link {
  &.card-content.content-view {
    display: inline;
    a {
      background: $button-color;
      font-size: 10px;
      padding: 3px 5px;
      margin-left: 10px;
      color: white;
      border-radius: 3px;
      -moz-border-radius: 3px;
    }
  }
}

@media all and (max-width: 1280px) and (min-width: 1024px) {
body>.SELF-wikirate_footer > .consortium-logo-container {
width: 102%;
}
}

#social-icons a {
 display: inline-block;
}

.claim-counting {
 float: right;
 margin-right: 6%;
}

.RIGHT-company > div> select {
 width: 40%;
}
.RIGHT-topic > div> select {
 width: 40%;
}
.claim_p_disclosure_link{
margin-left: 7.5%;
margin-bottom: 26px;
float: left;
display:none;
color:$anchor_color;
cursor:pointer;
}

.RIGHT-company>div>div>.chosen-choices li.search-choice {
background: #d3741c;
border: none;
box-shadow: none;
color: #fff;
padding: 7px 8px 8px 6px;
}
.RIGHT-topic>div>div>.chosen-choices li.search-choice {
background: #2b7ea0;
border: none;
box-shadow: none;
color: #fff;
padding: 7px 8px 8px 6px;
}

.claim-tip{
background: #FFF9B3;
text-align: center;
padding: 8px;
position: relative;
span{
position: absolute;
right: 2px;
top: 2px;
cursor:pointer;
}
}
.claim-clipboard {
 cursor:pointer;
}

//Chosen tweak to adjust height
.chosen-container .chosen-results {
    max-height:100px !important;
}

.modal-window{
  display:none;
}


/*
Style for signup success modal window content
*/
#signup-success-msg {
  text-align:center;
  .headingTxt{
  color:#000;
  font-size: 1.3em;
  margin-bottom: 18px;
  }
  .footNote{
    color: #C4C4C4;
    font-size: 0.8em;
    margin-top: 18px;
  }
}
