wagn.slotReady(function(slot){

var credit = slot.find('#tooltip-child');
var creditContent = credit.html();
var image = slot.find("#tooltip-parent");
image.attr("title", credit.html());
image.tooltip({
    position: {
      my: "left+15 center",
      at: "right center",
      collision: 'none'
    },
    close: function(event, ui) {
      ui.tooltip.hover(function() {
          $(this).stop(true).fadeTo(400, 1);
        },
        function() {
          $(this).fadeOut('400', function() {
            $(this).remove();
          });
        });
    }

  }

);
});

//***ALL COLOR VARS & MIXINS***//
$heading_color: #555;
$body_background: #ebebeb;
$primary_color: #555;
$anchor_color: #03998D;
$body-txt-color: #777;
$wanted-card-color: #b6060a;
$closed-content-a-color: #729;
$infographic-heading: #949494;
$div-border-color:#f1f1f1;

//button colors
$button-color: #03998D;
$button-color-hover: #009185;
$button-border: #00756C;
$cancel-button-color: #BEBEBE;
$cancel-button-border: #9B9B9B;
$cancel-button-hover:#B4B4B4;

//claim colors
$claim-tag-companybg: #D3741C;
$claim-tag-companybg-hover:#BB5B00;
$claim-tag-topicbg:#2B7EA0;
$claim-tag-topicbg-hover:#01648D;
$claim-tag-notexist:#f0f0f0;
$claim-tag-notexist-hover: #ececec;

//navigation colors
$nav-srch-background:#FFF;
$nav-srch-txt-color:#a8a8a8;
$navmenu-anchor-color: #fff;
$navmenu-hover-bgcolor: #03998D;
$navsbmenu-bgcolor: #03998D;
$navsbmenu-anchor-color: #00AC9E;

@mixin searchbox-color{
  background: $nav-srch-background !important;
  color: $nav-srch-txt-color !important;
}
@mixin searchbox-color-focus{
  background-color: #f3f4de !important;
  color: #a8a8a8 !important;
}

$card-header-color: #dbdbdb;

//card colors
@mixin card-txt-color{
  color: #646464;
  a {
    color: #646464;
  }
}

//border radius mixin
@mixin border-radius($radius) {
  -webkit-border-radius: $radius;
  border-radius: $radius;
  background-clip: padding-box;  /* stops bg color from leaking outside the border: */
}
//***COLOR VARS - END***//


body {
  color: $body-txt-color;
  font-family: 'Droid Sans', sans-serif;
  line-height: 1.5em;
  font-size: 0.9em;
}

body#wagn {
  min-height: 100%;
  min-width: 480px;
  background: $body_background;
  padding: 0;
  margin: 0;
}

body#tinymce {
   padding: 4px;
}

a {
  color: $anchor_color;
}

a.wanted-card {
  color: $wanted-card-color;
}

/* many sites may want this external link behavior; it's here because it's a nuisance to override when not desired */
a.external-link,
.closed-content a.external-link {
  color: $closed-content-a-color;
  background: url(/assets/external_link.png) center right no-repeat;
  padding-right: 13px !important;
}

img {
  border: 0
}

h1, h2 {
  color: $heading_color;
}

h1 {
  font-size: 1.2em;
  font-weight: normal;
  line-height: 1.8em;
  padding: 5px 0;
}

// Search box
input.navbox {
  @include searchbox-color;
  border: 0px solid #AAA !important;
  padding-left: 3px;
  border-radius: 3px;

  &:focus {
    @include searchbox-color-focus;
    outline: none;
  }
}

#logo-bar {
  padding: 0 3%;
  background:#fff;
  img {
    margin-top: 3px;
    height: 40px;
    border:none;
  }
  #logging {
    float:right;
    font-size: 1.1em;
    margin-top: 10px;
    a {
      /*color: #72b671;*/
      color: $anchor_color;
      /*text-shadow: #fff 1px 1px 0px;*/
      margin-left: 10px;
    }
    a:hover {
      color: $body-txt-color;
    }
  }
}


#menu-bar {
  background: $primary_color;
  padding: 0 3%;
  // border-bottom: 1px solid #999;
  // -moz-box-shadow: 0px 0px 2px 0px #ccc;
  // -webkit-box-shadow: 0px 0px 2px 0px #ccc;
  // box-shadow: 0px 0px 2px 0px #ccc;
  // text-shadow: #5c995b 1px 1px 0px;
  margin-bottom: 20px;

  .navbox-form {
    display: inline-block;
    float: right;
    width: 33%
  }
  .navbox {
    height: 25px;
    width: 100%;
    margin-top: 8px;
  }
  a {
    color: $navmenu-anchor-color;
    display: block;
    margin: 0;
    text-transform: uppercase;
    letter-spacing: 0.1em;
  }

  .main-links {
    min-height: 40px;
    > div {
      float: left;
      padding: 10px 3%;
      cursor: pointer;
      span {
        display: block;
        margin: 0;
        text-transform: uppercase;
        letter-spacing: 0.1em;
        color: $navmenu-anchor-color;
      }
    }
    > div:hover {
      background: $navmenu-hover-bgcolor;
    }
  }


  ul {
    display: none;
    position: absolute;
    z-index: 1;
    border-radius: 0;
    border: none;
    padding: 0;
    > li {
      padding: 0;
    }
    a {
      border: 0;
      font-size: 0.9em;
      width: auto;
      min-width: 120px;
      padding: 5px 28px;
      background: $navsbmenu-bgcolor;
      border-radius: inherit;
    }
    a:hover {
      background: $navsbmenu-anchor-color;
    }
  }
}

.more-link {
  float: right;
}

.card-header {
  font-family: 'Droid Sans';
}
.card-content .titled-view > .card-header {
  line-height: 1.4em;
  margin-top: 1em;
}

.card-frame {
  border-radius: 4px;
  border: 0px solid #E5E5E5;
  > .card-header {
    background: $card-header-color;
font-size: 1.1em;
// font-weight: bold;
    @include card-txt-color;
  }
  > .instruction {
    background: #bbb;
  }
}


#main {
  font-size: 1.1em;
  clear: both;
  padding: 20px 3%;
  .card-frame > .card-body > .titled-view {
    margin: 15px 0;
    > .card-body {
      margin: 5px 0 5px 3%;
    }
    > .card-content {
      padding: 10px 20px 15px 20px;
    }
  }
  .card-frame {
    border-radius: 4px;
    > .card-body > h1 {
      font-size: 2em;
      font-weight: normal;
      line-height: 2em;
      padding: 5px 0 0 0;
    }
  }
  > .card-slot > h1 {
    font-size: 1.5em;
    padding: 0 5px;
    .card-title {
      text-align: center;
      display: inline-block;
      width: 90%;
      /*text-indent: 10%;*/
      margin-left:5%;
    }
  }
}
.card-slot > .card-header .card-menu-link {
  display: none;
}
.card-slot.closed-view,
.card-slot:hover {
  > .card-header .card-menu-link {
    display: inline-block;
    margin-left: 10px;
  }
}




.revision-navigation,
.current {
  background: #FFFFFF;
}


body.home-layout #main {
  > .card-content {
    .SELF-wikirate_infographic {
text-align: center;
border-radius: 4px;
-moz-border-radius: 4px;
float: right;
border: 0px solid #E5E5E5;
background: #fff;
width: 41%;
padding-top: 20px;
margin-bottom: 12px;
      > .SELF-infographic {
        float: left;
        display: inline-block;
        width:68%;
      }
      span {
       font-size: 1.8em;
       color: $infographic-heading;
      }
      p {
        line-height: 2em;
        color: #d5d5d5;
        font-size: 1.3em;
      }
      > .wikirate-video-container {
        position: relative;
        padding-bottom: 17%;
        padding-top: 0px;
        height: 0;
        overflow: hidden;
        left: 14px;
        top: -14px;
        width: 30.2%;
        margin: 0px;
        iframe {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
        }
      }
    }
    .SELF-industry_of_the_month.content-view {
width: 100%;
text-align: center;
padding: 0px 0px 24px 0px;
float: left;
    }
    .home-left {
      width: 58%;
      float: left;
      .SELF-featured_topic.card-frame {
        margin-top: 40px;
      }
    }
    .home-right {
      width: 41%;
      float: right;
    }
    > #wikirate_tagline {
      text-align: center;
      overflow: visible;
      font-size: 2em;
      padding: 1.5em;
    }
  }
}


.STRUCTURE-company_feature,
.STRUCTURE-topic_feature {
  clear: both;
  padding: 20px 0;
  min-height: 200px;
}

.STRUCTURE-company_feature,
.STRUCTURE-topic_feature {
  > a {
    font-size: 1.3em;
  }

  h1 {
    font-size: 0.9em;
    font-style: italic;
    color: #aaa;
  }

  .TYPE-image.content-view {
    width: 25%;
    max-width: 100px;
    padding: 0 3% 10px 0;
    min-height: 200px;

    img {
        border-radius: 10px;
       -moz-border-radius: 10px;
    }
  }
}

/*~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/

body > .SELF-wikirate_footer {
  clear: both;
  display: block;
  padding: 45px 30px 10px 30px;
  text-align: center;
  font-size: 0.9em;
  background: #fff;
  border: 1px solid #E5E5E5;
a.external-link {
  padding-right: 0 !important;
  background: inherit !important;
}
[class*="SELF-logo_"] {
display: inline-block;
}
}

#credit,
#credit a {
  padding: 0 !important;
  background: none;
  border: 0px;
  margin-bottom: 15px;
  position: relative;
}

#credit {
  z-index: -0;
  left: 8px;
  display: block;
  a {
    right: 16px;
    position: relative;
    z-index: -1;
  }
}

.RIGHT-claim_count.content-view {
  display: inline;
  float: right;
  color: #999;
}

.search-result-item > .TYPE-page.content-view {
  .RIGHT-website .pointer-list {
    font-size: 1.1em;
    font-weight: bold;
    padding-bottom: 0;
  }
  .RIGHT-title {
    font-size: 0.9em;
    margin: 0 0 7px 3%;
  }
}



.RIGHT-image_source.content-view {
  font-size: 0.8em;
  font-style: italic;
}

.TYPE-analysi,
.TYPE-company,
.TYPE-topic {
  &.STRUCTURE-analysi_comparison a.internal-link,
  >.source-side > a {
    display: block;
    padding: 10px;
    margin: 10px 5%;
    float: right;
    border: 2px solid #555;
    border-radius: 10px;
    background: #eee;
    font-size: 1.2em;
  }
}
.TYPE-analysi {
  &.STRUCTURE-analysi_comparison a.internal-link,
  >.source-side > a {
    float: none;
    text-align: center;
  }
}



.TYPE-topic .STRUCTURE-topic_analysi_blurb,
.TYPE-company .STRUCTURE-company_analysi_blurb {
  padding: 20px 0;
  border-bottom: 1px solid #eee;
  > a {
    float: right;
  }
}

.STRUCTURE-analysi_blurb {
  > .RIGHT-article,
  > .RIGHT-claim {
    padding-left: 5%;
  }
  .RIGHT-claim {
    > .card-header {
      font-size: 0.9em;
      font-style: italic;
      color: #aaa;
      padding: 0;
    }
    .search-result-item {
      padding-bottom: 10px;
    }

  }
}


.STRUCTURE-icon_and_link.content-view > a > img,
.STRUCTURE-company_analysi_link.content-view > a > img,
.STRUCTURE-topic_analysi_link.content-view > a > img,
.STRUCTURE-company_link.content-view > a > img,
.STRUCTURE-topic_link.content-view > a > img {
  border-radius: 3px;
  -moz-border-radius: 3px;
  margin-right: 5px;
}

.STRUCTURE-topic_analysi_link_and_compare .STRUCTURE-compare_link,
.RIGHT-link_and_compare .RIGHT-compare_link {
  float: right;
}

.STRUCTURE-claim_summary {
  > h1 {
    font-size: 1.1em;
    padding-bottom: 0;
  }
  > .card-body {
    padding:  5px 0 18px 5%;
    margin: 0 0 5px 0px;
    border-bottom: 1px solid #eee;
    font-size: 0.8em;
    .fa {
      padding-right: 5px;
    }
    > div {
      margin-left: 5%;
      padding: 2px 0;
      text-indent: -3%;
      div {
        display: inline;
      }
    }
    .missing-view {
      display: none;
    }
    .RIGHT-source .source-title {
      font-style: italic;
    }
    .pointer-item, .search-result-item {
       text-indent: 10px;
       margin: 0;
       margin-right: 0px !important;
       padding: 2px;
      :after {
        content: ' ';
      }
    }
  }
}

#claim-sourcelink .pointer-item a {
  color: $body-txt-color;
    &:hover{
      color:$anchor_color;
    }
 }

.analysis-link > a,
.RIGHT-company.titled-view > .card-content .item-link > a,
.RIGHT-topic.titled-view > .card-content  .item-link > a {
    // margin: 3px 10px;
}

.analysis-link > a,
.RIGHT-company.card-content .item-link > a,
.RIGHT-topic.card-content .item-link > a {
  display: inline-block;
  text-indent: 0;
  border-radius: 3px;
  font-size: 0.9em;
  color: #fff;
  text-decoration: none;
}

.RIGHT-company.card-content .item-link > a,
.RIGHT-topic.card-content .item-link > a,
.analysis-link > a > span {
  display: inline-block;
  padding: 0px 6px !important;
  margin-bottom: 4px;
}

.RIGHT-company.card-content .item-link > a,
.analysis-link > a > .company {
  background: $claim-tag-companybg;
  border-top-left-radius: 3px;
  border-bottom-left-radius: 3px;
}
.RIGHT-company.card-content .item-link > a:hover,
.analysis-link > a:hover > .company {
  background: $claim-tag-companybg-hover;
}


.RIGHT-topic.card-content .item-link > a,
.analysis-link > a > .topic {
  background:  $claim-tag-topicbg;
  border-top-right-radius: 3px;
  border-bottom-right-radius: 3px;
}
.RIGHT-topic.card-content .item-link > a:hover,
.analysis-link > a:hover > .topic {
  background: $claim-tag-topicbg-hover;
}

.card-content .item-link > a.wanted-card {
  background: $claim-tag-notexist;
  border-top-left-radius: 3px;
  border-bottom-left-radius: 3px;
  color: $wanted-card-color;
  &:hover{
    background: $claim-tag-notexist-hover;
  }
}


#main > .TYPE-claim {
  > .card-header {
    background-color: #fff;
  }
  .source-side {
    border-right: 1px solid $div-border-color;
  }
}


.RIGHT-tag,
.RIGHT-year {
  &.labeled-view {
    font-size: 0.9em;
    color: #aaa;
    .card-content a {
      color: #aaa;
      font-style: italic;
    }
  }
  &.missing-view,
  &.labeled-view {
    label,
    .card-content a,
    a.missing-labeled {
      text-align: left;
      min-width: 30px;
    }
  }
}

.TYPE-claim.card-content .source-side > .RIGHT-tag {
  margin-top: 20px;
}

#main > .SELF-topic,
#main > .SELF-company {
  > .card-content {
    .pointer-item,
    .search-result-item {
      display: inline-block;
      min-width: 130px;
      width: 17%;
      padding: 10px 1%;
    }
  }
}

.STRUCTURE-topic_overview,
.STRUCTURE-company_overview {
  box-shadow: 0px 0px 12px 1px rgba(221, 221, 221, 0.87);
  border-radius: 10px;
  -moz-border-radius: 10px;
  text-align: center;
  border: 1px solid rgb(213, 213, 213);
  width: 100%;
  min-height: 250px;
  > a {
    font-size: 1em;
    line-height: 1.6em;
    display: block;
    padding: 5px;
  }
  :hover{
    background: #f8f8f8;
  }
  .card-title {
    display: inline-block;
    height: 60px;
  }
  .TYPE-image {
    width: 80%;
    padding: 5px 10%;
    height: 150px;
    overflow: hidden;
    img {
      max-height: 100%;
    }
  }
  .claim-count {
    color: #999;
    .RIGHT-claim_count {
      color: #444;
      float: none;
    }
  }
}


.STRUCTURE-labeled_source_frame {
  label {
    display: inline-block;
    padding-right: 10px;
    float: left;
  }
  .RIGHT-title {
    display: inline-block;
    font-size: 1.2em;
    line-height: 1.2em;
  }
  em {
    float: right;
  }
  .pointer-list,
  .pointer-item {
    display: inline;
    padding-left: 0;
  }
}
.comment-box {
  background: inherit;
}
.cited-claim-number {
  padding:1px 5px;
  border-radius: 10px;
  -border-radius: 10px;
  font-size: 0.8em;
  margin-right: 5px;
  background: #03998d;
  color: white;
}
.claim-clipboard {
  padding: 4px;
  color: #03998d;
  background: white;

}

.TYPE-analysi.card-content {
  .search-no-results {
    display: none;
  }
  > .search-result-list {
    padding: 0;
  }
  .side-by-side {
    text-align: center;
    padding: 10px;
    margin-bottom: 20px;
    .company-link {
      float: left;
    }
    .topic-link {
      float: right;
    }
    .TYPE-image {
      display: inline-block;
      padding: 10px;
      border-radius: 10px;
      -moz-border-radius: 10px;
      border: 2px solid #666;
    }
    img {
        max-height: 130px;
    }
    .analysis-name {
      font-size: 1.4em;
      line-height: 1.6em;
    }
    .analysis-joint {
      font-size: 0.8em;
    }
  }
  > .source-side {
    > h1 {
      margin-top: 40px;
      padding: 0;
    }
    > .TYPE-search > .card-content {
      padding-left: 3%;
    }
  }
  .RIGHT-right_sidebar.card-content {
    > h1 {
      padding: 0;
    }
    > h2 {
      font-weight: normal;
      font-size: 1em;
      padding: 0 0 15px 3%;
    }
    > .card-content  {
      padding-bottom: 25px;
    }
  }
}

.RIGHT-right_sidebar .source-title {
  display: block;
  padding: 3px 0 3px 5%
}
.RIGHT-wikirate_source .content-editor select {
  height: 200px;
}

.STRUCTURE-website_summary.titled-view {
  padding-bottom: 20px;
  border-bottom: 1px solid $div-border-color;
  margin-bottom: 20px;
  > .card-content {
    padding-left: 5%;
  }
  .RIGHT-page {
    > h1 {
      font-size: 0.8em;
    }
    padding-left: 5%;
    .search-result-item {
      line-height: 2em;
    }
  }
}


.SELF-company_comparison.card-content {
  table {
    width: 100%;
  }
  th {
    padding: 5px 1%;
  }
  td {
    padding: 5px 2%;
    width: 50%
  }
  > div {
    width: 46%;
    padding: 5px 2%;
  }
  .left-side {
    float: left;
  }
  .right-side {
    float: right;
  }

}

.closed-content label {
  display: inline;
}

.TYPE_PLUS_RIGHT-company-label img {
  margin: 0 2px 5px 0;
  border-radius: 4px;
  -moz-border-radius: 4px;
}

//'make a claim' button in source page
.TYPE-page .source-side .internal-link{
  padding: 7px 6px 4px 6px !important;
}
//To hide 'make a claim' in new and edit source page. It is not required there.
.TYPE-page.new-view .source-side .internal-link, .TYPE-page.edit-view .source-side .internal-link{
  display:none;
}

//snippet for all buttons in Wikirate. Should use this .button primary class anywhere a button is required for a positive action(like submit, Go, etc)
button, .button-primary, .TYPE-page .source-side .internal-link, .new-claim-button a, .ui-dialog .ui-dialog-buttonpane button{
   margin-right: 1%;
   background: $button-color;
   border: none;
   color: #fff;
   @include border-radius(3px);
   border-bottom: 3px solid $button-border;
   padding: 5px 12px 4px 12px;
   cursor: pointer;
   text-decoration:none!important;
   font-size: 0.7em;
   outline:none;
   &:hover{
     background:$button-color-hover;
    //  padding: 5px 12px 3px 12px;
    //  border-bottom: 4px solid $button-border;
   }
   &:active{
     background:$button-color-hover;
     border-top: 3px solid $button-border;
     border-bottom: 0;
     padding: 2px 12px 6px 12px;

   }
   &.create-cancel-button,&.cancel-button,&.button-secondary{
     background: $cancel-button-color;
     border-bottom: 3px solid $cancel-button-border;
     &:hover{
       background: $cancel-button-hover;
      //  padding: 5px 12px 3px 12px;
      //  border-bottom: 4px solid $cancel-button-border;
     }
     &:active{
       background:$cancel-button-hover;
       border-top: 3px solid $cancel-button-border;
       border-bottom: 0;
       padding: 2px 12px 6px 12px;
     }
   }
 }

 //Tweak to large buttons in topic,company and article page
 .new-claim-button a {
     text-align: center;
     display: block;
     font-size: 0.9em;
     margin-bottom: 10px;
  }


fieldset {
 > .button-area,
 > .editor,
 > legend,
 > legend > .instruction {
   float: none;
   text-align: left;
   display:inline-block;
   width: 90%;
   padding: 0 5%;
  position:relative;

>p {
  display: none;
  position: absolute;
  left: 18px;
  opacity: 0.8;
  top: 50%;
  margin-top: -18px;
  margin-left: 8px;
  width: auto;
  color: #FFFFFF;
  background: #000000;
  padding: 5px;
  text-align: center;
  border-radius: 3px;
  font-size: 0.8em;
