function byteCount(s) {
  return encodeURI(s).split(/%..|./).length - 1;
}

wagn.slotReady(function(slot) {

  /* commenting it for now. 
  $("body").on("blur", "#sourcebox", function() {
    return $(".sourcebox button").trigger("click");
  });
  */

  /**
  To Count number of characters in Claim input box
  **/
  slot.find("#card_name").keyup(function(event) {
    var maxlimit = 100;
    var countField = $('.claim-counting .claim-counting-number');
    if (byteCount(this.value) > maxlimit) {
      this.value = this.value.substring(0, maxlimit);
      //Here we can add helptext to recommend users to add basis.
    }
    countField.html((maxlimit - byteCount(this.value)));
  });

  /**
   To replace existing helptext with questionMark icon and
   show the helptext in a popover box
  **/
  var $helpText = slot.find(".instruction");

  $helpText.each(function() {
    _this = $(this);
    if (_this.parent('.card-frame')[0]) {
      return true;
    }
    _this.hide();

    _this.parent('legend').append('<div class="fa fa-question-circle helpTextQuestionMrk"></div>');
    var tooltipParent = _this.parent().find(".helpTextQuestionMrk");
    tooltipParent.attr("title", _this.html());
    console.log(tooltipParent);
    tooltipParent.tooltip({
      position: {
        my: "left+15 center",
        at: "right center",
        collision: 'none'
      }
    });
    /*
        _this.children().hide();
        _this.append('<div class="fa fa-question-circle helpTextQuestionMrk"></div>');
        _this.find(".helpTextQuestionMrk").mouseover(function(){
          $(this).siblings('p').show();
        });
        _this.find(".helpTextQuestionMrk").mouseout(function(){
          $(this).siblings('p').hide();
        });
    */
  })

  /**
   To add progressive disclousure link in the claim form to hide least important/optional fields
  **/

  var $disclosureLinks = slot.filter('.TYPE-claim.new-view').find("._pDisLink");

  if ($disclosureLinks.length) {
    $disclosureLinks.show();
    $(".RIGHT-basi,.RIGHT-tag,.RIGHT-year, .RIGHT-related_article, .RIGHT-discussion").hide();
    $("#_addBasis").click(function() {
      $(".RIGHT-basi").show();
      $(this).hide();
    });
    $("#_addMoreInfo").click(function() {
      $(".RIGHT-tag,.RIGHT-year,.RIGHT-related_article, .RIGHT-discussion").show();
      $(this).hide();
    });
  }

  /**
   To remove the source from claim form when clicked on 'close' icon
  **/
  var $slotContainer = slot.filter(".TYPE-claim.new-view, .TYPE-claim.edit-view");

  $slotContainer.on({
    mouseenter: function() {
      $(this).find(".remove-source").show();
    },
    mouseleave: function() {
      $(this).find(".remove-source").hide();
    }
  }, ".TYPE-page");

  $slotContainer.on("click", ".TYPE-page .remove-source", function() {
    $(this).slot().remove();
  });

  /**
   To close the claim tip (which displayed below the claim card title)
  **/

  slot.find("#close-tip").click(function() {
    $(".claim-tip").hide();
  })

})

