//-----------source meta structure-------------//



$div-border-color: #f1f1f1;
$note-tag-companybg: #d3741c;
$note-tag-companybg-hover: #bb5b00;
$note-tag-topicbg: #2b7ea0;
$note-tag-topicbg-hover: #01648d;
$note-tag-notexist: #f0f0f0;
$note-tag-notexist-hover: #ececec;
$anchor_color: #03998d;

$card-header-color: #dbdbdb;

.TYPE-analysis {
  &.STRUCTURE-analysis_comparison a.internal-link,
  >.source-side > a {
    float: none;
    text-align: center;
  }

}
/*--------Borders------------ */
.TYPE-analysis,
.TYPE-company,
.TYPE-topic,
.TYPE-metric {
  .source-side {
    width: 70%;
    border-right: 1px solid $div-border-color;
    padding-right: 20px;
    .ui-tabs {
      border: 0;
    }
  }
}

#main > .TYPE-note {
  > .card-header {
    background-color: #fff;
  }
  .source-side {
    border-right: 1px solid $div-border-color;
  }

}
.TYPE-note.card-content .source-side > .RIGHT-tag {
  margin-top: 20px;

}
.TYPE-analysis.card-content {
  .search-no-results {
    display: none;
  }
  > .search-result-list {
    padding: 0;
  }
  .side-by-side {
    text-align: center;
    padding: 0;
    margin-bottom: 20px;
    .company-link {
      float: left;
    }
    .topic-link {
      float: right;
    }
    .TYPE-image {
      display: inline-block;
      padding: 10px;
      border-radius: 2px;
      -moz-border-radius: 2px;
      border: 0 solid #ededed;
    }
    img {
      max-height: 130px;
    }
    .analysis-name {
      font-size: 1.4em;
      line-height: 1.6em;
    }
    .analysis-joint {
      font-size: 0.8em;
    }

  }
  > .source-side {
    > h1 {
      margin-top: 40px;
      padding: 0;
    }
    > .TYPE-search > .card-content {
      padding-left: 3%;
    }

  }
  .RIGHT-right_sidebar.card-content {
    > h1 {
      padding: 0;
    }
    > h2 {
      font-weight: normal;
      font-size: 1em;
      padding: 0 0 15px 3%;
    }
    > .card-content {
      padding-bottom: 25px;
    }
  }

}
//'make a note' button in source page
.TYPE-page .source-side .internal-link {
  padding: 0;

}
//To hide 'make a note' in new and edit source page. It is not required there.
.TYPE-page.new-view .source-side .internal-link,
.TYPE-page.edit-view .source-side .internal-link {
  display: none;

}
// To divide source and meta sides of content in Company, Note, Topic , Analysis and Source pages
.source-side,
.meta-side {
  float: left;
  padding: 0;
  margin-left: 2.5%;

}
.TYPE-metric {
  .source-side {
    width: 65%;
  }
  .meta-side {
    width: 30%;
    text-align: center;
    margin-left:0;
    .ui-tabs{
      border:none;
    }
  }

}
.TYPE-analysis {
  .source-side {
    width: 63% !important;
  }
  .meta-side {
    width: 30% !important;
  }

}

.TYPE-analysis .meta-side,
.TYPE-company .meta-side,
.TYPE-topic .meta-side {
  width: 22%;
}
.SELF-source,
.TYPE-note {
  .creator-credit {
    text-align: center;
    .search-result-list {
      display: inline-block;
    }

  }
  .preview-visit-link {
    float: right;
  }
  .remove-source {
    display: none;
    float: right;
    cursor: pointer;
  }

}
.TYPE-note .source-side,
.TYPE-page .source-side {
  width: 45%;
  margin-left: 2.5%;
  padding-right: 2.5%;
}
.TYPE-note .meta-side,
.TYPE-page .meta-side {
  width: 44.5%;
  position: static;
  > .card-slot {
    margin: 20px 0;
  }
  > .RIGHT-source > .card-header {
    margin-top: 0;
  }

}
.sourcebox > input {
  width: 100%;

}
