    }
    .search-result-list {
      float: left;
    }
    .paging {
      display: block;
      clear: both;
    }
    .TYPE_PLUS_RIGHT-company-logo {
      float: left;
      background: #fff;
      overflow: hidden;
      height: 42px;
      width: 58px;
      padding: 6px;
      border: 1px solid #f0f0f0;
      text-align: center;
    }
    .TYPE_PLUS_RIGHT-topic-image {
      float: left;
      background: #2f2f2f;
      overflow: hidden;
      height: 54px;
      border: 1px solid #f0f0f0;
    }
    h1 {
      clear: left;
      display: inline;
    }
    .RIGHT-note {
      float: left;
      width: 68%;
      margin: 0 10px;
      a {
        float: left;
        width: 100%;
        height: 40px;
      }
      .RIGHT-count {
        float: left;
      }
      .RIGHT-attr {
        font-size: 0.8em;
        a {
