wagn.slotReady(function(slot) {
  
  slot.find('.tab').each(function(){
    var $tabDiv = $(this);
    $tabDiv.find(".search-no-results").each(function(){
      if($(this).find(".empty-tab").length>0)
        return;
      var $div = $("<div>", { class: "empty-tab"});
      var emptyTabContent = $tabDiv.attr("empty-tab-content");
      if(emptyTabContent)
      {  
        $div.append("<span>"+emptyTabContent+"</span>");
        $(this).append($div);
      }
    });
  });
});

